css:
@-moz-document url-prefix("https://my.yota.ru/selfcare/") {
#balance-holder {
	border-bottom-width: 0 !important;
	border-color: #856666 !important;
	border-left-width: 2px !important;
	border-right-width: 0 !important;
	border-style: solid !important;
	border-top-width: 0 !important;
}

#balance-holder span {
	color: #34f711 !important;
	font-family: Lato !important;
	font-size: 28px !important;
	font-weight: bold !important;
}

#header {
	background-color: #000000 !important;
	overflow: visible !important;
}

#instant-payment {
	border-bottom-width: 0 !important;
	border-color: #856666 !important;
	border-left-width: 0 !important;
	border-right-width: 0 !important;
	border-style: dashed !important;
	border-top-width: 2px !important;
}

#instant-payment .currency {
	text-shadow: none !important;
}

#logo img {
	background-color: #0000 !important;
	filter: invert(100%) hue-rotate(109deg) brightness(1.3) !important;
}

#question {
	background-color: #1A0F1A !important;
	color: #56591d !important;
}

#secondAddLinks li a {
	color: #2c8a48 !important;
}

#sliders .content-container {
	border-left: none !important;
}

#sliders .devices-list .cost,
#sliders .devices-list .speed,
#sliders .devices-list .time {
	background: 0 !important;
}

#sliders .header-container {
	border-right: 2px solid #1d5328 !important;
}

#sliders .hint {
	background: 0 !important;
	border-bottom-width: 0 !important;
	border-left-width: 0 !important;
	border-radius: 20px !important;
	border-right-width: 0 !important;
	border-style: solid !important;
	border-top-width: 2pt !important;
}

#sliders .main-offer .tarriff-info .slider-message {
	background: 0 !important;
}

#sliders .other-offers .tariff {
	background: 0 !important;
	border-bottom-width: 3px !important;
	border-color: #ccb6b6 !important;
	border-left-width: 0 !important;
	border-right-width: 0 !important;
	border-style: dotted !important;
	border-top-width: 0 !important;
}

#sliders .slider-container {
	overflow: visible !important;
}

#sliders .slot-wrapper .devices-list strong,
#sliders .devices-list .tarriff-info strong {
	color: yellow !important;
	font-family: Lucida Grande !important;
}

#tooltip {
	color: #030303 !important;
	filter: invert(80%) !important;
}

.account-money dl dt {
	background: 0 !important;
}

.loading-overlay,
.white-overlay {
	filter: invert(85%) !important;
}

.message-list li.success {
	background: 0 !important;
}


/*I have not ideas, why they need that thing below*/
.separated iframe {
	display: none !important;
}

.styled tbody {
	background-color: #171117 !important;
	border-style: solid !important;
	border-width: 1px !important;
	color: #36d41a !important;
	font-size: 18px !important;
}

.styled tbody td {
	background-color: #120312 !important;
	border-bottom-width: 1px !important;
	border-color: #332733 !important;
	border-left-width: 0 !important;
	border-right-width: 1px !important;
	border-style: solid !important;
	border-top-width: 1px !important;
}

.styled td {
	color: #2fff00 !important;
}

.tab-content.border.r {
	background-color: #141414 !important;
	border-color: #ff0000 !important;
	border-style: solid !important;
	border-width: 4px !important;
}

.tab-content.user-devices.border.rc {
	background: 0 !important;
	border: 0 !important;
}

.tariff .tarriff-info div {
	border-bottom-width: 0 !important;
	border-color: #21de4d !important;
	border-left-width: 0 !important;
	border-right-width: 1px !important;
	border-style: solid !important;
	border-top-width: 0 !important;
}

a.amount {
	background-color: #ff0000 !important;
	color: #2d7545 !important;
}

a.auth-check {
	color: #1cff4d !important;
}

a.b-b2c-header__logo {
	background-color: #000000 !important;
}

a.b-link {
	color: #307527 !important;
}

a.btn {
	background-color: #1f944e !important;
	color: #000000 !important;
	font-size: 14px !important;
	font-weight: bold !important;
}

a.btn.auth-check.submit {
	background-color: #de2a2a !important;
	border-radius: 999px !important;
	color: #00c403 !important;
}

a.button {
	color: #545951 !important;
}

a.change-user-locale {
	color: #00d447 !important;
	font-style: italic !important;
	font-weight: bold !important;
}

a.sms {
	color: #3cb011 !important;
}

a.top-message__plans-link {
	color: #458a44 !important;
}

a.ui-slider-handle.ui-state-default.ui-corner-all {
	background: radial-gradient(ellipse at center, rgba(243, 197, 189, 1) 0%, rgba(232, 108, 87, 1) 50%, rgba(234, 40, 3, 1) 51%, rgba(255, 102, 0, 1) 75%, rgba(199, 34, 0, 1) 100%) !important;
	background-color: #ff0303 !important;
	border-color: #00ff0d !important;
	border-radius: 100% !important;
	border-style: ridge !important;
}

a.ui-slider-handle.ui-state-default.ui-corner-all.ui-state-hover {
	background: radial-gradient(ellipse at center, rgba(243, 197, 189, 1) 0%, rgba(232, 108, 87, 1) 50%, rgba(234, 40, 3, 1) 51%, rgba(255, 102, 0, 1) 75%, rgba(199, 34, 0, 1) 100%) !important;
	background-color: #ff0303 !important;
	border-color: #2d7330 !important;
	border-radius: 100% !important;
	border-style: solid !important;
}

button.b-button {
	background-color: #52182b !important;
	color: #35e300 !important;
	font-weight: bold !important;
	height: 60px !important;
	width: auto !important;
}

div.account-money {
	background-color: #092913 !important;
	border-color: #ff0000 !important;
	border-radius: 1000 !important;
	border-style: ridge !important;
	border-width: 5px !important;
}

div.b-b2c-auth-layout {
	background-color: #000000 !important;
}

div.b-b2c-auth__container {
	color: #abf299 !important;
}

div.b-b2c-auth__note {
	display: none !important;
}

div.b-b2c-auth__socials {
	display: none !important;
}

div.b-b2c-auth__tab.js-b2c-auth-tab.b-b2c-auth__tab_state_active {
	filter: invert(1) !important;
}

div.b-radio-tabs {
	border-radius: 999px 999px 99px 99px !important;
	border-width: 0 !important;
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.5) !important;
	filter: invert(85%) !important;
}

div.cost {
	border-style: none !important;
	border-width: 0 !important;
	padding-right: 10px !important;
	width: auto !important;
}

div.cost strong {
	border-style: none !important;
}

div.cost.no-arrow {
	border-style: none !important;
}

div.cost.no-arrow strong {
	border-style: none !important;
}

div.ctrl-holder label em {
	color: #00ff37 !important;
	font-size: 13px !important;
	font-weight: bold !important;
}

div.decrease {
	color: #0aff85 !important;
	filter: invert(90%) !important;
}

div.error-text {
	color: #044505 !important;
	font-style: italic !important;
	font-variant: small-caps !important;
	font-weight: bold !important;
}

div.form-container {
	border-style: none !important;
}

div.hint.hint_pos_current-conditions {
	background-color: #000000 !important;
	color: #11ff00 !important;
}

div.increase {
	filter: invert(90%) !important;
}

div.lb {
	background: 0 !important;
	background-color: #f01313 !important;
	border-radius: 999px !important;
}

div.line {
	background: 0 !important;
}

div.lt {
	border-radius: 999px !important;
}

div.max-value {
	border-style: none !important;
	border-width: 0 !important;
}

div.message-box {
	border-color: #37bd6d !important;
	border-radius: 999px !important;
	border-style: solid !important;
	border-width: 3px !important;
}

div.payment-iframe {
	filter: invert(90%) brightness(0.8) !important;
}

div.pimpa {
	filter: invert(90%) !important;
}

div.rb {
	border-radius: 999px !important;
}

div.required span {
	color: #00ff37 !important;
	font-size: 13px !important;
	font-weight: bold !important;
}

div.rounded.select-holder.input-holder {
	background-color: #1A0F1A !important;
	border-color: #ff0000 !important;
	border-style: solid !important;
	border-width: 2px !important;
}

div.row {
	background: 0 !important;
	border-color: #00ff3c !important;
	border-left-width: 0 !important;
	border-radius: 5px !important;
	border-right-width: 0 !important;
	border-style: ridge !important;
	border-top-width: 0 !important;
}

div.row.rc.rc-8.border-1.slider-item.active-device {
	border-color: #000000 !important;
	border-style: none !important;
	color: #ff9191 !important;
}

div.rt {
	border-radius: 999px !important;
}

div.slider-message {
	border-style: none !important;
	border-width: 0 !important;
}

div.slider-scale {
	background-color: #f7f7f7 !important;
	filter: invert(90%) !important;
}

div.tab-content.border.rc {
	background-color: #1a151a !important;
	border-color: #ff0000 !important;
	border-style: solid !important;
	border-width: 2px !important;
}

div.tarriff-info strong {
	color: yellow !important;
	font-family: Palatino !important;
	font-weight: normal !important;
	text-shadow: none !important;
}

dt span a {
	color: #00ff0d !important;
	text-decoration: none !important;
}

dt.selected span {
	color: #1aff00 !important;
	font-style: italic !important;
	font-variant: normal !important;
	font-weight: bold !important;
	text-transform: none !important;
}

h2.b-title.b-title_type_auth {
	border-color: #000000 !important;
	border-style: none !important;
	color: #00e379 !important;
}

h3.device-title {
	color: #26ff00 !important;
	font-style: normal !important;
	font-variant: normal !important;
	font-weight: bold !important;
	text-transform: none !important;
}

h3.highlighted {
	color: #9e8383 !important;
}

body {
	background-color: #000000 !important;
}

img.png {
	filter: invert(80%) !important;
}

input.b-field__input.js-field-input {
	background-color: #000000 !important;
	color: #ffffff !important;
}

input.button {
	color: #00ff48 !important;
}

input.payment-amount__input {
	background-color: #000000 !important;
	border-color: #ff0000 !important;
	border-radius: 400px !important;
	border-style: solid !important;
	border-width: 2px !important;
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.5) !important;
	color: #00ff0d !important;
}

input.text-input {
	background-color: #1a0f1a !important;
	border-color: #ff0000 !important;
	border-style: solid !important;
	border-width: 2px !important;
}

li.success {
	background-color: #ff0000 !important;
	color: #03fc07 !important;
	font-size: 14px !important;
	font-weight: bold !important;
	text-decoration: none !important;
}

span.b-button__text {
	font-family: Consolas !important;
	font-size: 24px !important;
	margin: -10px !important;
	margin-top: 2 !important;
}

span.b-radio-tabs__tab.js-tab {
	border-color: #dbc7db !important;
	border-style: solid !important;
	border-width: 1px !important;
}

span.b-radio-tabs__tab.js-tab[data-type="internet"] {
	border-radius: 0 999px 99px 0 !important;
}

span.b-radio-tabs__tab.js-tab[data-type="mobile"] {
	border-radius: 999px 0 0 99px !important;
}

svg.b-b2c-auth__tooltip-icon use {
	color: #000000 !important;
	font-size: 20px !important;
}

.b-b2c-header__icon {
	width: 56px;
	height: 100%;
	color: #00D60F;
}

table.styled tbody td.first {
	border-left: 1px solid #332733 !important;
}

td.last {
	font-family: Lato !important;
	font-size: 14px !important;
	font-weight: bold !important;
}

thead tr td {
	background-color: #2b272b !important;
	border-bottom-width: 0 !important;
	border-color: #fa0000 !important;
	border-left-width: 1px !important;
	border-right-width: 1px !important;
	border-style: solid !important;
	border-top-width: 0 !important;
	font-size: 15px !important;
}

ui-slider-handle ui-state-default ui-corner-all {
	filter: invert(85%) !important;
}

input.b-field__input.js-field-input {
	border-style: solid !important;
	border-color: #ff0000 !important;
	border-width: 3px !important;
	background-color: #000000 !important;
}

.b-field_theme_bordered {
	box-shadow: none !important;
}

.b-tooltip_type_auth .b-content,
.b-tooltip_type_auth .b-tooltip__message:after,
.b-tooltip_type_auth .b-tooltip__message:before {
	background: #1f1f1f !important;
}
}
