css:
@-moz-document domain(fast.com) {

* {
	font-family: "Roboto" !important;
    color: #212121 !important;
    transition: all 0.15s ease-in-out;
    user-select: none;
    overflow: hidden !important;
}

html, body {
    background: #fafafa;
}

.logo-container,
.footer-container,
.after-test-actions-container,
.spinner {
    display: none !important;
}

.wrapper {
    position: absolute;
    top: 50%;
    height: auto;
    transform: translateY(-50%);
    margin-left: -45px;
}

.speed-results-container {
	margin-top: 35px;
}

.speed-results-container * {
    user-select: initial !important;
    cursor: text;
}

.bordered-speed-container {
    border: none;
}

.speed-right-container {
    margin-top: 35px;
}

.speed-units-container {
    text-transform: uppercase;
    font-size: 22px;
    text-align: center;
    padding-right: 34px;
    font-weight: 500;
    letter-spacing: initial !important;
    padding-top: 20px;
    user-select: none;
    pointer-events: none;
}

.circle {
    border: none;
    transform: scale(1.4);
}

}
