css:
@-moz-document domain("wikipedia.org") {
@font-face {
        font-family: 'Cormorant';
        font-style: normal;
        font-weight: 400;
        src: local('Cormorant Regular'), local('Cormorant-Regular'), url(https://fonts.gstatic.com/s/cormorant/v6/a3k4tdhPAhIrqJlC0EH98qCWcynf_cDxXwCLxiixG1c.ttf) format('truetype');
    }
    @media print {
        body {
            font-size: 10pt !important;
        }
    }
    body {
        font-size: 16pt;
    }
    body,
    h1,
    h2,
    h3,
    h4 {
        background: #fefefe;
        font-family: 'Cormorant', serif !important;
    }
    a {
        transition: color 100ms linear;
        color: #2983c7;
    }
    a:hover {
        color: #11b2e6;
        text-decoration-color: #88b8c7;
    }
    a:focus {
        text-decoration: none;
    }
    a:visited {
        color: #777;
    }
    .infobox th {
        background-color: #e4e4e4 !important;
    }
    h1,
    h2,
    h3,
    h4 {
        font-variant: small-caps;
    }
    #content {
        background: transparent;
        border: none;
        color: #333;
        width: 700px;
        margin: 0 auto;
    }
    #mw-head {
        opacity: 0;
        transition: opacity 150ms linear;
    }
    #mw-head:hover {
        opacity: 1;
    }
    #mw-page-base {
        background: none;
    }
    #mw-panel .portal {
        opacity: 0;
        transition: opacity 150ms linear;
    }
    #mw-panel:hover .portal {
        opacity: 1;
    }
    #p-logo {
        display: none;
    }
    #toc {
        display: none;
    }
    .metadata.ambox {
        opacity: 0.25;
        transition: opacity 150ms linear;
    }
    .metadata.ambox:hover {
        opacity: 1;
    }
    .mw-headline {
        color: black;
        font-size: 1.5em;
        font-weight: 200;
    }
    .vertical-navbox {
        display: none;
    }
    div.vectorTabs li a {
        transition: color 100ms linear;
        color: #888;
        font-size: 14pt;
    }
    div.vectorTabs li a:hover {
        text-decoration: none;
        color: #333;
    }
    div.vectorTabs li.selected a {
        color: #08d69f !important;
    }
    div.vectorTabs span,
    div.vectorTabs span a,
    div.vectorTabs ul li {
        background: #fefefe;
        border: none;
    }
    div.vectorTabs ul li {
        line-height: 1em;
    }
    div.vectorTabs,
    #ca-talk {
        background: transparent;
    }
}
