css:
@-moz-document domain("global.cainiao.com") {
body {
  border-color: black !important;
}

.list-body {
  background: #1f1f1f;
}

.waybill-list-info {
  min-height: 12px;
  padding: 10px 18px;
  position: relative;
  background: #1f1f1f;
  color: #fde4c0;
  border-color: black;
}

.waybill-list-item.waybill-list-active {
  background-color: #353535;
}

.num-color01 {
  color: #ee5a3c;
  margin: 0 5px;
  font-size: 20px;
}

.waybill-path p {
  color: #9a9a9a;
  line-height: 1.5em;
  font-size: 13px;
}

.waybill-path>li:first-child p {
  color: #deaa7a;
  font-size: 20px;
}

.waybill-num {
  font-size: 20px !important;
  color: #991d2c!important;
  margin: 5px !important;
}

.waybill-list-item dd {
  min-height: 1.5em;
  color: #9a9a9a;
  border-color: black;
}

.waybill-list-item dd:first-child {
  color: #f5fff5 !important;
}

.waybill-list-item {
  padding: 10px 20px;
  line-height: 1.5em;
  position: relative;
  border-bottom: 1px solid #f3f3f3;
  cursor: pointer;
  color: #1f1f1f;
  background-color: #1f1f1f;
  border-color: black;
}

/*.waybill-list-item:hover{
	zoom: 120%;
	animation-timing-function:ease-in-out;
}
*/

.waybill-detail {
  width: 740px;
  margin: 0 -1px;
  padding: 20px 20px 65px 15px;
  position: relative;
  background-color: #1f1f1f;
  border-color: black;
}

.waybill-detail-title {
  font-size: 26px;
  line-height: 1em;
  height: 1em;
  margin-bottom: 5px;
  font-weight: 700;
  color: #f1cb9e;
}

.waybill-path>li:first-child p {
  color: #e29d62;
}

.waybill-path p {
  color: #9a9a9a;
  line-height: 1.5em;
}

.waybill-country-box .dd-country {
  height: 14px;
  font-size: 25px;
  color: #ececec;
}

.waybill-detail-footer {
  padding: 10px 20px;
  opacity: .9;
  text-align: right;
  height: 45px;
  position: fixed;
  z-index: 210;
  left: 0;
  width: 740px;
  box-sizing: border-box;
  border-top: 1px solid #f1f1f1;
  display: inherit;
  bottom: 0px !important;
  background-color: #1f1f1f;
  border-color: black;
  color: #d8d8d8;
}

#waybill_info_box, #list_body_wrap, #waybill_list_box, .cn-common-footer, .ccf-info {
  background-color: #1f1f1f;
  border-color: black;
  overflow: hidden;
}

.cn-common-footer .ccf-info {
  font-size: 12px;
  padding-top: 20px;
  text-align: center;
  border-top: 1px solid #f0f0f0;
  background-color: #1f1f1f;
}

.waybill-info-title {
  font-size: 15px;
  color: #de6145;
}

.waybill-info-txt {
  color: #f1cb9e;
}

#waybill_info_box {
  border-color: black;
}

.waybill-detail-title-box {
  border-color: black;
}

#waybill_path li {
  border-color: black;
}

#waybill_country_box .waybill-fail-dest, .waybill-fail-origin {
  border-right-color: black !important;
  border-top-color: black !important;
  border-bottom-color: black !important;
  border-left-color: #de392a;
}

#waybill_detail_footer {
  background-color: #1f1f1f !important;
  opacity: 100 !important;
}

#waybill_detail_footer * {
  background-color: #1f1f1f !important;
  opacity: 100 !important;
}

#waybill_img_box {
  -webkit-filter: invert(100%);
  filter: invert(100%);
}

/* Active border animation */

.waybill-list-item.waybill-list-active {
  animation: activeNow 2s ease-in-out, highlightIt 2s ease-in-out infinite;
  border: 2px solid rgba(255,0,0,0.2);
  border-radius: 20px;
  margin: 10px 0;
}
@-moz-keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@-webkit-keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@-o-keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@-moz-keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}
@-webkit-keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}
@-o-keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}
@keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}

}
