css:
@-moz-document domain("www.giantbomb.com") {
html
{
    color: /*[[textcolor]]*/ !important;
    background-color: /*[[backgroundcolor]]*/ !important;
}

.table-striped tbody tr:nth-child(odd) td, .table-striped tbody tr:nth-child(odd) th, .overlay-stats>i, .editorial-stats>i {
    background-color: /*[[backgroundcolor]]*/;
}

.dropnav dl, #forums .table-forums .type, .message-inner, .modal {
border: 1px solid /*[[menubordercolor]]*/;
background-color: /*[[backgroundcolor]]*/ !important;
}

.message-inner:before{
border-right-color: /*[[backgroundcolor]]*/;
}

a:link {
color: /*[[linkcolor]]*/ !important;
    text-decoration: none;
}

a:hover {
color: /*[[linkhovercolor]]*/ !important;
    text-decoration: underline;
}

.no-touch .editorial>li>a:hover h2, .no-touch .editorial>li>a:hover h3, .no-touch .editorial>li>a:hover:after, .no-touch .editorial>dd>a:hover h2, .no-touch .editorial>dd>a:hover h3, .no-touch .editorial>dd>a:hover:after, .no-touch .editorial>dt>a:hover h2, .no-touch .editorial>dt>a:hover h3, .no-touch .editorial>dt>a:hover:after
{
color: /*[[linkhovercolor]]*/ !important;
}

.sub-nav div>ul>li select, .sub-nav div>ul>li.active select {
color: /*[[linkcolor]]*/ !important;
}

.av-player-athena .av-splash-premium, .editorial .video:before, .editorial .podcast:before, .editorial .content--premium .video:before, .editorial .content--premium .podcast:before {
filter: hue-rotate(/*[[playerhuecolor]]*/deg);
}

.js-vid-player-chrome.js-vid-box.av-chrome.av-chrome--bottom {
background-color: rgba(/*[[Videoplayerbkcolor]]*/);
}

.grad-text{
background-image: -webkit-gradient(linear, left top, left bottom, from(/*[[linkcolor]]*/), to(/*[[linkcolor]]*/)) !important;
}

.kubrick-cover a, .kubrick-cover h3, .kubrick-cover p  {
background-color: rgba(/*[[wikiheadbkcolor]]*/);
}

/* player icon */
.av-player-athena .av-splash-top:before, .av-player-athena .av-splash-bottom:before
{
background-color: /*[[linkcolor]]*/;
}

.video-progress.has-data span

{
background-color: /*[[videoprogresscolor]]*/;
}

.video-progress.has-data
{
background-color: /*[[videoprogressbkcolor]]*/;
}



*, html > *, body > *, 
.kubrick-lead, .promo-upcoming, .promo-twitter-feed .editorial li,
#nav-info, #nav-main-hold, #nav-main>ul>li:hover, #nav-main>ul>li.active, #nav-main>ul>li.active:hover, .header-promotion__wrapper > span, .header-promo--pop:before, .promo-upcoming div,
#nav-main .dropnav dl, .kubrick-lead.video,  .promo-strip, .promo-upcoming dt,
.promo-strip .hdr-premium h4, .promo-strip h4, .btn-inverse, .sub-nav, .sub-nav div>ul>li:hover, .grad-theme, .nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-pills-super>.active>a, .nav-pills-super>.active>a:hover, .sub-nav div>ul>li:hover, .sub-nav div>ul>li.active, .data-count:before, .sub-nav div>ul>li select, .object-stats-wrap, .kubrick-lead.video-active .kubrick-promo-video-cover, .kubrick-info.no-sticky, .show-more-link:before, .show-more-link, .no-touch .show-more-link:hover, .no-touch .btn-primary:hover, .btn-primary, 
.av-podcast, .sub-nav div>ul>li.active select, .kubrick-cover, html .darkauth--body, .darkauth--body.auth .container-fluid, .darkauth--body #nav-info, .kubrick-strip, 
.mastfooter, .message-wrap, .tome .toolbars, .tome, .wysiwyg-article, .modal-footer, .btn
	{
	background-color: transparent;
    color: inherit !important;
    box-shadow:none !important;
    text-shadow: none !important;
    }

.promo-strip li:before, .promo-strip li:first-child:after, .promo-strip li:last-child:after, .kubrick-promo-video:before, .kubrick-promo-video:after,.av-player-athena .av-chrome--bottom:before, .kubrick-promo-article:before, .imgcast:before, .kubrick-promo-article:after, #nav-info, #nav-main-hold, #nav-main>ul>li:hover, #nav-main>ul>li.active, #nav-main>ul>li.active:hover,
.header-promotion__wrapper > span, .kubrick-info, #nav-main .dropnav dl, .grad-dark, .header-skinSwitch .skin-switch-btn.black, .avatar-user-info .dropdown-menu, .promo-upcoming, .tome .toolbar button.buttonActive, .tome .toolbar button:hover, .tome .toolbar .dropdown ul button.on, .tome .toolbar .dropdown ul button.on:hover, .gallery-header .gallery-header-grad, .profile-avatar, .promo-upcoming dd, .promo-upcoming div, .promo-upcoming dt, .header-logo, .promo-strip .hdr-premium h4, .promo-strip h4, .header-borders:before, .header-border, .header-borders,
.btn, .sub-nav, .grad-theme, .nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-pills-super>.active>a, .nav-pills-super>.active>a:hover, .sub-nav div>ul>li:hover, .sub-nav div>ul>li.active, .data-count:before, .sub-nav div>ul>li select, .object-kubrick, .no-touch .show-more-link:active, .show-more-link, .show-more-link:before, .feed-hdr, .editorial>li, .editorial>dd, .editorial>dt, .kubrick-promo-podcast, .av-podcast, .wiki-hdr p.wiki-descriptor, .promo-strip li:before, .mastfooter, .kubrick-strip, #forums .table-forums .type, .tome .toolbars
{
background-image: none !important;
       } 

.no-touch .kubrick-info a:hover h2{
    text-shadow: none !important;
}

#nav-main-hold:after, #nav-main>ul>li, #nav-main-hold, #nav-main > ul, .header-section--border:before, .header-section--border:after, .promo-strip, .header-promo--premium, .grad-dark-border, .av-podcast-playpause span, .av-player-athena .av-postplay-premium .icon, .sub-nav div>ul>li select, .overlay-stats>i, .editorial-stats>i, .avatar-user-info .dropdown-menu, .promo-upcoming, .tome .toolbar button.buttonActive, .tome .toolbar button:hover, .profile-avatar, .no-touch .kubrick-info a:hover h2, .btn-inverse, .btn, .sub-nav div>ul>li, .object-stats-wrap, .av-podcast, .no-touch .kubrick-info a:hover h2
{
    border-left-width: 0px !important;
	border-right-width: 0px !important;
	border-bottom-width: 0px !important;
	border-top-width: 0px !important;
}


.kubrick-lead:before, #nav-main>ul>li:hover:before, #nav-main>ul>li.active:before, #nav-main>ul>li.active:hover:before, #nav-main-hold:after,
.header-logo:before, .sub-nav:before, .sub-nav div>ul>li:hover:after, .sub-nav div>ul>li.active:after, .object-kubrick, .feed-hdr:before, .mastfooter:after
{
display: none;
}


.header-promo
{
max-width: 350px;
}

.header-upsell.hide-mobile.header-upsell--promotion.andre
{
width: 310px;
}

.header-promotion.header-section.header-section--border
{
flex-grow: 1;
    margin-left: 0px;
}

.header-promo--promotion, .header-promo--promotion.header-promo--subscriber

{
    margin-right: 240px;
}

.imgflare:after, .note-tray .note-tray-activity .img a:after, .header-section--border:before,
#nav-main .dropnav dl:after, .show-more-link:before
{
    box-shadow:none !important;
    text-shadow: none !important;
}

/* menu background color */
#nav-main .dropnav dl:after {
    background-color: inherit !important;
}


#nav-main{
width: /*[[menuwidth]]*/;
}








/* dealing with images behind text */
.kubrick-promo-article {
background-position: right center !important;
    background-size: 50% ;
    background-repeat: no-repeat ;
}
    
.kubrick-promo-video {
background-position-y: 25% !important;
background-position-x: 88% !important;
   background-size: 55% ;
}

.promo-strip li
{
background-position-y: 0% !important;
background-position-x: 0% !important;
   background-size: 330px ;
   background-repeat: no-repeat;
   height: 320px;
}

.promo-strip h4
{
left:0px;
}


/* */
.promo-upcoming dt
{
text-indent: initial;
padding: 10px 15px;
}


#nav-main .dropnav>a:after
{
content: "\2bc6";
opacity: 1;
}

/* main menu left */

#nav-main-hold{
margin-top: 2em;
margin-bottom: 2em;
}

/* article */

.kubrick-info
{
padding-top: 0em;
}

.promo-strip h4
{
padding-left: 0em;
}



.sub-nav div>ul>li select:hover{
 text-decoration: underline !important;  
}

.header-logo
{	
    vertical-align: middle;
    width: /*[[LogoGBwidth]]*/;
    font-size: 0px !important;
}

.header-logo.header-section:after{
content: "/*[[LogoGB]]*/";
    padding-top: 0px;
}


.promo-strip ul{
display: flex;
}

.promo-strip ul > li{
display: block;
}

.promo-strip li>div {
width: 330px ;
}


.promo-strip .promo-strip-width {
    max-width: /*[[promowidth]]*/;
}

.promo-strip .hdr, .promo-upcoming dt, .promo-upcoming div{
padding-left: 0px;
}

#wrapper > section > div > ul > li:nth-child(3){
padding-right: 0px;
}


#wrapper > section > div > ul > li > div
{
    display: block;
    float: left;
}

.promo-strip ul:before, .promo-strip ul:after, .promo-strip li:before, .promo-strip li:after
{
display: none;
}

.promo-strip li:nth-child(1), .promo-strip li:nth-child(2), .promo-strip li:nth-child(3) {
width:100%;
}



/* FONTS */

html, body, body, input, button, textarea, select, a, p, div, span, dl, dt {
 	font-family: "/*[[Fontfamily]]*/" !important;
    font-weight: /*[[Fontweight]]*/ !important;
    font-style: /*[[Fontstyle]]*/ !important;
    text-rendering: optimizeLegibility!important;
   
}

h1, h2, h4, h3, 
#nav-main > ul > li > a,
#nav-main > ul > li > dl > dt > a {

	font-family: "/*[[Fontfamily]]*/" !important;
    font-style: /*[[Fontstylemenu]]*/ !important;
    font-weight: /*[[Fontweightmenu]]*/ !important; 
    text-transform: uppercase !important;
}


.header-logo.header-section:after {
    font-size: 1.5rem !important;
}

.header-logo {
font-family: "/*[[Logofont]]*/" !important;
color: /*[[Logocolor]]*/ !important;
}

html{
font-size: /*[[Fontsz]]*/;
}

body, input, button, textarea, select, a, p, div, span, dl, dt,h1, h2, h4, h3, nav, time,
.spotlight-release .title, .spotlight-release dd, .editorial p, .editorial .editorial-excerpt, .editorial>li, .editorial>dd, .editorial>dt, .overlay-stats>i, .editorial-stats>i, #forums .table-forums
{	
    font-size: inherit !important;
}

}
