css:
@-moz-document url-prefix("https://www.ncbi.nlm.nih.gov/")
{
.col,
.header,
body .page-box,
body .page-box-wide {
    background-color: #121212 !important;
}
#maincontent .links-box,
.grid .lit-style h2,
.format-menu h2,
.share-buttons h2,
.ten_col,
.grid .lit-style h3,
#footer {
    background-color: transparent !important;
    background: transparent !important;
}
.figpopup {
    background-color: transparent !important;
}
.ui-ncbisetswitch {
    background-color: transparent !important;
}
.header,
.pmc-wm {
    background-image: none !important;
}
[color="#000"],
[color="#000000"],
[color="Black"] {
    color: #ffffff !important;
}
.page-breadcrumbs,
.grid .lit-style h2,
.format-menu h2,
.share-buttons h2,
.format-menu ul li.selected {
    color: #FFFFF !important;
}
.ui-ncbisetswitch .jig-ncbisetswitch {
    color: #AAAAAA !important;
}
[color="#00F"],
[color="#0000FF"],
[color="Blue"],
[color="Navy"],
[color="#000080"],
[color="#0000F0"],
[color="#0000ee"],
.fm-citation-ids span {
    color: #3399FF !important;
}
.pmc-page-banner,
#rightcolumn,
.grid .lit-style.content {
    word-break: keep-all !important;
}
.grid .lit-style.content p {
    font-size: 16px !important;
    line-height: 120% !important;
}

.tsec {
    text-align: justify !important;
}

.page-breadcrumbs,
.figpopup {
    font-size: 16px !important;
}
.grid .lit-style h3 {
    font-size: 24px !important;
}
body * {
    font-family: "文泉驛微米黑", "WenQuanYi Micro Hei", "Droid Sans Fallback", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif;
}
body * {
    text-decoration: none !important;
    text-shadow: none !important;
    box-shadow: none !important;
}
html,
body {
    color: #FFFFFF;
    background: #121212 !important;
    background-color: #000000 !important;
    background-image: none !important;
}
body h1,
body h2,
body h3,
body h4,
body h5,
body h6,
body ul,
body li,
body ol,
body span,
body p,
body dt,
body dd,
body dl {
    background-color: #121212 !important;
}
body h1,
body h2,
body h3,
body h4 {
    color: #00ddc1 !important;
}
body h5,
body h6,
body th,
body em,
body b,
body strong,
label {
    color: #FFFFFF;
}
body div,
body p {
    color: #FFFFFF;
}
body blockquote {
    color: #AAAAAA !important;
    background-color: #2D2D2D !important;
    background-image: none !important;
}
body textarea {
    color: #f4ff4a !important;
    background-color: #2D2D2D !important;
    background-image: none !important;
}
body table,
body tr,
body td {
    background-color: transparent;
    color: #AAAAAA;
    background-image: none !important;
}
body select,
body option {
    background-color: #2D2D2D !important;
    color: #f4ff4a !important;
}
body input {
    color: #f4ff4a !important;
    background-color: #2D2D2D !important;
}
body button,
body input[type="button"],
body input[type="submit"] {
    color: #000000 !important;
    background-color: #CCCCCC !important;
}
body a {
    color: #f4ff4a !important;
}
body a:link {
    color: #f4ff4a;
}
body a:visited {
    color: #AAAAAA;
}
body a:hover {
    color: #3399FF !important;
}
body a:active {
    color: #423F3F !important;
}
lightbox * {
    background: transparent !important;
    background-color: transparent !important;
}
.pmc_para_cit {
    border: none !important;
    color: transparent !important;
    background-color: transparent !important;
    background-image: none !important;
    background: transparent !important;
}
.contact_info {
    border: none !important;
    color: transparent !important;
    background-color: transparent !important;
    background-image: none !important;
    background: transparent !important;
}
.ui-ncbipopper-basic {
    color: #f4ff4a !important;
    background-color: #000000 !important;
    background-image: none !important;
    background: #000000 !important;
}
.ui-ncbipopper-wrapper {
    color: #f4ff4a !important;
    background-color: #000000 !important;
    background-image: none !important;
    background: #000000 !important;
}
.oneLevel li a,
.ui-ncbibasicmenu li a {
    color: #f4ff4a !important;
    background-color: #336699 !important;
    background-image: none !important;
    background: #336699 !important;
}
.ui-ncbisetswitch .jig-ncbisetswitch {
    color: #000000 !important;
}
.portlet .two_line .tertiary {
    color: #00fff7 !important;
    background-color: #000000 !important;
}
ul.inline_list a .value {
    color: #ffffff !important;
}
.ncbi_carousel {
    background-color: #000000 !important;
}
div#gd-lcl-nav.gd-lcl-nav-howto ul {
    border-left: 3px solid #000000 !important;
}

.oneLevel li a, .ui-ncbibasicmenu li a {
    background: #121212 !important;
}

.grid {
    min-width: 800px;
    max-width: 1600px;
    text-align: justify;
}

.universal_header {
    background-color: #121212 !important;
}
.abstract .abstr p {
font-size: 16px !important;
}
.content {
    background-color: #121212 !important;
}
ul.ui-ncbilocalnav, ul.ui-ncbilocalnav li {
    background-color: #121212 !important;
    background-image: none !important;
}
.built_search {
    background-image: none !important;
    background-color: #121212 !important;

}
th {
    background-image: none !important;
    background-color: #121212 !important;
}
.ui-ncbimenu li>a {
    background-color: #121212 !important;
}
a {
    background-color: transparent !important;
}
div#gd-lcl-nav.gd-lcl-nav-howto ul li {
    background-image: none !important;
}
div#gd-lcl-nav ul li {
    background-image: none !important;
}
.home_page {
    background-color: #121212 !important;
}
.ui-widget-content {
   background: none !important;
}
.ui-ncbiclearbutton-wrap {
    background: #121212 !important;
}
.grid {
    background: #121212 !important;
}
.sensor_inner {
    background: #121212 !important;
}

ul.ui-ncbiautocomplete-options li {
color: white !important;
}

.bg-gray {
    background-color: none !important;
}
.table-wrap {
    background-color: none !important;
}
.iconblock {
    background-color: none !important;
}

.iconblock, .table-wrap {
    background-color: none !important;
}
.table-wrap, .table, .anchored, .whole_rhythm {
    background-color: none !important;
    background: none !important;
}
}
