css:
@-moz-document url-prefix(http://stash), url-prefix(https://stash) {
h1,
h2,
h3,
h4,
h5,
th,
a,
p,
span
:not([class^="cm-"])
:not([class="ediff-add"]),
label,
span.stub,
span.message-subject,
textarea,
input,
.line-number,
button,
code,
div.commit-message > pre,
div.commit-badge-oneline,
.aui-message,
.aui-badge,
span[role="menuitemcheckbox"],
span[role="menuitemradio"],
span.pull-request-id,
dd,
dt,
li {
    color: #D1D1D1 !important;
    text-shadow: none !important;
}
#header > nav,
header.aui-dialog2-header {
    background: #000000;   
}
button[disabled="disabled"] {
    color: #999999 !important;
    text-shadow: none !important;
}
div:not(.pullRequestStatusBox):not(.line):not(.avatar-with-name):not([class="CodeMirror-gutter-wrapper"]),
.diff-view.text-view .context.line .add-comment-trigger,
pre,
code,
div.comment-container comment-box *,
.aui-lozenge,
button[disabled="disabled"],
.aui-dropdown2.aui-style-default:hover .aui-dropdown2-checkbox, .aui-dropdown2.aui-style-default:hover .aui-dropdown2-radio, .aui-dropdown2.aui-style-default:hover a,
button.stash-link-button,
#diff-options-dropdown:hover,
span.ref-label,
footer {
    background: #383838 !important;
    box-shadow: none !important;
}
.menu-item.active-tab > a,
.aui-navgroup-vertical .aui-nav>li>a:focus, .aui-navgroup-vertical .aui-nav>li>a:hover, .aui-nav-vertical>li>a:focus, .aui-nav-vertical>li>a:hover{
    background: #383838 !important;
    color: #D1D1D1;
}
.menu-item > a,
.jstree-clicked,
.aui-nav-selected > a,
.aui-badge,
textarea,
input,
div > a[class^="select"],
div > a.aui-button,
button {
    background: #525252 !important;
    color: #D1D1D1;
}
.side-by-side-diff .line .CodeMirror-gutter-elt>*,
.side-by-side-diff .modified.line+.context.line,
.side-by-side-diff .context.line+.modified.line,
.side-by-side-diff .context.line+.conflict-ours.line,
.side-by-side-diff .conflict-ours.line+.context.line,
.side-by-side-diff .commented.line+.context.line,
.side-by-side-diff .context.line+.commented.line,
.side-by-side-diff .paired-with-change,
.side-by-side-diff .line,
.side-by-side-diff .CodeMirror-code>* {
    border-top-color: #383838 !important;
}
div.CodeMirror-code {
    background-color: #002b36 !important;
    color: #839496 !important;
}
a.change-type-ADD {
    color: #619217 !important;
}
a.change-type-MODIFY {
    color: #237FB9 !important;
}
.emphasis {
    font-weight: bold !important;
}
.dotted {
    border-bottom: 1px dotted #cb4b16 !important;
}
.CodeMirror-gutter {
    background-color: #073642 !important;  
}
.CodeMirror-gutter .CodeMirror-gutter-text {
    color: #586e75 !important;
}
.CodeMirror-cursor {
    border-left-color: #fdf6e3 !important;
}
.CodeMirror-matchingbracket {
    color: #fdf6e3 !important;
    background-color: #073642 !important;
    box-shadow: 0 0 10px #073642 !important;
    font-weight: bold !important;
}
.CodeMirror-nonmatchingbracket {
    background-color: #073642 !important;
    box-shadow: 0 0 10px #073642 !important;
    font-weight: bold !important;
    color: #dc322f !important;
    border-bottom: 1px dotted #cb4b16 !important;
}
span.cm-keyword {
    color: #839496 !important;
    font-weight: bold !important;
}
span.cm-atom {
    color: #2aa198 !important;
}
span.cm-number {
    color: #93a1a1 !important;
}
span.cm-def {
    color: #268bd2 !important;
}
span.cm-variable {
    color: #cb4b16 !important;
}
span.cm-variable-2 {
    color: #cb4b16 !important;
}
span.cm-variable-3 {
    color: #cb4b16 !important;
}
span.cm-comment {
    color: #586e75 !important;
}
span.cm-property {
    color: #b58900 !important;
}
span.cm-operator {
    color: #839496 !important;
}
span.cm-string {
    color: #6c71c4 !important;
}
span.cm-error {
    font-weight: bold !important;
    border-bottom: 1px dotted #cb4b16 !important;
}
span.cm-bracket {
    color: #cb4b16 !important;
}
span.cm-tag {
    color: #839496 !important;
}
span.cm-attribute {
    color: #93a1a1 !important;
    font-weight: bold !important;
}
span.cm-meta {
    color: #268bd2 !important;
}
div.modified >pre > span,
div.modified .line-number {
    color: #232323 !important;
}
div.added,
div.added > div.CodeMirror-gutter-wrapper *,
div.added > pre,
.diff-view.text-view .added.line,
.diff-view.text-view .added.segment-connector,
.diff-view.text-view .added.line .comment-box,
.diff-view.text-view .added.line .bitbucket-gutter-marker,
div.added > div.CodeMirror-gutter-wrapper > div:nth-child(1),
div.codemirror-auto-resizing-container div.added > div.CodeMirror-gutter-wrapper > div:nth-child(3),
div.added > div.CodeMirror-gutter-wrapper {
    border-color: #DDFFDD !important;
    background-color: #DDFFDD !important;
}
div.removed,
div.removed > div.CodeMirror-gutter-wrapper *,
div.removed > pre,
.diff-view.text-view .removed.line,
.diff-view.text-view .removed.line .bitbucket-gutter-marker,
.diff-view.text-view .removed.segment-connector,
.diff-view.text-view .removed.line .comment-box,
.diff-view.text-view .removed.line .bitbucket-gutter-marker div.removed .diff-view.text-view .added.line .bitbucket-gutter-marker,
#commit-file-content > div.content-view.diff-view.commentable.text-view.side-by-side-diff.diff-type-MODIFY.animated.full-window-scrolling.fully-loaded.diff-api-ready > div.diff-editor.side-by-side-diff-editor-from > div.CodeMirror.cm-s-stash-default > div.CodeMirror-scroll > div.CodeMirror-sizer > div > div > div > div.CodeMirror-code > div.removed.modified.last-focus.line > div > div > div,
div.removed > div.CodeMirror-gutter-wrapper > div:nth-child(1),
div.codemirror-auto-resizing-container div.removed > div.CodeMirror-gutter-wrapper > div:nth-child(3),
div.removed > div.CodeMirror-gutter-wrapper{
    border-color: #FEE8E9 !important;
    background-color: #FEE8E9 !important;
}
:after {
    background-image: none !important;
}
div.CodeMirror-gutter-wrapper {
	border-top:1px solid !important;
}
div.line:not(.modified) > pre:hover{
 	background: #0E4B5A   !important;
}
div.line.added > pre:hover{
 	background: #BBF3BB   !important;
}
div.line.removed > pre:hover{
 	background: #F1CDCF  !important;
}
header.aui-dialog2-header {
    border: 1px solid #CFCFCF;
    border-bottom: none;   
}
div.aui-dialog2-content {
    border-left: 1px solid #CFCFCF;
    border-right: 1px solid #CFCFCF;
}
footer.aui-dialog2-footer {
    border: 1px solid #CFCFCF;
    border-top: none;   
}

/** For bitbucket 4.4 */
.pull-request-row .reviewers {
    left: 0px;                      
} 
.reviewers {
 	background: #383838;
}
.CodeMirror-gutter-elt > a {
    background-color: #073642 !important;  
}
span.commit-author {
    color: #586e75 !important;
}
span.sep {
    color: #D1D1D1 !important;
}
td {
    background: #383838 !important;
    color: #D1D1D1;
}
}
