css:
@-moz-document domain("community.wemod.com") {
/*   WeMod Dark Mode  */
/*    -- V.1.05 --    */

/* Body & Navigation  */
body {
    background-color: #22232b;
    color: #fff;
}
.btn {
    border: none;
    color: #e2e2e2;
    font-weight: normal;
    background: #191a1f;
}
.btn:hover, .btn.btn-hover {
    background: #22242b;
    color: #fff;
}
.d-header {
    background-color: #191a1f;
}
span#top-navbar-links a {
    padding: 6px 13px;
    text-transform: uppercase;
    font-size: 14px;
    font-family: 'Montserrat', sans-serif;
    color: #e2e2e2;
    font-weight: bold;
}
span#top-navbar-links a:hover {
    color: #fff;
}
.d-header .icons .icon:hover {
    background-color: #22242b;
}
.drop-down-visible .d-header-icons .active .icon {
    color: #7b7b7b;
    background-color: #22242b;
    border-top: 1px solid #22242b;
    border-left: 1px solid #22242b;
    border-right: 1px solid #22242b;
}
.menu-panel {
    border: 1px solid #22242b;
    background-color: #191a1f;
}
.drop-down-visible .d-header-icons .active .icon:after {
    border-top: 1px solid #22242b;
}
hr {
    border-top: 1px solid #191a1f;
}
.user-menu .notifications .read {
    background-color: #191a1f;
}
.user-menu .notifications li span {
    color: #fff;
}
.user-menu .notifications li:hover {
    background-color: #22242b;
}
.user-menu .notifications li {
    background-color: #191a1f;
}
.menu-panel ul.menu-links li a:hover, .menu-panel ul li.heading a:hover {
    background-color: #22242b;
}
div.menu-links-header a:hover {
    background-color: #191a1f;
    color: #fff;
}
.btn[href] {
    color: #fff;
}

/* Sections */
.category-list tbody tr:first-of-type {
    border-top: 1px solid #191a1f;
}
.category-list tbody tr {
    border-bottom: 1px solid #191a1f;
}
.table-heading {
    border-bottom: 1px solid #191a1f;
}
.latest-topic-list-item {
    border-bottom: 1px solid #191a1f;
}
.nav-pills>li>a:hover {
    color: #fff;
}
.nav-pills>li>a:hover {
    background-color: #191a1f;
}

/* Category Tags */
.nav-pills>li>a {
    color: #fff;
}
.nav-pills>li.active>a, .nav-pills>li>a.active {
    color: #fff;
    background-color: #191a1f;
}
.list-controls .category-breadcrumb li.bullet>.dropdown-header {
    background: #191a1f;
    color: #fff;
}
.list-controls .category-dropdown-menu {
    border: 1px solid #191a1f;
    background-color: #191a1f;
}
.list-controls .category-dropdown-menu a.badge-category, .list-controls .category-dropdown-menu .dropdown-header {
    color: #fff;
}


/* Text */
.category-list tbody .category h3 a[href] {
    color: #fff;
}
.list-cell, .table-heading, .category-list td, .category-list th {
    color: #ffffff;
}
.badge-wrapper.bullet span.badge-category {
    color: #ddd;
}
.topic-list-main-link a.title, .topic-list .main-link a.title, .latest-topic-list-item .main-link a.title {
    color: #fff;
}
.latest-topic-list-item .topic-last-activity a {
    color: #ddd;
}
.discourse-tag.simple, .discourse-tag.simple:visited, .discourse-tag.simple:hover {
    color: #ddd;
}
.latest-topic-list .table-heading {
    color: #ffffff;
}
.user-info .user-detail .username a {
    font-weight: bold;
    color: #ffffff;
}

/* Threads */
.topic-list>tbody>tr:first-of-type {
    border-top: 2px solid #191a1f;
}
.topic-list>tbody>tr {
    border-bottom: 1px solid #22242b;
}
.topic-list .topic-excerpt {
    color: #f1f1f1;
}
.topic-list .topic-list-item-separator td {
    border-bottom: 1px solid #191a1f;
    line-height: 0.1em;
    padding: 0px;
    text-align: center;
}
.topic-list .topic-list-item-separator td span {
    background-color: #191a1f;
    color: #757575;
    padding: 1px 15px;
    border-radius: 10px;
    font-size: 0.929em;
    font-weight: bold;
}
.topic-avatar {
    border-top: 1px solid #191a1f;
}
.topic-body {
    border-top: 1px solid #191a1f;
}
.small-action {
    border-top: 1px solid #191a1f;
}
nav.post-controls button.create {
    color: #ffffff;
}
nav.post-controls button.d-hover {
    background: #191a1f;
    color: #fff;
}
nav.post-controls button.like.d-hover {
    color: #f34343;
    background: #22242b;
}
.timeline-container .topic-timeline .timeline-handle {
    border-radius: .8em;
    width: 0.35em;
    background-color: #191a1f;
    height: 100%;
    float: left;
    z-index: 501;
}
.timeline-container .topic-timeline .timeline-scrollarea {
    margin-top: 0.5em;
    margin-left: 0.5em;
    border-left: 1px solid;
    border-color: #191a1f;
    position: relative;
    -webkit-transform: translate3d(0, 0, 0);
}
div.poll {
    background-color: #191a1f;
    border: 1px solid #191a1f;
}
div.poll li[data-poll-option-id] {
    color: #fff;
    padding: .5em .7em .7em .5em;
}
div.poll .poll-info {
    border-left: 2px solid #22242b;
}
div.poll .poll-buttons {
    border-top: 2px solid #22242b;
}
div.poll .results .bar-back {
    background: #191a1f;
}
.topic-status-info {
    border-top: 1px solid #191a1f;
    padding: 10px 0px;
    height: 20px;
    max-width: 757px;
}
.select-box .select-box-collection {
    background: #191a1f;
}
.select-box .select-box-collection .collection:hover .select-box-row.is-highlighted:hover {
    background: #22242b;
}
.dropdown-select-box.dropdown-select-box .select-box-row:hover {
    background: #22242b;
}
.select-box .select-box-row.is-selected {
    background: #22242b;
}
.dropdown-select-box.dropdown-select-box .select-box-row .texts .title {
    flex: 1;
    font-weight: bold;
    display: block;
    font-size: 1em;
    color: #fff;
    padding: 0;
}
#share-link {
    background-color: #191a1f;
}
.modal .modal-body label {
    color: #fff;
}
.modal-footer {
    padding: 14px 15px 15px;
    border-top: 1px solid #22252b;
}
#reply-control {
    background-color: #191a1f;
}
#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview {
    background-color: #22242b;
}
#reply-control .d-editor-button-bar {
    background-color: #22252b;
}
#reply-control .d-editor-button-bar {
    border-bottom: 2px solid #191a1f;
}
#reply-control .d-editor-button-bar button {
    color: #ffffff;
}
#reply-control .d-editor-button-bar .btn:hover {
    color: #e9e9e9;
    opacity: 1;
}
.d-editor-spacer {
    margin-right: 0;
    margin-left: 0;
    background-color: #22262b;
}
#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview-header {
    color: #fff;
}
#reply-control .contents .d-editor-preview {
    border: 1px dashed #22252b;
    color: #fff;
}
.topic-map {
    margin: 20px 0 0 0;
    background: #191a1f;
    border: 1px solid #191a1f;
    border-top: none;
}
.topic-map section {
    border-top: 1px solid #22242b;
}
.topic-map .buttons .btn {
    border: 0;
    padding: 0 23px;
    color: #919191;
    background: #191a1f;
    border-left: 1px solid #191a1f;
    border-top: 1px solid #191a1f;
}
.topic-map .buttons .btn {
    border: 0;
    padding: 0 23px;
    color: #919191;
    background: #191a1f;
    border-left: 1px solid #191a1f;
    border-top: 1px solid #191a1f;
}
.topic-map .link-summary .btn:hover {
    color: #fff;
    background: #22242b;
}
.topic-map .avatars, .topic-map .links, .topic-map .information {
    color: #646464;
}
#topic-title h1 a {
    color: #fff;
}
nav.post-controls .show-replies:hover {
    background: #191a1f;
}
nav.post-controls button.has-like {
    color: #f34343;
}

/* Keyboard Shortcut */
.modal-inner-container {
    background-color: #191a1f;
}
#keyboard-shortcuts-help ul b {
    padding: 2px 6px;
    border-radius: 4px;
    box-shadow: 0 2px 0 rgba(0,0,0,0.2), 0 0 0 1px #22232b inset;
    background: #22232b;
    border: 1px solid #22232b;
    border-bottom: medium none #22232b;
    color: #8e8e8e;
    white-space: nowrap;
    display: inline-block;
}
.modal-header {
    border-bottom: 1px solid #22242b;
}

/* User Card */
#user-card .card-content {
    background: rgb(25, 26, 31);
}
#user-card h1 a {
    color: #fff;
}
#user-card {
    color: #fff;
}
#user-card .location-and-website .website-name a {
    text-decoration: underline;
    color: #fff;
}
#user-card.show-badges .badge-section .user-badge, #user-card.show-badges .badge-section .more-user-badges {
    background: #22242b;
    border: 1px solid #22242b;
    color: #fff;
}

/* Profile*/
.user-main .about .secondary {
    background: #191a1f;
    border-top: 1px solid #191a1f;
    border-bottom: 1px solid #191a1f;
}
.user-main .about .secondary dd {
    color: #fff;
}
.user-main .about .details {
    background: hsla(230, 11%, 11%, 0.85);
}
.user-main .about.collapsed-info .details {
    background: rgba(25, 26, 31, 0.85);
}
.user-content {
    background-color: #22242b;
}
.badge-card .badge-contents .badge-icon {
    background-color: #191a1f;
}
.badge-card {
    background-color: #191a1f;
    box-shadow: 1px 1px 3px rgba(0,0,0,0);
}
.badge-card .badge-contents .badge-info {
    color: #fff;
}
.user-preferences .static {
    color: #fff;
}
.user-navigation .nav-stacked a.active {
    color: #fff;
}
.top-sub-section li {
    border-left: #191a1f solid 2px;
    padding: 5px 8px;
    margin: 10px 0;
}
.stats-section li.linked-stat a {
    color: #fff;
}
.user-stream .item, .user-stream .user-stream-item {
    background-color: #22242b;
    border-bottom: 1px solid #191a1f;
}
.user-stream .excerpt {
    color: #fff;
}
.user-main .about.collapsed-info .details {
    border-bottom: 1px solid #22242b;
}
.autocomplete {
    background-color: #191a1f;
    border: 1px solid #191a1f;
}
.autocomplete ul li {
    border-bottom: 1px solid #191a1f;
}
.autocomplete ul li a.selected {
    background-color: #22252b;
}
.discourse-no-touch .autocomplete ul li a:hover {
    background-color: #22252b;
    text-decoration: none;
}
.autocomplete ul li a span.username {
    color: #fff;
}

/* Messages */
div.ac-wrap {
    background-color: #22252b;
    border: 1px solid #22252b;
}
div.ac-wrap input[type="text"] {
    background-color: #22252b;
}
#private-message-users {
    color: #fff;
}
.d-editor {
    border: 1px solid #22252b;
}
blockquote {
    border-left: 5px solid #121317;
    background-color: #191a1f;
}
p>code, li>code, pre>code {
    color: #fff;
    background: #191a1f;
}
aside.quote .title {
    border-left: 5px solid #121317;
    background-color: #191a1f;
    color: #646464;
}
.d-editor .d-editor-modal {
    background-color: #191a1f;
    border: 1px solid #191a1f;
}
.emoji-picker {
	background-color: #22252b;
    border: 1px solid #22252b;
    color: #fff;
}
.emoji-picker .categories-column{
	background-color: #22252b;
    border-right: 1px solid #191a1f;
}
.emoji-picker .footer{
    border-top: 1px solid #191a1f;
}
.emoji-picker .section-header{
    border-bottom: 1px solid #191a1f;
}
.emoji-picker .filter{
    border-top: 1px solid #191a1f;
}
.emoji-picker .section-header .title{
    color: #fff;
}
.d-editor {
    border: 1px solid #22242b;
}
.d-editor-preview {
    color: #fff;
    border: 1px dashed #191a1f;
}
.timeline-container.timeline-fullscreen {
    background-color: #22242b;
    border-top: 1px solid #191a1f;
}
.topic-list .sortable:hover {
    background-color: #191a1f;
}
.directory table tr.me td {
    background-color: #191a1f;
}
.directory table tr.me td .username a, .directory table tr.me td .name, .directory table tr.me td .title, .directory table tr.me td .number, .directory table tr.me td .time-read {
    color: #ffffff;
}
.directory table td, .directory table th {
    border-bottom: 1px solid #191a1f;
}
.period-chooser #period-popup {
    border: 1px solid #191a1f;
    background: #191a1f;
}
.period-chooser #period-popup ul li a, .period-chooser #period-popup ul li a:visited {
    color: #fff;
}
.period-chooser #period-popup ul li:hover {
    background-color: #22242b;
}
.directory table th.sortable:hover {
    background-color: #191a1f;
}
#reply-control.draft {
    border-top: 1px solid #191a1f;
}
.bootbox.modal {
    background-color: #191a1f;
    border: 1px solid #191a1f;
}

/* Users */
.names span a[href*="/u/Hova"]::before {
    content: "WeMod Dark Creator " !important;
    color: #fff !important;
    font-weight: normal;
    background-color: #191a1f;
    border-radius: 4px;
    padding: 2px 3px 2px 10px;
    margin-right: 10px;
}
.names span a[href*="/u/Psyco"] {
    background-image: -webkit-linear-gradient(left, #FF2212 0%, #FFFF00 20%, #0DFF3D 30%, #14FFFF 50%, #FF1CCA 80%);
    background-image: -moz-linear-gradient(left, #FF2212 0%, #FFFF00 20%, #0DFF3D 30%, #14FFFF 50%, #FF1CCA 80%);
    background-image: -o-linear-gradient(left, #FF2212 0%, #FFFF00 20%, #0DFF3D 30%, #14FFFF 50%, #FF1CCA 80%);
    -webkit-background-clip: text;
    color: transparent !important;
    font-weight: 800;
}
.names span a[href*="/u/MrPrecise"]::before {
    content: "WeMod Dark Contributor " !important;
    color: #fff !important;
    font-weight: normal;
    background-color: #191a1f;
    border-radius: 4px;
    padding: 2px 3px 2px 10px;
    margin-right: 10px;
}
}
