css:
@-moz-document url-prefix("https://www.reddit.com/r/TaylorSwift/"), url-prefix("https://www.reddit.com/user/") {
.res-nightmode:not(.res-nightMode-coloredLinks) .thing .title {
    color: hsl(109, 97%, 42%);
}

.res-nightmode:not(.res-nightMode-coloredLinks) .thing:not(.stickied) .title:visited, .res-nightmode:not(.res-nightMode-coloredLinks) .thing.visited:not(.stickied) .title, .res-nightmode:not(.res-nightMode-coloredLinks).combined-search-page .search-result a:visited, .res-nightmode:not(.res-nightMode-coloredLinks).combined-search-page .search-result a:visited > mark {
    color: hsl(0, 88%, 41%) !important;
}

.res-nightmode .usertext-edit textarea {
    width: 633px;
    height: 450px;
}

#header {
    background: #F5F5F4 url(https://i.imgur.com/jqHqNCi.png) no-repeat scroll bottom left;
    padding: 0px;
    margin: 0px;
    clear: none;
    position: relative;
    background-position: left bottom;
    z-index: 99;
    height: 168px;
}

div#header-bottom-left {
    padding-top: 104px !important;
}

div#header-bottom-right {
    top: 144px !important;
    background-color: #333333e0 !important;
}

.res-nightmode body .content, .ProfileTemplate__body {
    background-image: url("https://i.imgur.com/v4qJfZN.jpg")!important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-size: cover !important;
}

.res-nightmode .RES-keyNav-activeElement, .res-nightmode .RES-keyNav-activeElement .md-container {
    background-color: #3737372b !important;
}

.side, .titlebox, .res-nightmode .side .md ul, .res-nightmode .side *, .res-nightmode .side {
    background-color: #0000002e !important;
}

.md {
    background-color: #161616ba !important;
}

.res-commentBoxes.res-commentBoxes-rounded .comment, .res-nightmode.res-commentBoxes .comment .comment, .res-nightmode.res-commentBoxes .comment, .res-nightmode.res-commentBoxes .comment .comment .comment, .res-nightmode.res-commentBoxes .comment .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comment .comment .comment .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comment .comment .comment .comment .comment .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comment .comment, .res-nightmode.res-commentBoxes .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comment .comment, .res-nightmode.res-commentBoxes .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comment .comment .comment .comment .comment .comment {
    background-color: #12121200 !important;
}

a.title.may-blank.loggedin {
    background-color: #00000073;
}

section.infobar.profilebetabar {
    display: none !important;
}

#header-img {
    display: none !important;
}

.tabmenu {
    margin-top: 30px;
}

.domain a {
    color: #dadada;
}

.res-nightmode .entry.res-selected, .res-nightmode .entry.res-selected .md-container {
    background-color: #37373775 !important;
}

.CommentListing__comment {
    background-color: #0000004f !important;
}

.UserTopSubredditsSidebar, .UserSpecialsListSidebar, .TrophyCaseSidebar, .res-d2x-nightmode .BlueBar__account, .res-d2x-nightmode .ProfileSidebar__titleContainer, .res-d2x-nightmode .Post, .res-d2x-nightmode .CommentListing__comment {
background-color: #0000008a; 
}

.res-d2x-nightmode .SubredditListDropdown__listItem:hover, .res-d2x-nightmode .Post__comment:hover {
background-color: #0000; 
}

.StyledHtml blockquote, .StyledHtml code, .StyledHtml pre, .StyledHtml pre code {
    background-color: #16161600 !important;
    color: #0079d3;
}

.ProfileBanner {
    background-color: #fff0;
}
}
