css:
@-moz-document url-prefix("http://starwars.wikia.com") {

/*Header Backround, cant change actual images in it*/
.wds-global-navigation {
    background-color: #323234;
    box-shadow: 0 1px 0 0 #323234;
}

/* Background Color of Page itself*/
.WikiaPage .WikiaPageBackground {
    background: #474749;
    height: 100%;
    left: 0;
    opacity: 1;
    position: absolute;
    top: 0;
    width: 100%;
}

/*Main Text*/
body {
    color: white;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 12px;
}

/* Background of Site, janked to color from origional jpeg background*/
body.background-dynamic.skin-oasis, body.skin-oasis, body.skin-oasis.background-not-tiled {
    background: #323234;
}

/* Link Colors*/
a {
    color: #c87553;
    text-decoration: none;
}

/*Page Header Colors*/
.WikiaPage .page-header__title {
    color: white;
}

/*infobox bio 1 and 2 alterating colors of cool easy reading effect*/
.portable-infobox .pi-data:nth-child(2n+1) {
    background-color: #222222;
}
.portable-infobox .pi-data:nth-child(2n) {
    background-color: #333333;
}

/*Event Infobox*/
.pi-horizontal-group-item.pi-data-value {
    background: #333333;
}

/*Page Notes Background (Page incomplete, sorry bout the mess, ect.*/
.disambig {
    background: #222222;
}

/*Contents Box*/
#catlinks, #toc, .toc {
    background: #222222;
}

