css:
@-moz-document regexp("https?://(www|images|notifications|plus|encrypted).google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2}).*")
{
/* Made by zapper_max */
    @keyframes bgcolor {
    0% {
        background-color: #ff7070
    }
	8% {
        background-color: #ffa970
    }
	16% {
        background-color: #fff770
    }
	25% {
        background-color: #c5ff70
    }
	32% {
        background-color: #77ff70
    }
	40% {
        background-color: #70ffbc
    }
	48% {
        background-color: #70ffff
    }
	56% {
        background-color: #70beff
    }
	68% {
        background-color: #7087ff
    }
	76% {
        background-color: #9370ff
    }
	84% {
        background-color: #d470ff
    }
	92% {
        background-color: #ff70fa
    }
	100% {
        background-color: #ff7070
    }
}
body {
    -webkit-animation: bgcolor 30s infinite;
    animation: bgcolor 20s infinite;
    -webkit-animation-direction: alternate;
    animation-direction: alternate;
}
img#hplogo {
    content: url(http://vignette2.wikia.nocookie.net/logopedia/images/9/9d/Google_logo_white_2015.svg/revision/latest?cb=20150901202125);
    filter: brightness(100);
}
body {
    color: white;
}
span {
    color: grey;
}
div {
    color: #c4c4c4;
}
h3 {
    color: white;
}
cite {
    color: blue;
}
}
