css:
@-moz-document domain("zimuzu.io"), domain("www.zmz2019.com") {
.menu-box a,
.top24 li a,
h2.it,
.user-release dt,
h3.f16 {
    font-weight: normal;
}




/*
	Hide Trash
*/
#help,
div.top-box div.notice,
.top-box > div:not(.header):not(.menu-box),
.app_link {
    display: none !important
}



/*
	Hide xianliao.me
*/
#xianliaome_window {
    display: none
}




/*
	NAV
*/
.top-box {
    box-shadow: 0px -2px 15px 5px rgba(0, 0, 0, 0.4);
}
.search-box {
    width: 440px;
}
.header {
    padding-top: 5px;
    border-bottom: 0;
}
.menu-box {
    height: 47px;
}
.menu-box a {
    border-radius: 2px;
    padding: 0 17px;
    height: 38px;
    line-height: 38px;
    margin: 2px 2px;
    transition: all 0.1s linear;
}
.menu-box a.cur,
.menu-box a:hover {
    border-radius: 5px;
    margin: 2px 2px;
}
.menu-box a:active {
    background: #05263a;
}




/*
	Homepage
*/
h2.it,
.top24 h2.it,
.today-play h2.it {
    height: 40px;
    line-height: 40px;
    font-size: 20px;
    border-radius: 5px 5px 0 0px;
}
.middle-box .box  {
    box-shadow: 0 3px 4px rgba(0, 0, 0, 0.3);
    border-radius: 5px;
    margin-top: 10px;
    margin-bottom: 10px;
}




/* 
	Footer
*/
.footer {
    display: none
}




/*
	pages switch
*/
.pages a {
    border-radius: 5px;
    transition: all 0.1s linear;
}
.pages a:active {
    background-color: #456171;
    color: #fff;
}



/*
	美化 box
*/
.res-view-top,
.res-imgs-box,
.res-bbs-box {
    box-shadow: 0 3px 9px #0000003d;
}
.res-imgs-box {
    margin: 13px 0 13px 0;
    border-top: 3px solid #115783;
}

.area-right > [style="margin-bottom:15px;"] {
    display: none
}

/*
	下載連結
*/
.view-res-tips {
    padding-top: 0;
}
.view-res-tips a {
    text-decoration: none;
    background-color: #2573A5;
    color: white;
    border-radius: 10px;
    padding: 3px 8px;
}
.view-res-tips a:hover {
    background-color: #1d608a;
}
.view-res-tips a:active {
    background-color: #154a6a;
}
/* 評分 */
.has-point li .fl-img .point {
    background: #faba5d;
    border-radius: 100em;
    box-shadow: 0 2px 6px #00000054;
    color: white;
}
}

@-moz-document url-prefix("http://www.zimuzu.io/user"), url-prefix("http://www.zmz2019.com/user") {
.usercenter-box {
    box-shadow: 0 3px 9px #0000003d;
    border-radius: 10px;
    background-color: #115783;
    overflow: hidden;
}
.user-menu a.cur,
.user-menu a:hover {
    border-radius: 10px 0 0 10px;
}
.user-menu a {

    border-left: 0px;
    margin-left: 4px;
}
}
