/* FRIENDSTER-LAYOUTS.COM */
/* a kitschy kind of love by shirahime shou */

body {
  background-image: url(http://i200.photobucket.com/albums/aa73/fs-layouts/fslybg/2007/07/a-kitschy-kind-of-love.jpg);
  background-position: center right;
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-color: #000000;
}
.usercontent {
  font-family: Georgia;
  color: #ff6600;
}
.usercontent a, .usercontent a:link, .usercontent a:visited, .usercontent a:hover, .usercontent a:active {
  text-decoration: none;
  color: #ff0000;
}
.usercontent a:hover {
  text-decoration: overline;
  color: #ff0000;
}
.commonbox {
  border-width: 4px;
  border-color: #ac1d03;
  border-style: solid;
  background-color: transparent;
}
.commonbox .evenrow {
  background-color: transparent;
}
.commonbox h1{ 
  font-family: Bookman Old Style Italic;
  color: #ffcc99;
  background-color: #ac1d03;
}
.commonbox h2 {
  font-family: Bookman Old Style Italic;
  color: #000000;
  background-color: #ac1d03;
}
.commonbox .viewall {
  font-family: Comic Sans MS;
  color: #ff9900;
}
.commonbox .imgblock75, .ir {
  border-width: 2px;
  border-color: #ffcc99;
