css:
@-moz-document regexp("^(https?\\:\\/\\/)?((((sso|venus|wish)\\.wis)|wis|intu|www|ntutopia|ntulearn)\\.)?(ntu\\.edu\\.sg).*") {
* {
        color: white !important;
        background-color: black !important;
    }
    h1>a,
    .logo {
        background-image: url(http://www3.ntu.edu.sg/cao3/careerfair2017/website/img/logo_ntu_new.png) !important;
        background-color: black;
        background-size: contain !important;
    }
    p>a,
    #navlist>li>a,
    p.right {
        color: white !important;
    }
    h1.pagetitle>a,
    li.active,
    li.preActive,
    li.preactive,
    h2.greyarrow,
    .side-links>ul>li>a,
    .lastcrumbs,
    .sitetitle>a,
    h2,
    #crumbs>ul>li {
        background-image: none !important;
    }
    li.active>a {
        border-bottom-style: solid !important;
        border-width: 1px !important;
        border-color: red !important;
    }
    .nprev {
        background-image: url(http://cdn.mysitemyway.com/etc-mysitemyway/icons/legacy-previews/icons/blue-jelly-icons-arrows/007358-blue-jelly-icon-arrows-arrow-thick-left.png) !important;
    }
    .nnext {
        background-image: url(http://cdn.mysitemyway.com/etc-mysitemyway/icons/legacy-previews/icons/blue-jelly-icons-arrows/007359-blue-jelly-icon-arrows-arrow-thick-right.png) !important;
    }
    .banner-up>span {
        background-image: url(http://cdn.mysitemyway.com/etc-mysitemyway/icons/legacy-previews/icons/blue-jelly-icons-arrows/007360-blue-jelly-icon-arrows-arrow-thick-up.png) !important;
    }
    .banner-down>span {
        background-image: url(http://cdn.mysitemyway.com/etc-mysitemyway/icons/legacy-previews/icons/blue-jelly-icons-arrows/007357-blue-jelly-icon-arrows-arrow-thick-down.png) !important;
    }
    .nnext,
    .nprev,
    .banner-up>span,
    .banner-down>span {
        background-size: contain !important;
        transition: 1s !important;
        background-position: center !important;
    }
    a.nivo-imageLink,
    .slider-dir-prev,
    .slider-dir-next,
    .nivo-prevNav,
    .nivo-nextNav {
        background-color: initial !important;
    }
    .side-links>ul>li>img {
        visibility: hidden
    }
    h2.greyarrow {
        color: white !important;
    }
    tr,
    td {
        border-color: gray !important;
        border-style: ridge !important;
    }
    
    
    #pageTitleDiv {
        background: gray !important;
    }
    #appTabList>tbody>tr,
    #appTabList>tbody>tr>td,
    #appTabList>tbody>tr>td>a,
    #appTabList>tbody>tr>td>a>span,
    .breadcrumbs.clearfix {
        background: black !important;
        border: 0px !important;
    }
    .contentBox,
    #pageTitleText>span,
    #pageTitleText,
    #pageTitleHeader,
    .pageTitleIcon {
        color: #eaeaea !important;
        background: gray !important;
        text-shadow: 0px 0px 0px black !important;
        border: 0px !important;
    }
    h1, h2, #news .title {
    color: white !important;
    }
}
