css:
@-moz-document domain("www.computercraft.info") {
html, body {
    background: #666!important;
    background-color: #666!important; 
}

#content, .ipsBox, .ipsBox_container, .post_block {
	background: #666!important;
    background-color: #666!important;
}

.ipsBox_container {
    border: #666!important;
}

table.ipb_table td {
    border-color: #555!important;
}

#branding, .row2, .post_block.row {
    background: #555!important;
    background-color: #555!important;
    border-color: #555!important;
}

p.citation {
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#a1a1a1),color-stop(100%,#909090));
    border-top: #909090;
    border-right: #909090;
}

div.blockquote {
    background: #a2a2a2;
    border-bottom: #909090;
    border-right: #909090;
}

.ipsButton_secondary {
    background: #a1a1a1!important;
    background-color: #a1a1a1!important;
    border-color: #868686!important;
    border: 1px solid #868686!important;
    box-shadow: 0 1px 0 #aaa inset, 0 1px 0 rgba(0,0,0,.3)
}

.cke_skin_ips .cke_wrapper {
    border: #9099a0!important;
    background-color: #8f969d!important
}

.cke_skin_ips textarea.cke_source {
    background-color: #a2a2a2
}

.cke_skin_ips .cke_contents iframe {
    background-color: #a1a1a1!important
}

.cke_top {
    background: #7f868d!important
}

.post_block {
    border-color: #666!important
}

.signature {
	border-top: 1px solid #555
}

.maintitle {
    background: #555!important;
    background-color: #555!important;
    border-color: #565656!important;
	-webkit-box-shadow: inset 0 1px 0 #606060!important
}

.statistics {
    border-top: 1px solid #555
}

#search_wrap, #primary_nav .active a {
    background: #a1a1a1!important;
    background-color: #a1a1a1!important;
}

.maintitle a {
    color: #a1a1a1!important;
}

#page {
	background: #666;
}

.one-column #nav-below, .hentry, .no-results {
    border-bottom: 1px solid #555
}

#site-generator {
    background: #777;
    border-top: 1px solid #555;
}

#mw-page-base {
    background-image: linear-gradient(#555 50%,#666 100%)
}

div#mw-panel div.portal {
    background-image: url(data:)!important;
}

.mw-body {
    border: 1px solid #545454;
}

table.wikitable {
	background-color: #aaa;
}

table.wikitable>tr>th, table.wikitable>*>tr>th {
    background-color: #999;
}

.catlinks {
	background-color: #aaa;
}

pre, .mw-code, code {
    background-color: #bbb;
    border: 1px solid #999;
}

div#simpleSearch {
    background-color: #aaa;
    background-image: url(data:);
}

#toc, .toc, .mw-warning {
    background-color: #aaa;
}

div.thumbinner {
    background-color: #777;
    border: 1px solid #888;
}

.commentlist>li.comment {
    background: #777;
    border: 1px solid #888;
}
.commentlist .children li.comment{
    background: #7a7a7a;
    border-left: 1px solid #888;
}
.commentlist>li:before {
    content: url(data:);
}

.row1, .post_block.row1 {
    background: #7a7a7a!important;
    background-color: #7a7a7a!important;
}

.header {
    background: #444!important;
    background-color: #444!important;
}

.general_box, .general_box h3 {
    background: #666!important;
    background-color: #666!important;
}

.ipsVerticalTabbed_tabs li {
    background: #666!important;
    background-color: #666!important;
    border-color: #555!important;
}

.ipsVerticalTabbed_tabs>ul {
    border-top: 1px solid #555!important;
    border-left: 1px solid #555!important;
}

.ipsVerticalTabbed_tabs li.active a {
    background: #888!important;
    background-color: #888!important;
    border-color: #888!important;
}

.ipsVerticalTabbed_tabs li a:hover {
    background: #777!important;
    background-color: #777!important;
}

#board_stats .value {
    background: #aaa!important;
}

.popupInner {
    background: #aaa!important;
}

.userpopup dl {
    border-bottom: 1px solid #555;
}

.popupInner h3 {
    background: #555 url(data:) repeat-x top;
    border-color: #555;
    box-shadow: inset 0 1px 0 #555;
}

#primary_nav, #primary_nav a {
    background: #444!important;
    background-color: #444!important;
}

.ipsSideMenu ul li.active a {
    background: #555!important;
    background-color: #555!important;
}

.ipsSideMenu ul li {
    border-bottom: #888!important;
}

.ipsSideMenu ul {
    border-top: #888!important;
}

body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
    background: #666!important;
    background-color: #666!important;
}

input, select {
    background-color: #a1a1a1!important;
}

.input_text, .ipsTagBox_wrapper {
    border-color: #bbb #aaa #bbb #aaa;
    background-color: #aaa;
}

.ipsHeaderMenu {
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#aaa),color-stop(70%,#a0a0a0),color-stop(100%,#767676));
}

#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
    background-color: #aaa;
}
.ipsType_sectiontitle {
    border-bottom: 1px solid #888;
}

#copyright, #user_link_menucontent #links li, .ipsTag {
    text-shadow: 0 1px 0 #aaa
}

.ipsTagBox_dropdown {
	background: #aaa;
    border: 1px solid #888;
}

.ipsSettings_section {
    border-top: 1px solid #888;
}

.cke_skin_ips .cke_dialog_footer, .cke_skin_ips .cke_dialog_contents {
    background-color: #aaa;
}

.no_messages {
    background: #aaa!important;
    background-color: #aaa!important;
}

.progress_bar {
    background-color: #aaa;
    border: 1px solid #888;
}

.progress_bar span {
	background: #777 url(http://www.computercraft.info/forums2/public/style_images/ComputerCraft/gradient_bg.png) repeat-x left 50%
}

.ipsType_pagedesc, .breadcrumb a, .breadcrumb {
    color: #aaa;
}

.desc.lighter, .desc.lighter.blend_links a {
    color: #a1a1a1;
}

.ipsSideBlock h3 {
    background: #555!important;
    background-color: #555!important;
}

.ipsSideBlock {
    background: #666!important;
    background-color: #666!important;
}

#folder_list .total {
    background-color: #777;
}

ul.mini_pagination li a {
    background: #aaa;
    border: 1px solid #888;
}

.pagination .back a, .pagination .forward a {
    background: #aaa!important;
    background-color: #aaa!important;
}

.pagination .back a:hover, .pagination .forward a:hover {
    background: #888!important;
    background-color: #888!important;
}

.status_update {
    background: #888!important;
    background-color: #888 !important;
}

pre.prettyprint {
    background: #aaa;
    border: 1px solid #888;
}

.bar {
    background: #777!important;
    background-color: #777!important;
}

.unread .altrow, .unread.altrow, .unread {
    background: #777!important;
    background-color: #777!important;
}

div.vectorTabs ul li {
    background-image: url(data:);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#888),color-stop(70%,#777),color-stop(100%,#666));
}

div.vectorTabs li.selected {
    background-image: url(data:);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#999),color-stop(70%,#888),color-stop(100%,#666));
}

div.vectorTabs, div.vectorTabs span {
    background-image: url(data:);
}

#pagehistory li {
    border: 1px solid #888;
}

#pagehistory li.selected {
    background: #888;
}

.cke_skin_ips .cke_separator {
    border-left: 1px solid #888;
}

.poll_question ol {
    background-color: #888;
}

.poll_question h4 {
    background-color: #777;
}
}
