css:
@-moz-document domain("www.humblebundle.com") {
/* =========== HOME =========== */

/* Game bloc */
/* Title part */
.full-tile-view .tile-info.store .info-section.primary {
    background-color: #3b3e48;
    color: rgba(255, 255, 255, 0.8);
}
/* Price part */
.full-tile-view .tile-info.store .info-section.secondary {
    background-color: #282c34;
    color: #dadada;
}

/* =========== STORE =========== */

/* Background */
.storefront.v2 .page-wrap {
    background-color: #333333;
}
/* Shading with the image */
.storefront.v2 .page .background-container:before {
    background-image: linear-gradient(to bottom, rgba(228, 231, 237, 0) 40%, #333333 95%);
}
/* Category titles */
.page-section .header-text, .sidebar-top-section .header-text {
    color: #dadada;
}
/* Top blocs background "Featured" and "Featured Sales" */
.storefront.v2 .page .entity-list.carousel-large .carousel .entity-block-container .entity-purchase-details, .storefront.v2 .page .entity-list.carousel-mini .carousel .entity-block-container .entity-purchase-details {
    background-color: #252525;
}
/* Top blocs shadow "Featured" and "Featured Sales" */
.storefront.v2 .page .entity-list.carousel-large .carousel .entities-list, .storefront.v2 .page .entity-list.carousel-mini .carousel .entities-list {
    box-shadow: black 0 1px 5px;
}
/* Top bloc "Featured" text */
.storefront.v2 .entity-block-container .entity-title {
    color: #dadada;
}
/* Top bloc "Featured Sales" text */
.storefront.v2 .page .entity-list.carousel-mini .carousel .entity-block-container {
    color: #dadada;
}
/* Other categories (Publisher Sales, Top Indie Deals...) title background */
.storefront.v2 .entity-block-container .entity-meta {
    background-color: #252525;
}
/* Categories blocs, price part background, except "Publisher Sales" */
.storefront.v2 .page .entity-list .entity-block-container .entity-purchase-details {
    background-color: #1b1b1b;
}
/* Categories blocs, price part border top, except "Publisher Sales" */
.storefront.v2 .entity-block-container .entity-purchase-details {
    border-top: 1px solid #0e0e0e;
}
/* Categories blocs, Platforms icons */
.storefront.v2 .entity-block-container .entity-devices {
    color: rgba(255, 255, 255, 0.5);
}
/* Category "Publisher Sales", "View Games" part */
.storefront.v2 .entity-block-container .page-view-details, .storefront.v2 .entity-container .page-view-details {
    background-color: #1b1b1b;
    border-top: 1px solid #0e0e0e;
    color: #adadad;
}
/* Categories bloc shadow */
.storefront.v2 .page .entity-list.carousel-standard .carousel .entity-block-container, .storefront.v2 .page .entity-list .grid .entity-block-container {
    box-shadow: black 0 1px 5px;
}
/* Right bloc (Popular, New, Pre-order) */
/* Games blocs */
.storefront.v2 .page .entity-list.tabbed-list .entity-container {
    background-color: #252525;
    color: #dadada;
    border-bottom: 1px solid #000;
}
/* Platforms icons */
.storefront.v2 .page .entity-list.tabbed-list .entity-container .operating-system, .storefront.v2 .page .entity-list.tabbed-list .entity-container .platform {
    color: rgba(255, 255, 255, 0.5);
}
/* Popular, New, Pre-order text */
.storefront.v2 .page .entity-list.tabbed-list .list-tab {
    color: #dadada;
}
/* Price (not on sale) */
.storefront.v2 .entity-block-container .entity-pricing, .storefront.v2 .entity-container .entity-pricing {
    color: #adadad;
}
/* Price (not on sale) border */
.storefront.v2 .entity-block-container .entity-pricing .price-button, .storefront.v2 .entity-container .entity-pricing .price-button {
    color: #adadad;
}
/* "View all" part background */
.storefront.v2 .page .entity-list.tabbed-list .entity-container.view-all .entity-link {
    background-color: #1b1b1b;
}
/* Bloc shadow */
.storefront.v2 .page .entity-list.tabbed-list .list-content {
    box-shadow: black 0 1px 5px;
}


/* =========== GAME =========== */

/* Recommendation, game bloc, price part background */
.storefront.v2 .entity-block-container .entity-purchase-details {
    background-color: #1b1b1b;
}
/* Description */
.storefront.v2 .product-details-page .light-row {
    background-color: #2e3545;
    color: #dadada;
}
/* Description shading */
.storefront.v2 .product-details-page .light-row .property-view.expandable .property-content::after {
    background: linear-gradient(to top, #2e3545, rgba(228, 231, 237, 0));
}


/* =========== BUNDLES =========== */

/* Description background */
.bundle-info-container {
    background-color: #31343d;
}
/* Description text */
.bundle-info {
    color: #dadada;
}
/* Description shading*/
.bundle-info-text::after {
    background: linear-gradient(to bottom, rgba(228,231,237,0) 0%, #31343d 100%);
}
/* Description "Read more" button */
.bundle-info-text-expand-wrapper {
    color: #adadad;
}
/* Numbers and Top Contributors background*/
.st-container {
    background-color: #3b3e48;
    color: #dadada;
}
/* Purchase background */
.order-form-container {
    background-color: #31343d;
}
/* Purchase text */
.order-form {
    color: #dadada;
}
/* Packs background */
.order-form-amount-selection-step {
    background: #31343d;
}
/* Pack bloc background */
.order-form-image-fan-box {
    background-color: #25272d;
}
/* Price in local currency */
.order-form-image-fan-box-header .subheader {
    color: #adadad;
}
/* "Choose where your money goes" background */
.order-form-email-step {
    background: #31343d;
}
/* "More Payment Options" Button */
.order-form-simple-payment-button, .order-form-image-payment-button {
    color: #adadad;
    box-shadow: inset 0px 0px 0px 1px #adadad;
}
/* "More Payment Options" Button HOVER */
.order-form-simple-payment-button:hover, .order-form-image-payment-button:hover {
    color: #dadada;
    box-shadow: inset 0px 0px 0px 1px #dadada;
}
/* Payments details */
.order-form-payments-message {
    color: #adadad;
}
/* Payments details button */
.order-form-text-expand-wrapper {
    color: #adadad;
}
/* Order summary */
.order-summary-wrapper {
 	background: #3b3e48;   
}
/* Price line, text */
.order-summary-item-name {
    color: #dadada;
}
/* Text "Pay What You Want" */
.order-form-amount-selection-label {
    color: #adadad;
}
/* Total price */
.order-summary-subtotal {
 	color: #dadada;
}
/* Bundle is gift block */
.order-form .order-form-email-step {
    background: #3b3e48; 
}

/* =========== LOGIN =========== */

/* Background */
.generic-popup.content {
        background-color: #282c34;
}
/* Bottom part background*/
.account-switch {
    background: #1d2127;
}
/* Text "Log In" */
.generic-popup.content h1 {
    text-shadow: 0 0 1px #000;
    color: #dadada;
}
/* Input text */
.generic-popup input[type='text'], .generic-popup input[type='password'], .generic-popup input[type='email'] {
    color: #dadada;
    box-shadow: 0 1px 2px #000 inset;
}
/* Links "Forgot your password?", "Two-step verification" */
.link-holder a, .generic-popup .small-link {
    color: #adadad;
}
/* Link "Need to create an account?" */
.account-switch .account-creation {
    color: #dadada;
}
/* Bottom part, border top */
.account-switch {
    border-top: 1px solid #14161b;
}


/* =========== Create Account =========== */

/* Text */
.generic-popup.content h2 {
    color: #adadad;
    text-shadow: 0 0 1px #000;
}
/* Links "Terms and Conditions" and "Privacy Policy" */
.generic-popup a {
    color: #adadad;
}

/* =========== Order Complete =========== */

/* Background */
.base-main-wrapper .inner-main-wrapper {
    background: #31343d;
}
/* Text */
html, body {
    color: #dadada;
}
/* Horizontal band below the menu */
.page-wrap {
    background: #31343d;
}
/* Horizontal band at the bottom of the page */
.responsive-page-wrap {
    background: #31343d;
}

/* =========== Downloads =========== */

/* Main block */
#papers-content {
    background-color: #3b3e48;
}
/* Text */
#main {
    color: #adadad;
}
/* Informations and download blocks */
.whitebox-redux {
 	background: #31343d;
    border: 1px solid #333;
    box-shadow: 0 1px 1px 0px #232323;
}
/* Buttons */
.flexbtn .a {
 	color: #dadada !important; 
}
/* Buttons text shadow */
.flexbtn.active .a {
    text-shadow: none;
}
/* Sizes of files */
.whitebox-redux {
 	color: #dadada;
}
/* Editor */
.subtitle a {
    color: #adadad !important; 
}



/* https://www.humblebundle.com/user/signup-complete?email=azerty%40affd.com */


}
