css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("headlines.yahoo.co.jp") {
/* delete ads */
 div#pos-tn,
 div.adCt,
 div#pos-sqb
   {
  display: none !important;
   }

/* delete header */
 div#uhd
   {
  display: none !important;
   }

/* delete sidebar */
 div#sub
   {
  display: none !important;
   }

/* delete footer */
 div#contents-footer,
 div#footer
   {
  display: none !important;
   }

/* custom design */
 div#masthead
   {
  margin : 10px auto !important;
   }

 div#main-wrapper,
 div#main,
 div#wrapper
   {
  margin    : 0px auto !important;
  padding   : 0px      !important;
  max-width : 800px    !important;
  min-width : 600px    !important;
  float     : none     !important;
   }

}
