css:
@-moz-document domain("app.sococo.com") {
.media-buttons svg {
    width: 15px;
    margin-right: 0;
    padding-right: 0;
}

.map-zone {
    background: rgba(0, 255, 0, 0.6);
    border: solid 1px rgba(0, 255, 0, 1);
    border-radius: 2px;
}

.map-zone-door {
    width: 100% !important;
    left: 0 !important;
    right: 0 !important;
    height: 0 !important;
    border: solid 2px red;
}

#map-image {
    display: none !important;
}

sc-map {
    /*
    background: url('https://i.makeagif.com/media/10-24-2015/jOiyeV.gif');
    background-size: cover;
    */
    
    background-image: url('https://orig00.deviantart.net/c6db/f/2015/110/0/9/matrix_animated_image_2_1__by_shabbygirlblog-d8qhr9j.gif');
    background-size: 30% auto;
}

.map .map-zone-label {
    display: block !important;
    font-family: sans-serif !important;
    font-size: 8pt !important;
    line-height: 1 !important;
    overflow: visible !important;
    overflow-wrap: break-word !important;
    word-break: normal !important;
    height: auto !important;
    white-space: normal !important;
    top: 10px !important;
    z-index: 0;
    opacity: 1 !important;
    text-align: center;
    color: white !important;
}

.map sc-avatar {
    margin-top: 10px;
    background: white;
    border-radius: 10px;
    z-index: 10;
}

.map .avatar-container .initials, .map .avatar-container .name {
    font-family: sans-serif !important;
    bottom: -8px !important;
    padding: 0 2px 1px 2px;
    background: white;
    border-radius: 3px;
    font-size: 6pt !important;
    line-height: 1.5 !important;
}

.map .avatar-container md-icon {
    z-index: 150;
}

.map .avatar-container img {
    z-index: 100;
}
}
