css:
@-moz-document regexp('^https://duckduckgo.com.*'){
	html body.body--home{
		background-image: url(https://cdn.discordapp.com/attachments/380448625706401794/385501833524805632/b-and-a2-nier-automata-artwork-2u.jpg) !important;
		background-repeat:no-repeat !important;
		background-position: center top !important;
		background-size: cover !important;
		background-attachment: fixed !important;
	}
	.js-side-menu-twitter.header--aside__twitter, .tag-home__item, .tag-home__wrapper, .js-tag-home.no-js__hide.tag-home--slide.tag-home, #footer_homepage{
		display :none;
	}
}
@-moz-document regexp('^https://www.google.(be|fr|com).*'){
	html{
    	overflow-y: auto;
	}
	body {
    	background-image: url(https://cdn.discordapp.com/attachments/380448625706401794/385501833524805632/b-and-a2-nier-automata-artwork-2u.jpg) !important;
    	background-repeat:no-repeat !important;
    	background-position: center top !important;
    	background-size: cover !important;
    	background-attachment: fixed !important;
	}
	html,body{
    	height: 100% !important;
	}
	#s_foot_wrap,#fbar,#s_main{
		background-color: rgba(255,255,255,0) !important;
	}
	#prm{
		margin-top:210px;
	}
	#fbar{
    	margin: 0px !important;
    	padding-top: 0px !important;
    	padding-bottom: 0px !important;
    	border-top: 0px  !important;
    	line-height: 35px !important;
	}
	._Ubu, .gb_ug.gb_Dg.gb_R.gb_ke, .fbar, #_eEe{
		display :none;
	}
}
