/* Ririchiyo Google Style by MJayK */
/* Good with DarkSearch for Google Style */

/* https://i.imgur.com/iO7mck0.png */
/* https://i.imgur.com/20PSubV.png */
/* https://i.imgur.com/EYSo5vR.png */
@-moz-document domain(google.com) {
/*
Domains: (URLs on the domain
	google.com
	google.uk
	google.pl
	google.de
	google.it
	google.ca
*/


 /*Google podczas wyszukiwania*/
   /*Google the search page*/
	/*-------*//*-------*/
  #logocont
  {
    background: url("https://i.imgur.com/iO7mck0.png")no-repeat;
    height:200px;
    width:140px; 
    text-indent: -9000px;
  }
	/*-------*//*-------*/



     /*Głowna strona google*/
      /*Google main page*/
	/*-------*//*-------*/
  img#hplogo 
  {
    box-sizing: border-box;
    background: url("https://i.imgur.com/EYSo5vR.png") center no-repeat !important;
    padding-left: 521px;
    bottom: 55px;
    position: relative;
    padding-bottom: 335px;
    margin: auto;
  }
  div#hplogo
  {
    box-sizing: border-box;
    background: url("https://i.imgur.com/EYSo5vR.png") center no-repeat !important;
    padding-left: 521px;
    bottom: 155px;
    z-index: -999;
    position: relative;
    padding-bottom: 335px;
    margin: auto;
  }
  #hplogo 
  {
    text-indent: -9000px !important;
    white-space: nowrap;
  }
  #logo img
  {
    background: url("https://i.imgur.com/EYSo5vR.png") 17% 13% no-repeat !important;
  }
	/*-------*//*-------*/



       /*Google Tłumacz*/
      /*Google Translator*/
	/*-------*//*-------*/
  #gt-src-wrap 
  {
    background: url("https://i.imgur.com/20PSubV.png") 30% 30% no-repeat;
    height:241px;
    background-attachment: fixed;
  }
  #gt-res-dir-ctr
  {
    height:200px;
    background-attachment: fixed;
  }
	/*-------*//*-------*/



         /*Pozostałe*/
          /*Others*/
	/*-------*//*-------*/
  #gt-r
  textarea
  {
    color: #fff;
  }
  .mw
  {
    background: url("https://i.imgur.com/EYSo5vR.png") 80% 100% no-repeat;
    background-attachment: fixed;
  }
  .sbibod, .jhp input[type="submit"]
  {
    opacity: .7;
  }
	/*-------*//*-------*/
}
