css:
@-moz-document domain(gab.ai) {
/*
===================================================
Neon Frogs - A Gab.ai theme by Dr. Kekelston (@DrKekelston).

"It's not easy being green."
===================================================

COLORS
#fed74c 	orange
#fd3945 	red
#24329d 	blue
#e63fc0 	violet
#ff2fff 	pink
#33AA3388 
===================================================
*/

/* PAINT THE BACKGROUND NEON */
body
{
	background: linear-gradient(
		to bottom, 
		#fed74c, 
		#fd3945, 
		#24329d, 
		#e63fc0);
	background-attachment: fixed;
}
.main-login .header 
{
	box-shadow: 0px 0px 30px 10px #000000;
	background: black;
	border-bottom: none;
	padding-bottom: 10px;
}
.post-modal .post-modal__content,
div.profile__bio.profile__box,
div.profile__bar.clearfix,
div.composer-modal__content,
.notification-filter a,
a.post-list__load-more
{
	background: black !important;
	border: 1px solid #ff1fff !important;
}

/* BLACK UI ELEMENTS */
div.composer__content__attachment
{
	background: black !important;
}

/* PINK INTERACTIVE ELEMENTS */
a,
h2,
h3,
.gab .gab__meta .gab__meta__author__full-name,
.gab.gab--big .gab__reason 
{
	color: #ff2fff !important;
	font-weight: 800 !important;
}
label,
a.composer__content__attachment__option,
.main-login .header .header__link.header__link--compose,
a.composer__content__button.composer__content__button--send,
.gab__meta__author__badge
{
	background: #ff2fff !important;
	color: white !important;
}
.gab .gab__quoted-post,
a.post-attachment-url
{
	border: 1px solid #ff2fff !important;
	background: inherit;
}
.main-login .header .header__link
{
	font-size: 15px;
}

/* FONT DEFAULTS */
span,
div,
p,
font
{
	color: white !important;
	font-weight: 600 !important;
}

/* LAYOUT */
div.notification-list__item.notification-list__item--like,
div.notification-list__item.notification-list__item--follow,
div.notification-list__item.notification-list__item--mention,
div.notification-list__item.notification-list__item--repost
{
	max-width: initial !important;
}
.main-login .header-wrapper,
div.profile-wrapper
{
	max-width: initial !important;
}
div.header
{
	height: 70px !important;
}
div.content 
{
	padding-top: 20px;
}
.main-login .wrapper 
{
	max-width: 98%;
}
div.content__right 
{
	padding-left: 50px !important;
	padding-right: 50px !important;
}
.gab--big 
{
	margin-bottom: 30px !important;
}

/* BLACK BOXES WITH NEON GLOW */
.gab--big,
.content__left__box--categories,
div.home__right.content__right__box,
div.topic__right.content__right__box,
div.notification-list__item__message,
div.content__left__box.content__left__box--links,
div.content__left__box.content__left__box--topics
{
	box-shadow: 0 0px 30px 20px #ff88ff !important;
	background: black !important;
	margin-top: 40px !important;
	border: 1px solid #ff2fff !important;
	opacity: 0.7;
}
div.profile__box.profile__box--posts
{
	overflow: initial !important;
}

/* REMOVE BORDERS AND UNDERLINES */
.gab.gab--big .gab__reason,
.mini-user-list__item
{
	border-bottom: 0px !important;
}
.gab .gab__actions 
{
	background: black !important;
}
.main-login .content__left__box a 
{
	background: black !important;
	padding: 10px 20px 2px;
	margin-bottom: 0px !important;
	border: 0px;
}
.main-login .content__left__box h3 
{
	padding: 10px 20px;
}

/* SELECTION HIGHLIGHT */
.main-login .content__left__box a.content__left__box__active, 
.main-login .content__left__box a:hover,
a.post-attachment-url:hover,
div.mini-user-list__item:hover
{
	background: #ff2fff !important;
	border: none !important;
	color: black !important;
}

/* COMPOSER */
div.composer__content,
div#composer.composer,
textarea
{
	background: black !important;
	border: none !important;
}
}
