css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kanji.koohii.com") {
  
  body {
    background-color: #282828;
    color: #aaa;
  }
  
  body.with-footer, body.with-footer #main {
    background-color: #282828;
  }
  
  /**** Home Page ****/
  
  /** Top dropdown **/
  #k-nav_d .k-nav_dropdown {
    background-color: #222;
    border-left-color: #333;
    border-right-color: #333;
  }
  
  #k-nav_d .k-nav_dropdown li {
    border-top-color: #333;
  }
  
  #k-nav_d .active a, #k-nav_d .k-nav_dropdown a {
    color: #aaa;
  }
  
  #k-nav_d .active, #k-nav_d .active a:hover {
    background-color: #333;
  }
  
  #k-nav_d .k-nav_dropdown a:hover {
    background-color: #333;
    color: #aaa;
  }
  
  /** Boxes e.g. 'No restudy kanji', 'No expired kanji' **/
  .padded-box-inset {
    background-color: #222;
  }
  
  #home-qs .hd {
    color: #aaa;
  }
  
  /* News */
  #sitenews .bd {
    color: #aaa;
  }
  
  #sitenews h2 {
    border-bottom-color: #666;
  }
  
  /* Footer */
  #footer .transition {
    background-color: #eee;
    filter: invert(90%);
  }
  
  .k_bg_free {
    background: linear-gradient(to bottom,#282828 0,#222 100%);
  }
  
  /**** Study > Browse ****/
  
  #browse {
    background-color: #222;
  }
  
  .form-control {
    background-color: #333;
    color: #aaa;
    border-color: #666;
  }
  
  .study-search_btn {
    background-color: #282828;
  }
  
  .study-search_btn:hover {
    background-color: #333;
  }
  
  /**** Study > Browse > Kanji Page e.g. kanji.koohii.com/study/kanji/2 ****/
  
  #my-story {
    background-color: #282828;
    border-color: #333;
  }
  
  .rtkframe #sv-textarea {
    border-color: #333;
  }
  
  .rtkframe #storyedit textarea {
    background-color: #282828;
    border-color: #333;
  }
  
  .rtkframe #sv-textarea.hover {
    background-color: #333;
    border-color: #333;
  }
  
  #my-story .bottom {
    background: #282828;
  }
  
  /* Dictionary */
  #DictHead {
    background: linear-gradient(to bottom,#222 0,#282828 100%);
    text-shadow: 0 1px rgba(40,40,40,0.75);
  }
  
  .dict-list {
    background: #282828;
  }
  
  .dict-list dt {
    border-top-color: #333;
  }
  
  .dict-list dt .c {
    background: #333;
    color: #aaa;
  }
  
  .dict-list dt .r {
    color: #ccc;
  }
  
  .dict-list dd {
    color: #999;
  }
  
  /* Favourite(s) */
  #sharedstories-top .title {
    filter: invert(90%);
  }
  
  /* New & updated stories */
  #sharedstories-new .title {
    filter: invert(90%);
  }
  
  .rtkframe {
    background-color: #222;
  }
  
  .rtkframe .bookstyle {
    color: #aaa;
  }
  
  .sharedstory.rtkframe {
    border-bottom-color: #333;
  }
  
  /* Shared stories */
  #sharedstories-fav .title {
    filter: invert(100%);
  }
  
  #SharedStoriesListComponent .uiPagerDiv {
    background-color: #222;
  }

}
