@-moz-document regexp("https?://plus.google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2}).*") {
/* ==UserStyle==
@name Amazing Google+
@namespace github.com/openstyles/stylus
@version 1.0.0
@description A dark and improve version of Google+
@author Random guy
@link: https://userstyles.org/styles/152077/
==/UserStyle== */


:root {
  --bgn: #272c35; /* dark color */
  --bgd: #1F232A; /* darker color */
  --bgh: #3C4149; /* light color */
  --bgt: #B6B6B6; /* faded text color */
  --bgo: #E9E9E9; /* highlighted text color */
  --bgu: #979797; /* under text color */
  --bga: #7289DA; /* accent color */
  --bgc: #f13f3f /* red-ish text */
}




}

@-moz-document regexp("https?://plus.google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2}).*") {
/*-----------------Icons---------------*/
.f81Q3b.u3bW4e.Or9bCd .yuvn1 {
	color: rgba(255, 255, 255, .50) !important;
}
.VD1ohe, .EPjpnf {
	filter: invert(50%) !important;
}
.gb_da .gb_b {
	background-position: 0 -2180px !important;
	opacity: 1 !important;
}
.gb_Nc.gb_Oc {
    background-position: 0 -600px !important;
	opacity: 1 !important;
}
.KSpDGb, .xdjHt.FYQzvb, .xdjHt.FYQzvb.ex5ZEb, .xdjHt {
	background-image: url(https://ssl.gstatic.com/images/branding/lockups/2x/lockup_gplus_light_color_88x24dp.png);
	opacity: .7 !important;
}
/*----------------Text Colors---------------*/
.Ab28Nd, .Dm8wYc, .FHAiOd, .FQ6U7b .RH4Xh, .Gu1oT, .M9kDrd, .MWHCG, .MocG8c, .Tuxepf, .WMav5e, .X865Z, .g6UaYd, .iLGfrb, .jHTpsd, .jsery, .l7Q68c .wftCae, .m3JvWd, .mivp1b, .vGowKb, .whsOnd, .xZ4jvc, .z80M1, .zicebf, body, .lAVJU, .uBkdse, .zlYNGe:hover, .awJPSb, .O0WRkf.RDPZE, .cDCnlb .kaAt2.KKjvXb, .cDCnlb .kaAt2:hover, .ByAcB .wftCae, .LbgX9d, .U40TGd .snByac, .gb_Ab, .gb_Xb, .jCwybd, .Y1C4l {
	color: var(--bgo) !important;
	fill: var(--bgo) !important;
}
.BCNiN.u3bW4e .Yylo3e path, .BCNiN:hover .Yylo3e path, .CuPm0 .Xa1Zuc, .EKledf, .M9kDrd .DCYkOe, .SY5ZZb, .XVzU0b.J3yWx circle, .XVzU0b.J3yWx path, .aNdhqf, .ahil4d.qhIQqf, .c8b3Te, .f6umD, .fKz7Od, .gmFStc, .ikPEid, .jdiJDd, .qXj2He, .vWOjp, .wQqyXe .U8PEid, .xRbTYb, textarea, .yNVtPc, .s49ete .Ww5CL, .M9kDrd.rTaXUb .DCYkOe, .f81Q3b.Or9bCd .yuvn1, .Pjg9Lb, .IYZ3vd path, .CuPm0 .Wbuh5e, .W7E6D, .O0WRkf.RDPZE:hover, .XVzU0b path, .Ww5CL, .ELUvyf, .sXku1c, .lmwwcb, .XS1fT path, .VOEIyf .ZAGvjd, .gb_Ib, .o614gf, .s1VJnb, .KA9vud.KKjvXb {
	color: var(--bgo) !important;
	fill: var(--bgo) !important;
	opacity: .7 !important;
}
.AlZIPe, .G7pzvd path, .cjGgHb .GcESAf:hover .QInnmc, .cjGgHb .GcESAf:hover .mFYoKe, .dzuq1e .mFYoKe, .dzuq1e.iCPjVb .qXj2He, .ehbzjb .KieDTe, .ehbzjb.J3yWx .KieDTe, .iOAN1d, .ndJi5d, .pgYAwf .M9kDrd, .yB7QDc path, .yj03vf, .u6tzLe, .cDCnlb .kaAt2, .eYSPjc, .SVDMFb, .N1L8yd, .gb_Cb, .gb_Zb {
	color: var(--bgo) !important;
	fill: var(--bgo) !important;
	opacity: .5 !important;
}
.i2jSQ, .EGToT, .WiMrBe, .fcl2Fe, .vH39A .KNnOOe.xRXSA.abKBZe, .U40TGd .snByac:hover, .N9wOvf:hover, .vdgHBe.KKjvXb, .gb_tb, #gb a.gb_tb.gb_tb, .gb_ub a, #gb .gb_ub.gb_ub a, .mfqKsb, .V4dHue, .RzoEI, a, .KA9vud {
	color: var(--bga) !important;
}
.QInnmc {
	color: transparent !important;
}


}

@-moz-document regexp("https?://plus.google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2}).*") {
/*-----------------Background-----------------*/
.GsLz7c, .M7vp2c, .O1bNWe, .bYmF4e, body, .xqrdOe, .xr0heb, .f0Eb9c, .zUk1Zc, .MocG8c, .OnM0ef, .dI13We, .CIwCoe, .UPYtIb, .RIrM6d, .BCNiN, .ym0cb, .vWH6tc, .SVDMFb, .Jvazdb, html {
	background: var(--bgn) !important;
}
.J3fjEb.qhIQqf:after, .ahil4d.qhIQqf:after, .fu5e3b.qhIQqf:after {
	background: none !important;
}
.BCNiN.g0Pkrd, .CKkvDd, .CULXYe, .GcESAf, .Ihwked, .JPdR6b, .LZOeXd, .Nw9uye, .RIrM6d, .RW0zMe, .XS1fT, .ez1Qqf, .f8kJQb, .g3VIld, .gZXxHe, .goq0fd.gZXxHe, .sJxH5c, .sVAYfc, .tjHUud, .Cqguyc, .VOEIyf .jBmls, .qLoJv, .EqMbD, .gb_fa, .u5oEgd, .gb_Nb, .gb_Lb, .gb_Eb, .gb_Ib, .qnc6Hf.RH4Xh.DZW8fd {
	background-color: var(--bgd) !important;
}
.oKubKe[aria-selected="true"], .gZXxHe:hover, .zlYNGe:hover, .z80M1.FwR7Pc, .M9kDrd:focus, .Yylo3e:focus, .Yylo3e:hover, .cjGgHb .GsLz7c:hover, .cjGgHb .M9kDrd:hover, .ncFHed .MocG8c.KKjvXb, .sH8mib, .zZhnYe.RDPZE:hover, .q71Plf .RH4Xh:hover, .qnc6Hf.RH4Xh.DZW8fd:hover {
	background-color: var(--bgh) !important;
	-webkit-transition: background-color 0.5s ease;
    -moz-transition: background-color 0.5s ease;
    -o-transition: background-color 0.5s ease;
    transition: background-color 0.5s ease;
}
.gb_Nc.gb_Oc {
	background-color: var(--bgo) !important;
}
.J3fjEb.qhIQqf:after, .ahil4d.qhIQqf:after, .fu5e3b.qhIQqf:after {
	background: linear-gradient(to right, rgba(66, 66, 66, 0), var(--bgn), var(--bgn))
}


/*---------------------Borders------------------------*/
.vI9UUd, .I4vZdf, .gb_Nb, .gb_Lb, .qnc6Hf.RH4Xh.DZW8fd {
	border-color: var(--bgh) !important;
}
.pUUP1c {
	border: 1px solid rgba(255, 255, 255, .12)
}
.LZOeXd, .PULuYe, .RIrM6d, .YVtJp, .ZYK0Xb, .fXF4Ef {
	border-top: 1px solid rgba(255, 255, 255, .12)
}
.CKkvDd, .LCX1Id, .PvjVhe, .mgK5Hf, .uFOQFe {
	border-bottom: 1px solid rgba(255, 255, 255, .12)
}
.teJoc {
	border-left: 5px solid rgba(255, 255, 255, .50)
}
.a48Y3e {
	border-top: 5px solid rgba(255, 255, 255, .50)
}
.xSzCT {
	background: rgba(48, 48, 48, .9)
}
.e2CuFe {
	border-color: rgba(255, 255, 255, .4) transparent
}
.lbqc8c {
	background: linear-gradient(to left, #303030, rgba(255, 255, 255, 0))
}


}
