css:
@-moz-document domain(gab.ai) 
{
/*
===================================================
Gab Wave - A Gab.ai theme by Dr. Kekelston (@DrKekelston).

"It's not easy being green."
===================================================

THEME COLORS
#fed74c 	orange
#fd3945 	red
#24329d 	blue
#e63fc0 	violet
#ff2fff 	pink
===================================================
*/

/*
Wide layout.
*/
.home,
.wrapper,
.profile-wrapper,
.notification-list__item,
.main-login .header-wrapper
{
	max-width: initial !important;
}

.home,
.wrapper
{
	margin-left: 25px !important;
	margin-right: 25px !important;
}

/*
Hide the list of suggested users.
*/
.home__right.content__right__box
{
	display: none;
}

/*
Neon grid.
*/
body
{
	background: 
		linear-gradient(0deg, #ff1fff99 3px, #00000000 3px), 
		linear-gradient(90deg, #ff1fff99 3px, #00000000 3px), 
		linear-gradient(0deg, #ff1fff99 1px, #00000000 1px), 
		linear-gradient(90deg, #ff1fff99 1px, #00000000 1px), 
		#223333ff !important;
  
	background-position: -2px -2px, -2px -2px, -1px -1px, -1px -1px !important;
	background-origin: padding-box !important;
	background-clip: border-box !important;
	background-size: 100px 100px, 100px 100px, 50px 50px, 50px 50px !important;

}

/*
Default look.
*/
*
{
	background-color: initial !important;
	border-width: 0 !important;
	font-weight: 800 !important;
	font-size: 18px !important;
	color: black !important;
	font-family: sans-serif !important;
    text-shadow:
	    -2px -2px 0 white,
    	2px -2px 0 white,
	    -2px 2px 0 white,
	    2px 2px 0 white
		!important;
}

textarea,
input
{
	border: 1px solid #ff5fff !important;
}

/*
Neon text glow dim.
*/
a
{
	text-decoration: none !important;
	color: #ff1fff !important;
}

/*
Neon text glow bright.
*/
a:hover
{
	text-decoration: none !important;
	color: #ffffff !important;
}

post .gab,
post .gab--big,
.notification-list__item .notification-list__item__message,
.composer-modal .composer-modal__content
{
	background: #ff2fff55 !important;
	box-shadow: none !important;
}

.composer-modal__bg,
.post-modal .post-modal__scroll
{
	background: #000000dd !important;
}

.gab__actions__action--vote--active.gab__actions__action--vote--upvote.pull-left i.ion-chevron-up,
.gab__actions__action--vote--active.gab__actions__action--vote--downvote.pull-left i.ion-chevron-down
{
	color: violet !important;
}
}
