css:
@-moz-document domain("gdax.com") {
body {
  --status-banner-height: 34px;
  --gdax-border-gray: #252b33;
  --gdax-black: #0E141D;
  --font-family: 'Open Sans', 'opensans', sans-serif, 'btcglyph';
  --link-color-default: rgba(255, 255, 255, 0.4);
  --link-color-hover: rgb(255, 255, 255);
  --link-transition: all .1s ease-in-out;
}
.AccountPanel_sliding-content_m10as {
  background-color: var(--gdax-black) !important;
  border-color: var(--gdax-border-gray);
  border-style: solid;
  border-width: 1px 0px 1px 1px;
  font-family: var(--font-family) !important;
  top: var(--status-banner-height) !important;
}
.AccountPanel_account-panel-links_jUWw7 {
  letter-spacing: 1px;
  padding: 0px 0px 10px 0px !important;
}
.AccountPanel_account-panel-links_jUWw7 li {
  font-size: 16px !important;
  font-weight: 100 !important;
}
.AccountPanel_signout-link_2hJCy {
  background-color: transparent !important;
  border-top-color: var(--gdax-border-gray)!important;
  margin-top: 22px !important;
  padding-top: 22px !important;
  font-weight: 100 !important;
  text-transform: uppercase;
  font-size: 16px !important;
  letter-spacing: 1px !important;
  color: var(--link-color-default) !important;
  transition: var(--link-transition) !important;
}
.AccountPanel_signout-link_2hJCy:hover {
  color: var(--link-color-hover) !important;
}
.AccountPanel_account-panel-group_1qnG9 label {
  font-weight: 600 !important;
  font-size: 14px;
  letter-spacing: 0.75px;
  margin-bottom: 12px !important;
  padding-bottom: 14px !important;
  border-bottom-width: 1px !important;
  border-bottom-color: var(--gdax-border-gray) !important;
}
.AccountPanel_account-panel-group_1qnG9 li {
  padding: 0 !important;
  margin: 0 !important;
  line-height: 22px !important;
}
}
