css:
@-moz-document domain(framapiaf.org) {
html,body,div { 
    /*font-family: Ubuntu; */
    color:white; 
}
body {
    font-family: "DejaVu Sans", Verdana, Geneva, sans-serif;
    color: white;
}
.drawer__header {
    background: #393f4f;
}
.drawer__inner {
    background: #444b5d;
}
.ui {
    background: #191b22;
}
.column-header {
    background: #313543;
}
.column-back-button {
    background: #313543;
    color: #2b90d9;
}
.column-header__button {
    background: #313543;
    color: #9baec8;
}
.column > .scrollable {
    background: #282c37;
}
.detailed-status, .detailed-status__action-bar {
    background-color: #282c37;
    color: white;
}
.account:hover, .status:hover {
    background-color: #393f4f;
}
.status__content {
    color: white;
}
.activity-stream .status.light .status__content a, .empty-column-indicator a, .reply-indicator__content a, .search-results__hashtag, .status__content a, .status a {
    color: #98d1e7;
}
a {
    color: #0C5B7A;
}
.account__header > div {
    background: rgba(49,53,67,.9);
    background-color: rgba(49, 53, 67, 0.9);
}
.status {
    border-bottom: 1px solid #393f4f;
	border-bottom-color: rgb(57, 63, 79);
}
.account {
    border-bottom: 1px solid #393f4f;
}
.detailed-status__action-bar {
    border-top: 1px solid #393f4f;
    border-bottom: 1px solid #393f4f;
}
.getting-started {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    padding-bottom: 235px;
/*    background: url(/packs/mastodon-getting-started-758db9bb72f30f65b07bb7b64f24ea83.png) no-repeat 0 100%;*/
    -webkit-box-flex: 1;
    -ms-flex: 1 0 auto;
}
.column-subheading {
    background: #282c37;
}
.column-link {
    background: #393f4f;
    background-color: rgb(57, 63, 79);
    color: #fff;
    text-decoration: none;
}
.column-header__back-button, .column-header__button, .column-header__button.active:hover, .column-header__buttons {
    background: none;
}
.column-header__collapsible-inner {
    background: #393f4f;
}
.column-header__collapsible > div {
    background: #393f4f;
}
.column-header__button.active, .column-header__button.active:hover {
    color: #fff;
    background: #393f4f;
    background-color: rgb(57, 63, 79);
}
.column-settings__section {
    color: #9baec8;
}
.setting-meta__label, .setting-toggle__label {
    color: #9baec8;
}
.setting-text:active, .setting-text:focus {
    color: #fff;
}
.setting-text:active, .setting-text {
    color: #fff;
}
}
