css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("scirate.com") {
  
  body {
    background-color: #222;
    color: #aaa;
  }
  
  .feed-header {
    border-bottom-color: #333;
  }
  
  /** Topmost Bar **/
  
  header.navbar {
    border-bottom-color: #282828;
  }
  
  a.header-brand {
    color: #aaa;
  }
  
  a.header-brand img {
    filter: invert(30%);
  }
  
  header.navbar .nav li > a {
    color: #aaa;
  }
  
  header.navbar .nav li.dropdown .caret {
    border-top-color: #aaa;
  }
  
  /* 'Search' box */
  .form-control {
    background-color: #222;
    color: #aaa;
    border-color: rgba(80,80,80,0.25); /* #666 */
  }
  
  /** Left navigation column **/
  .feedbar li.feed-folder .nav-header a {
    color: rgba(170,170,170,0.65); /* #aaa */
  }
  
  .feedbar .leaf a:link, .feedbar .leaf a:visited {
    color: rgba(170,170,170,0.65); /* #aaa */
  }
  
  .nav > li > a:hover, .nav > li > a:focus {
    background-color: #282828;
  }
  
  /* 'arXiv.org' heading */
  .feedbar .source-heading {
    border-bottom-color: #333;
  }
  
  /* Right 'Date Published' column */
  
  div.feed-rightbar .timenav table td.top, div.feed-rightbar .timenav table td.top {
    border-top-color: #333;
  }
  
  div.feed-rightbar .timenav table td:first-child {
    border-left-color: #333 !important;
  }
  
  div.feed-rightbar .timenav table td {
    border-color: #333;
  }
  
  div.feed-rightbar .timenav table td.underline {
    background-color: #282828;
  }
  
  blockquote {
    border-left-color: #222;
  }
  
  /*** Middle column ***/
  
  .feed-center {
    background-color: #222;
  }
  
  /** Item Row **/
  
  /* Row heading */
  li.paper .title a {
    color: #999;
  }
  
  /* 'Scite' button */
  .btn-default {
    background-color: #222;
    color: #aaa;
    border-color: #333;
  }
  
  .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open .dropdown-toggle.btn-default {
    background-color: #333;
    color: #aaa;
    border-color: #333;
  }
  
  /* BibTeX button */
  .bibtex .card textarea {
    background-color: #222;
  }
  
  /* 'PDF' button */
  .btn-success {
    background-color: #282828;
  }
  
  .btn-success:hover {
    background-color: #333;
    color: #ddd;
  }
  
  .paper-download.btn {
    color: #999;
  }
  
  /***** On Login *****/
  
  /**** 'Home' Page ****/
  
  .description {
    color: #aaa;
  }
  
  /* top 'User' tab dropdown */
  .dropdown-menu {
    background-color: #282828;
  }
  
  header.navbar .nav ul.dropdown-menu li a {
    color: #aaa;
  }
  
  header.navbar .nav li > a:hover {
    color: #aaa;
    background-color: #333;
  }
  
  /**** 'Profile' Page ****/
  
  /* Profile Pic, on left */
  #profilePage ul.user-info {
    border-top-color: #333;
    border-bottom-color: #333;
  }
  
  #profilePage ul.subscriptions li {
    background-color: #333;
  }
  
  /** Tabs **/
  .nav-tabs {
    border-bottom-color: #666;
  }
  
  .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    background-color: #222;
    border-color: #666;
    border-bottom-color: transparent;
  }
  
  /* 'Activity' tab */
  
  #activityTab .event {
    border-top-color: #333;
  }
  
  /* 'Comments' tab */
  abbr.timeago {
    color: rgba(170,170,170,0.5); /* #aaa */
  }
  
  /* 'Scites' tab */
  
  hr {
    border-top-color: #333;
  }
  
  /* 'No comments posted', 'No papers scited' message */
  .well {
    background-color: #282828;
    border-color: #333;
  }
  
  /** Footer **/
  .site-footer {
    border-top-color: #333;
  }
  
  /**** 'Settings' Page ****/
  
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #282828;
  }
  
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top-color: #333;
  }
  
  /**** Paper Page e.g. scirate.com/arxiv/1706.08560 ****/
  
  /* Comments ?Undo/Redo toggle button */
  .btn-default.disabled, .btn-default[disabled], fieldset[disabled] .btn-default, .btn-default.disabled:hover, .btn-default[disabled]:hover, fieldset[disabled] .btn-default:hover, .btn-default.disabled:focus, .btn-default[disabled]:focus, fieldset[disabled] .btn-default:focus, .btn-default.disabled:active, .btn-default[disabled]:active, fieldset[disabled] .btn-default:active, .btn-default.disabled.active, .btn-default.active[disabled], fieldset[disabled] .btn-default.active {
    background-color: #333;
    border-color: #333;
  }

}
