css:
@-moz-document url-prefix(http://confluence), url-prefix(https://confluence) {
.content-header,
.aui-sidebar-body,
.aui-sidebar-footer,
.nav-item-all-updates,
.aui-page-panel,
.acs-side-bar,
.space-tools-section,
.aui-navgroup-horizontal,
.aui-nav-item,
#full-height-container,
#main-header,
#footer,
span.today,
span.other-days,
th, td {
    color: #F2F2F2 !important;
    background-color: #333 !important
}
h1, h2, h3, h4,
#title-text > a,
.aui-nav-selected > a,
.tablesorter-header-inner,
.fc-day-number {
    color: #F2F2F2 !important;
}
#footer-logo,
.aui-icon {
    filter: invert(100%);
}
}
