css:
@-moz-document domain("gab.ai") {
/*
Wide layout.
*/

.home,
.wrapper,
.profile-wrapper,
.notification-list__item,
.main-login .header-wrapper
{
    max-width: initial !important;
}

.wrapper
{
    margin-left: 0px !important;
    margin-right: 0px !important;
}

.content__left
{
    margin-left: 25px !important;
}

.content__right
{
    margin-left: 25px !important;
    margin-right: 0px !important;
}

.home,
.tv-guide,
.topics,
.topic,
.popular-content,
.explore,
.news-feed,
.search-wrapper
{
    margin-left: 25px !important;
    margin-right: 0px !important;
}

.popular-content
{
    padding-right: 0px !important;
}

.profile
{
    margin-right: 25px !important;
}
/*
Hide the list of suggested users.
*/

.home__right.content__right__box,
.topic__right.content__right__box,
.popular-content__right.content__right__box
{
    display: none;
}
/*
Global settings.
*/

*
{
    font-size: 15px !important;
    font-weight: 400 !important;
}
/*
Post size.
*/

.post-attachment-media__item
{
    max-height: 500px !important;
    background-color: transparent !important;
    background-size: auto 100% !important;
    background-position: left !important;
    background-repeat: no-repeat !important;
}
/*
Bug fix for media attachment alignment.
*/

.composer__content__attachment__media__item
{
    vertical-align: top;
}
/*
Increase size of left hand menu.
*/

.content__left
{
    left: 0px !important;
    width: 260px !important;
}

.content__left__box--topics,
.content__left__box--links,
.content__left__box--categories
{
    padding: 10px !important;
}
}
