css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.arxiv-sanity.com") {
  
  :root {
    --default-bkgd-color: #222;
    --darker-bkgd-color: #1c1c1c;
    --lighter-bkgd-color-1: #282828;
    --lighter-bkgd-color-2: #333;
    --border-color: #333;
    --border-color-2: #484848;
    --default-color: #999;
    --darker-color: #666;
    --darker-color-2: #aaa6;
    --blue-color: #3c63af; /* from SciRate */
  }
  
  /* during page transition/loading */
  html {
    background-color: var(--darker-bkgd-color);
  }
  
  body {
    background-color: var(--darker-bkgd-color);
    color: var(--default-color);
  }
  
  #maindiv {
    background-color: var(--default-bkgd-color);
    border-left-color: var(--border-color);
    border-right-color: var(--border-color);
  }
  
  /*** topmost 'Arxiv Sanity Preserver' bar ***/
  #titdiv {
    background-color: var(--lighter-bkgd-color-1);
    border-bottom-color: var(--border-color);
    color: var(--default-color);
  }
  
  #titdiv a:link {
    color: var(--default-color);
  }
  
  #tittxt a {
    color: var(--default-color) !important;
  }
  
  /* 'Login or Create' */
  #userinfo {
    color: var(--default-color);
  }
  
  .btn-fancy {
    background-color: var(--lighter-bkgd-color-2);
  }
  
  /* Login Error Messages e.g. 'You have to enter a username' */
  ul.flashes li {
    background-color: #48480c; /* from default #EE9 */
    border-color: #606020; /* from default #993 */
  }
  
  /* MathJax Loading Message */
  #MathJax_Message {
    background-color: var(--darker-bkgd-color);
    border-color: var(--border-color);
    color: var(--darker-color);
  }
  
  /*** Search box ***/
  #qfield {
    border-color: var(--border-color);
    color: var(--default-color);
  }
  
  #banner {
    background-color: var(--lighter-bkgd-color-1);
    border-color: var(--border-color);
  }
  
  /*** Tabs bar ***/
  
  #recommend-time-choice {
    background-color: var(--default-bkgd-color);
  }
  
  #pagebar {
    background-color: var(--darker-bkgd-color);
    border-top-color: var(--darker-bkgd-color);
    border-left-color: var(--darker-bkgd-color);
    border-right-color: var(--darker-bkgd-color);
  }
  
  #pagebar a:link, #pagebar a:visited {
    color: var(--default-color);
  }
  
  #pagebar .tab-selected a {
    color: var(--blue-color);
  }
  
  .centerdiv {
    border-left-color: var(--border-color);
    border-right-color: var(--border-color);
  }
  
  /** for Well-bordered Tabs **/
  
  #recommend-time-choice {
    border-top: 1px solid var(--border-color);
  }
  
  #pagebar div {
    background-color: var(--default-bkgd-color);
    border: 1px solid var(--border-color);
    border-bottom: 0;
  }
  
  #pagebar div.tab-selected {
    border-bottom: 1px solid var(--default-bkgd-color);
    margin-bottom: -1px;
  }
  
  /*** Paper Row ***/
  
  /* Title */
  .ts a:link, .ts a:visited {
    color: var(--default-color);
  }
  
  /* 'pdf' link */
  .dllinks a:link {
    color: var(--blue-color);
  }
  
  /* Authors */
  .as a:link, .as a:visited, .as {
    color: var(--blue-color);
  }
  
  /* 'show similar' */
  .sim {
    color: var(--darker-color);
  }
  
  .sim[style*="border-left: 1px solid black;"] {
    border-left-color: var(--border-color) !important;
  }
  
  /* 'discuss' */
  a[style="color:black"] {
    color: var(--darker-color) !important;
  }
  
  /* dates */
  .ds, .ds2 {
    color: var(--darker-color);
  }
  
  /* 'cs.X' field */
  .cs a:link, .cs a:visited, .cs {
    color: #717F9B; /* from SciRate */
  }
  
  /* Save icon */
  img.save-icon {
    filter: invert(40%);
  }
  
  /* Conference */
  .ccs {
    color: var(--darker-color-2); /* rgba(170,170,170,0.5); */ /* #aaa */
  }
  
  /* Paper Preview pages */
  .animg img {
    filter: invert(86.5%); /* #282828 */
  }
  
  /* Description box */
  .tt {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /**** Tabs ****/
  
  /*** most recent ***/
  
  /** Message box e.g. 'Showing most recent Arxiv papers' **/
  
  /* Donation */
  #prompt {
    background-color: var(--lighter-bkgd-color-2) !important;
  }
  
  #prompt input[name="submit"] {
    filter: invert(70%);
  }
  
  .msg {
    background-color: var(--lighter-bkgd-color-2);
  }
  
  .vchoice {
    background-color: var(--lighter-bkgd-color-2);
  }
  
  /*** top recent ***/
  
  .timechoice {
    background-color: var(--lighter-bkgd-color-1);
  }
  
  .timechoice-selected {
    background-color: var(--lighter-bkgd-color-2);
  }
  
  .fdivider {
    background-color: var(--default-bkgd-color);
    color: var(--border-color); /* as acts as border */
  }
  
  #recommend-time-choice a:link, #recommend-time-choice a:visited {
    color: var(--blue-color);
  }
  
  /*** top hype ***/
  
  .tweetcount, .twcont {
    background-color: var(--lighter-bkgd-color-2);
    border-color: var(--border-color);
  }
  
  img.twimg {
    opacity: 0.3;
  }
  
  .twimg[style="border: 2px solid #fff;"] {
    border-color: var(--border-color) !important;
  }
  
  .twimg[style="border: 2px solid #3c3;"] {
    border-color: var(--blue-color) !important;
  }
  
  
  /*** discussions ***/
  
  .sim a[style="color:red"] {
    color: var(--blue-color) !important;
  }
  
  
  /*** Similar papers Page e.g. www.arxiv-sanity.com/1712.00443v1 ***/
  
  /* 'Most similar papers' message */
  .paperdivider {
    background-color: var(--lighter-bkgd-color-2);
    border-color: var(--border-color-2);
    color: var(--default-color);
  }
  
  
  /**** On Login ****/
  
  /* top Username, Logout buttons */
  #userinfo a {
    border-color: var(--border-color);
    color: var(--default-color) !important;
  }
  
  /*** Account Page ***/
  
  /* 'N followers', 'N following' */
  .userlst-title {
    background-color: var(--default-bkgd-color);
    border-bottom-color: var(--border-color);
  }
  
  .userlst {
    border-color: var(--border-color);
  }
  
  .fdx {
    background-color: #400;
    color: #770000;
    border-color: #770000;
  }
  
  .li-inactive {
    background-color: #400;
  }
  
  /*** Discuss Page ***/
  
  /* 'Pitch in!' */
  #pitchin-cfa {
    background-color: var(--lighter-bkgd-color-2) !important;
    border-color: var(--border-color) !important;
  }
  
  textarea#post-text {
    border-color: var(--border-color) !important;
  }
  
  #pitchin div[style*="border-top:1px solid black;"] {
    border-top-color: var(--border-color) !important;
  }
  
  #pitchin div[style*="background-color: #eee;"] {
    background-color: var(--default-bkgd-color) !important;
  }
  
  .ppbutton {
    background-color: var(--lighter-bkgd-color-2);
    color: var(--default-color);
  }
  
  /* 'Discussion' */
  .cheader {
    background-color: var(--lighter-bkgd-color-2);
  }
  
  .cheader[style="background-color:#ff0"] {
    background-color: #061a45 !important;
  }
  
  .cuser {
    color: var(--blue-color);
  }
  
  .caction img {
    filter: invert(40%);
  }
  
  .ctext {
    background-color: var(--lighter-bkgd-color-1);
    border-color: var(--border-color);
  }
  
  .ctag {
    background-color: var(--lighter-bkgd-color-2);
    color: var(--darker-color-2);
  }
  
  .ctag-count-zero {
    background-color: var(--darker-bkgd-color) !important;
    color: var(--darker-color-2);
  }

}
