css:
@-moz-document regexp(".*duckduckgo.com/html.*") {
/* Background colors */

.body--home,
.site-wrapper--home,
.body--html {
    background-color: #1d1c1c;
}
html {
    background-color: #1d1c1c;
}
.result.highlight {
    background-color: #1d1c1c;
}
.result__body.highlight {
    background-color: #1d1c1c;
}
.sitelinks_td.highlight {
    background-color: #1d1c1c;
}
.frm__text,
.frm__input {
    background-color: #1d1c1c;
}
.zci-wrapper {
    background-color: #1d1c1c;
}
.result:hover {
    background-color: #000000;
}
/* Text colors */

.msg--spelling {
    color: #B7B7B7;
}
body,
input,
select,
textarea {
    color: #A3A3A3;
}
.zci__heading {
    color: #B7B7B7;
}
.result__snippet {
    color: #818181;
}
.result__snippet b,
.result__snippet strong {
    color: #A3A3A3;
}
.result .result__snippet:hover,
.result .result__snippet:active {
    color: #a7a7a7;
}
/* Link colors */

a:hover,
a:focus {
    color: #7E5891;
}
.zci__result a {
    color: #6a86a8;
}
.msg--spelling a {
    color: #6a86a8;
}
a {
    color: #6a86a8;
}
.result__a {
    color: #6a86a8;
}
.result__a:hover {
    color: #729fd7;
}
.result__a:visited,
.result__a:visited:hover {
    color: #7E5891;
}
/* Logo */

.logo_homepage {
    background-image: url("https://duckduckgo.com/assets/logo_homepage.alt.v106.svg");
}

/* Fix searchbar */

.search, .search--adv {
	padding-right: 3.75em;
}
}
