css:
@-moz-document domain("gab.ai") {
/*
Theme colors:
hsla(335, 73%, 57%, 1.0)	UI pink
#e14283 					UI pink
hsla(149, 62%, 51%, 1.0)	green
#34cf7f						green
*/
/*
Purple on white polka dots.
*/

.gab--big,
.gab--comment,
.content__left__box--topics,
.content__left__box--links,
.content__left__box--categories,
.notification-list__item .notification-list__item__message,
.composer-modal__content,
.settings-wrapper,
.topics__group__topic,
.tv-guide__show,
.news-list,
.profile__columns__left .profile__box,
.chat__message-container,
.mini-user-list,
.settings-wrapper,
.settings-menu,
.composer__content__poll,
.user-list
{
    background: white !important;
    background-image: radial-gradient(#e1428311 20%, transparent 16%), radial-gradient(#e1428311 20%, transparent 16%) !important;
    background-size: 30px 30px !important;
    background-position: 0 0, 15px 15px !important;
    box-shadow: inset 0px 0px 5px 10px rgba(255, 255, 255, 1.0) !important;
    border: 0px !important;
    border-radius: 0px !important;
}

/*
White on green polka dots.
*/

body,
.post-modal__content,
.composer__content__attachment,
.chats,
.gab__quoted-post,
.post-attachment-url
{
    background: hsla(149, 62%, 90%, 1.0) !important;
    background-image: radial-gradient(white 10%, transparent 10%), radial-gradient(white 10%, transparent 10%) !important;
    background-size: 50px 50px !important;
    background-position: 0 0, 25px 25px !important;
    border-width: 0px !important;
    border-radius: 15px !important;
}
/*
Purple stripes.
*/

.header,
.gab__actions.clearfix,
.composer__content,
.profile__bar,
.header-dropdown__content,
.settings-menu__item--active,
.post-list__load-more,
.notification-bubble
{
    box-shadow: inset 0px 0px 5px 5px rgba(255, 255, 255, 1.0) !important;
    background-color: hsla(335, 73%, 96%, 1.0) !important;
    background-image: repeating-linear-gradient(135deg, transparent, transparent 10px, rgba(255, 255, 255, 0.8) 10px, rgba(255, 255, 255, 0.8) 20px) !important;
    border-width: 0px !important;
}
/*
Green stripes.
*/

.gab__reason,
.composer-modal__content h2,
.chats__left__item--active,
.settings-wrapper h2,
.main-login .content__left__box a.content__left__box__active,
.header-dropdown__content__notifications__item--unread
{
    box-shadow: inset 0px 0px 3px 3px rgba(255, 255, 255, 0.5) !important;
    background-color: #34cf7f !important;
    background-image: repeating-linear-gradient( 45deg, transparent, transparent 10px, rgba(255, 255, 255, 0.5) 10px, rgba(255, 255, 255, 0.5) 20px) !important;
    text-shadow: -1px -1px 0 white, 1px -1px 0 white, -1px 1px 0 white, 1px 1px 0 white;
    color: #e14283 !important;
    font-weight: bold !important;
    border-width: 0px;
    border-radius: 3px 3px 0px 0px;
}
/*
Solid green button.
*/

button,
.composer__content__attachment a,
.composer__content__attachment label,
.tv-guide .main-title a,
.composer__content__button,
.header__link--compose
{
    background: #34cf7f !important;
    font-weight: bold !important;
    border: 1px solid white !important;
    color: white !important;
}
/*
Solid pink button.
*/

.header__link--compose:hover,
.composer__content__attachment a:hover,
.composer__content__attachment label:hover,
.composer__content__button:hover,
.main-title a:hover
{
    background: #e14283 !important;
    font-weight: bold !important;
    border: 1px solid white !important;
    color: white !important;
}
/*
Pink highlight.
*/

.main-login .content__left__box a:hover,
.header-dropdown__content__notifications__item:hover,
.header-dropdown__link:hover,
.settings-menu__item:hover,
.mini-user-list__item:hover,
.news-list__item:hover,
a.chats__left__item:hover,
a.tv-guide__show:hover,
.header-dropdown__content__notifications-all:hover,
.post-attachment-url:hover,
.user-list__item:hover
{
    background: #e1428333 !important;
    border-radius: 5px !important;
    box-shadow: inset 0px 0px 5px 5px #ffffffaa !important;
}
/*
Big rounded corners.
*/

.gab--big,
.gab--comment,
.tv-guide__show,
.topics__group__topic,
.content__left__box--topics,
.content__left__box--links,
.content__left__box--categories,
.composer-modal__content,
.notification-list__item .notification-list__item__message,
.profile__columns__left .profile__box,
.mini-user-list,
.profile__box,
.news-list
{
    border: none !important;
    border-radius: 15px !important;
}
/*
Borderless.
*/

.content__left *,
.content__right__box *,
.header-dropdown__content *,
.profile__columns *
{
    border: none !important;
    opacity: initial !important;
}
/*
Round borders.
*/

.gab__actions.clearfix,
.gab__reason,
.settings-wrapper,
.chat__message-container
{
    border-radius: 3px !important;
}
/*
Layout changes.
*/

.gab--comment
{
    margin-top: 15px !important;
}
/*
Hide boxes.
*/

.notification-filter a,
chats div,
chats a,
chats h2,
.composer__content__attachment
{
    background: initial !important;
    border: none !important;
}

.main-login .content__left__box a.content__left__box__topic
{
    background: none;
}
}
