css:
@-moz-document domain("www.drive2.ru") {
html,
body {
    color: #111;
    background-color: #cfcfcf;
}
a {
    color: #70a0e0;
}
.c-link--color00:not(.c-link--nd) {
    color: #eee;
}
.l-content {
    background-color: #cfcfcf;
}
.c-nav-block__link {
    color: #aaa;
}
.c-block {
    background-color: #cfcfcf;
}
.c-lb-card {
    background-color: #cfcfcf;
}
.c-link {
    color: #111 !important;
}
.c-button {
    color: #5890e6;
}
.c-block__more {
    background: #cfcfcf;
    color: #70a0d0;
    border-top: 1px solid #707070;
}
.c-button--solid,
.c-button-hover:hover .c-button-hover__item {
    color: #fff !important;
}
.footer-container {
    background: #cfcfcf;
}
.c-block__footer {
    background-color: #cfcfcf;
}
.c-block--collapse-top,
.c-block__button,
.c-block__footer {
    border-top: 1px solid #707070;
}
.c-link--text:not(.c-link--nd) {
    color: #FFF;
}
.c-car-card__info {
    color: #333;
}
.c-msg__body {
    color: #fff;
    background: #cfcfcf;
}
.bubble-body {
    color: #ccc;
}
.bubble-ntfy .bubble-body {
    padding: 0;
    background-color: #cfcfcf;
}
.ntfy-links-item {
    background: #666;
    color: #50a0d0;
}
.c-link--color09:not(.c-link--nd) {
    color: #27b02b;
}
.c-post__desc {
    color: #222;
}
.c-block-nav__link span {
    color: #bbb;
}
.c-block-nav__link strong {
    color: #70a0d0;
}
.c-input,
.c-select>option,
.c-textarea {
    color: #222;
    background: #cfcfcf;
}
.imagelist-controls {
    background: #cfcfcf;
}
.c-button--gray {
    color: #aaa !important;
}
.bubble-tooltip .bubble-body {
    background-color: #cfcfcf;
}
.c-user-card__cars {
    color: #222;
}
.c-user-card__status {
    color: #3e3e3e;
}
a.c-block-nav__link:hover {
    background-color: #333;
}
.c-nav-block__item:hover {
    background-color: #cfcfcf;
}
.c-pc-card {
    border-bottom: 1px solid #777;
    background-color: #cfcfcf;
}
.dialog {
    background: #cfcfcf;
}
.c-msgr-contact__status {
    color: #eee;
}
.post-promo--standalone {
    background: #cfcfcf;
}
.c-pager--alt {
    background-color: #cfcfcf;
    border-bottom: 1px solid #777;
}
.c-car-desc.is-shaded:before {
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0), #cfcfcf);
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0), #cfcfcf);
}
.l-alt {
    display: none;
}
.c-dv-sns {
    display: none;
}
.c-post-card--h .c-post-card__body {
    background-color: #cfcfcf;
    border: 0px solid black !important;
}
.c-post-card {
    box-shadow: none;
}
.c-button--solid.c-button--lightgray,
.c-button-hover:hover .c-button-hover__item.c-button--lightgray {
    background-color: #cfcfcf;
}
.c-button--solid.c-button--lightgray:hover:not(.is-off):not([disabled]) {
    background-color: #222 !important;
}
.c-cp-body {
    background: #444;
}
.c-cp-block:nth-of-type(2n+1) {
    background-color: #222;
}
.c-cp-block--alt {
    background-color: #222;
}
.c-cp-stats {
    color: #ccc;
}
.c-cp-feedback-stub {
    background-color: #222;
}
.c-select {
    color: #ccc;
    background: #222;
}
.c-block__more:hover {
    background-color: #222 !important;
}
.c-msgr-contact:hover {
    background: #222;
}
.page-header {
    color: #aaa;
}
.totop {
    color: rgba(255, 255, 255, 0.5);
    fill: #fff;
}
.totop:hover {
    background-color: rgba(34, 34, 34, 0.5);
}
.offer-card--wide .offer-card__location {
    color: #aaa;
}
.offer-card:hover {
    background-color: #222 !important;
}
.l-page-side,
.c-logo,
.c-nav-block-icon,
.l-footer {
    display: none;
    width: 0px !important;
}
.l-page-main {
    width: 100%;
}
.c-top {
    position: fixed;
    top: -43px;
    border: 0px solid #999;
    width: 100%;
}
.c-top:hover {
    top: 0px;
}
.c-post-card,
.c-user-card,
.c-car-card {
    opacity: 0.3;
}
.c-post-card:hover {
    opacity: 1;
}
.c-user-card:hover {
    opacity: 1;
}
.c-car-card:hover {
    opacity: 1;
}
.c-preview-pic {
    width: 200px;
}
.c-preview-pics,
.c-article-card__pic {
    opacity: 1;
    width: 200px;
    border: 0px solid #fff
}
.c-article-card__pic:hover {
    opacity: 1;
}
.c-preview-pics:hover {
    opacity: 1;
}
.c-post-preview__title,
.c-article-card__title {
    font-size: 11pt;
    color: #222;
}
.c-link {
    text-decoration: underline;
    text-decoration-color: #333 !important;
}
.c-like {
    opacity: 0.5;
}
.c-nav {
    left: -230px;
    position: relative;
    background-color: #111;
    border: 0px solid #999;
    padding-left: 20px;
}
.c-nav:hover {
    left: -50px;
    z-index: 20;
}
.l-page {
    position: absolute;
}
img {
    opacity: 0.5;
}
img:hover {
    opacity: 1;
}
.c-link,
.c-header,
.c-header-main,
.c-article-card__title,
.ytp-title-link {
    font-size: 11pt;
}
.c-block-card .c-snaps-preview__top-info,
.c-post-preview__title .c-link,
.c-article-card__title {
    font-weight: 600;
    text-decoration: none !important;
}
.o-grid__item {
    width: 100% !important;
}
.c-block-card__body .c-snaps-preview__pics,
.c-recommended-users .c-user-card-inline__pic,
.c-post-preview .c-preview-pics,
.c-post-card .c-post-card__pic-container,
.c-comm-card .c-comm-card__pic,
.c-post-preview .c-preview-pic,
.c-article-card .c-article-card__pic,
.o-grid__item .c-car-card__pic {
    position: relative !important;
    width: 170px !important;
    margin: 0px 10px 0px 0px;
    padding: 0px;
    float: left;
    max-height: 100px;
    overflow: hidden;
}
.c-block-card__body .c-snaps-preview__lead,
.c-comm-card .c-post-card__body,
.c-comm-card .c-comm-card__body,
.c-comm-card .c-comm-card__title,
.c-post-preview .c-post-preview__lead,
.c-post-preview .c-post-preview__title,
.c-article-card .c-article-card__title,
.c-article-card .c-article-card__lead,
.o-grid__item .c-car-card__body {
    position: relative !important;
    margin: 0px;
    padding: 0px;
}
.c-block-card__body .c-snaps-preview__lead {
    top: 15px;
    height: 70px;
    overflow: hidden;
}
.c-block-card__body .c-snaps-preview__top-info {
    position: absolute;
    left: 180px;
}
.c-block-card__body .c-snaps-preview__item .c-snaps-preview__like {
    position: absolute;
    top: 70px;
}
.c-post-card--outline {
    border: 0px solid #fff;
}
.c-post-card__pic-container {
    margin: 0px !important;
}
.c-block .c-block__footer {
    display: none;
}
.c-block {
    border: 0px solid #cfcfcf;
    padding: 0px;
    background: none;
    margin: 0px;
}
.c-block-card__body {
    padding: 0px;
}
.c-block .c-block-card__meta,
.c-block-card .c-post-preview__top-info,
.c-block-card .text,
.c-block .c-header {
    display: none;
}
.c-block__more {
    min-height: 20px;
    margin: 0px;
    padding: 0px;
    width: 170px;
}
.o-grid {
    margin-top: 0px;
}
.c-preview-pic .o-img {
    min-height: 170px;
}
.c-preview-pic .o-img img {
    min-height: 170px;
}
.c-post-card {
    margin: 10px 0px 0px 0px;
}
.c-post-card--alt .c-post-card__pic-container {
    margin: 0px 10px 0px 0px !important;
}
.c-block-card .c-block-card__body {
    border: 0px solid white;
    min-height: 110px;
}
.c-block .o-grid .o-grid__item {
    border: 0px solid white;
    min-height: 110px;
    margin: 0px 0px 0px 0px !important;
}
.c-button--red {
    background-color: green !important;
}
.c-like .c-like__button,
.c-like .c-like__counter {
    height: 20px;
    top: 0px;
    margin-top: 0px;
    line-height: 0px;
}
.c-comm-card__button .c-button {
    opacity: 0.3;
    background: none;
    border: 1px solid grey;
    color: #222 !important;
    height: 20px;
    width: 100px;
}
.c-block-nav__link {
    font-size: 10px;
    padding: 0px;
    width: 100%;
}
.c-snaps-preview__pics,
.c-post__media,
.c-post__pic {
    max-width: 400px;
}
.l-page-main .c-cotd {
    height: 200px;
    overflow: hidden;
}
.c-notification-bubble {
    top: 37px;
}
.totop {
    left: inherit !important;
    right: 20px !important;
}
.c-recommended-user {
    opacity: 0.3;
}
.c-recommended-user:hover {
    opacity: 1;
}
.l-nav .c-notification-bubble {
    display: none !important;
}
.c-comment-form {
    top: 30px;
    position: relative;
}
.c-block-nav__link--next span:before {
    display: none !important;
}
.c-block-nav__link--prev span:before {
    display: none !important;
}
.c-top__wrapper .bubble-body {
    width: 98%;
    left: 1%;
    position: fixed;
    height: 220px;
}
.c-top__wrapper .ntfy-list {
    height: 200px;
    width: 50%
}
.c-top__wrapper .bubble-body,
.c-top__wrapper .c-notifications__body .c-link {
    color: #333;
    font-size: 12px !important;
}
}
