css:
@-moz-document domain("news.ycombinator.com") {
#hnmain tbody td.title > a {
	color: #ec5c00;
	font-size: 1.3rem;
}

tr.spacer {
	height: 15px!important;
}

table.itemlist {
	border-spacing: 3px 6px;
	margin: 0 auto;
	width: 70%;
}

span.rank {
	font-size: 12px;
}

td[bgcolor] {
	padding: 10px 20px 10px 27px;
}

body {
	margin: 0!important;
}

a.togg {
	color: #ff8600!important;
	font-size: 13px;
}

tr.athing.comtr > td {
	padding-bottom: 12px;
}

.comment p > a[href],
.commtext.c00 a[rel] {
	color: #1a91ff!important;
	display: inline-block;
	font-size: 1.1rem;
	margin-top: -15px!important;
	text-decoration: none;
}

div.comment > span > div > p > font a[href],
div.comment > span > div > p > font u,
.comment a:link,
.comment a:visited {
	color: #ff8232!important;
	font-size: 1.1rem;
}

.title {
	font-size: 15pt;
}

table.fatitem > tbody td.subtext {
	opacity: .4;
	transition: .5s cubic-bezier(0.25, 0.1, 0, 1.23);
}

table.fatitem > tbody > tr > td > form {
	opacity: .2;
	transition: .5s cubic-bezier(0.25, 0.1, 0, 1.23);
}

.subtext {
	color: #828282;
	font: 13px Verdana, Geneva, sans-serif;
	opacity: 0.2;
	transition: .5s cubic-bezier(0.25, 0.1, 0, 1.18);
}

.comhead {
	color: transparent;
	margin-left: 5px;
}

center {
	background: #f6f6ef;
}

td.default > div.comment {
	color: #1f1f1f;
	font: 400 19px/28px 'Open Sans', myriad-pro, Arial, Helvetica, Calibri, sans-serif;
	text-align: justify;
}

tbody td.title > a {
	font-size: 30px;
}


table.fatitem tr:nth-child(4) td:nth-child(2) {
	color: #1f1f1f;
	font: 600 19px/24px 'Open Sans', myriad-pro, Arial, Helvetica, Calibri, sans-serif;
	font-size: 1.2rem;
	padding: 20px 0 0;
}

span.age > a {
	color: #000!important;
	font-weight: 700;
	opacity: 1;
}

td.default > div:nth-child(1) {
	margin-bottom: 0!important;
	opacity: 0.3;
}

span.sitestr {
	opacity: 0.2;
	transition: .5s cubic-bezier(0.25, 0.1, 0, 1.18);
}

table#hnmain {
	width: 100%;
}

td[bgcolor] table {
	margin: 0 auto;
	width: 75%!important;
}

td.title a.morelink {
	font-size: 1rem!important;
}

#hnmain tbody td.title > a:visited {
	color: #ffbc90;
}

.subtext a:hover {
	color: #000;
}

a[href^="reply"] {
	font-size: .9rem!important;
	text-decoration: none!important;
}

pre {
	background: #ff924a1c;
	border-radius: 10px;
	font-size: 1rem;
	margin: 25px 0;
	max-width: none;
	padding: 20px 0;
}

.default p {
	line-height: 30px;
	margin-top: 15px;
}

span.commtext.c00 {
	line-height: 30px;
}

pre > code {
	font-family: Consolas, monospace;
	font-size: 1.1rem;
	line-height: 26px;
}

div.comment > span > div > p > font u,
.comment a:link,
.comment a:visited,
.subtext a:link,
.subtext a:visited,
font > u {
	text-decoration: none;
}

table.fatitem > tbody > tr > td > form:hover,
.subtext:hover,
span.sitestr:hover {
	opacity: 1;
}

table.fatitem,
table.comment-tree {
	margin: 0 auto;
	width: 75%;
}
a.storylink {
	color: #ec5c00 !important;
	line-height: 2rem;
}

table#hnmain tr#pagespace {
	height: 25px !important;
}
.votearrow {
	background: none;
	width: 0px;
	height: 0px;
	border-left: 5px solid transparent;
	border-right: 5px solid transparent;
	border-top: 4px solid transparent;
	border-bottom: 8px solid #ff65029c;
}
}
