css:
@-moz-document domain("slack.com") {
/**
   * A super-compact mode for Slack.
   * Activate it with the "dense" theme in your profile preferences.
   */
/* Messages */

body {
    font-size: 0.8rem !important;
}

.c-message {
    font-size: 0.8rem !important;
    line-height: 0.8rem;
}

.c-message__avatar {
    height: 24px !important;
    line-height: 24px !important;
    width: 24px !important;
}

.p-message_pane__banner, .p-message_pane__briefings_back_button {
    font-size: 0.8rem;
    line-height: 0.8rem;
}

.channel_header {
    padding: .2rem 0 0;
}

#search_container {
    display: none;
}

#client_header {
    flex-basis: auto;
}

.flex_pane_showing:not(.details_showing) .flex_header, .search_focused .flex_header {
    width: 100px;
}
    
.message {
    font-size: 0.7rem !important;
    line-height: 1.0rem !important;
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}
.inline_attachement,
.inline_attachment .inline_attachment_wrapper {
    padding-top: 0.2rem !important;
    padding-bottom: 0.2rem !important;
}
.inline_attachement,
.inline_attachment .attachment_service_name,
.inline_attachment .attachment_title,
.inline_attachment .attachment_contents,
.file_comment .timestamp,
.file_reference .file_details,
.message.file_share .file_details,
.message.file_mention .file_details,
.message.file_upload .initial_comment,
.message .file_name {
    font-size: 0.5rem !important;
}
.file_details {
    line-height: 1.0rem !important;
}
.channels_list_holder .unread_highlight {
    line-height: 1.0rem !important;
}
.message.divider {
    border-top: none !important;
}
.msg_inline_holder {
    margin-bottom: 0.25rem !important;
}
span.emoji-inner {
    width: 80% !important;
    height: 80% !important;
}
/* Sidebar */

.p-workspace--context-pane-expanded {
    grid-template-columns: 150px auto 300px;
}

.channels_list_holder .section_holder {
    margin: 1rem 0px !important;
}
.channels_list_holder ul li,
.channels_list_holder ul li a {
    line-height: 1rem !important;
    height: 1.1rem !important;
}
.client_channels_list_container {
    max-width: 150px;
}
#col_channels {
    width: 150px;
}
.p-channel_sidebar, .p-workspace__sidebar, .p-classic_nav__team_header {
    width: 150px;
}
.p-channel_sidebar__static_list div, .p-channel_sidebar__static_list div a {
    height: 1.1rem !important; 
}

.p-channel_sidebar__channel:before, .p-channel_sidebar__link:before, .p-channel_sidebar__channel:after, .p-channel_sidebar__link:after {
    font-size: 1.1rem;
}

.p-channel_sidebar__banner--unreads {
    display: none;
}

/* other sidebar */

#client-ui.flex_pane_showing #col_flex {
    flex-basis: 200px;
}

.member_image.thumb_36 {
    width: 26px;
    height: 26px;
}

ts-message .message_gutter {
    width: 50px !important;
}

ts-message .message_content {
    margin-left: 50px !important;
}

#flex_contents .heading {
    padding: 10px 10px 5px 10px;
}

#mentions_options {
    display: none;
}

.mention_rxn .rxn_emoji_icon.rxn_1 span.emoji-sizer {
    font-size: 30px;
}

#msg_input.ql-container {
    padding: 0 0 0 2.625rem;
    height: 10px;
    max-height: 10px;
    min-height: 30px;
}

#message_edit_form .ql-editor, #message_edit_form .ql-placeholder, #msg_form .ql-editor, #msg_form .ql-placeholder {
    line-height: 1.46667;
    padding-bottom: 9px;
    padding-top: 4px;
}

.ts_icon {
    line-height: 1.8rem;
}

.day_container:last-child .day_msgs {
    padding-bottom: 10px;
}

.mention_rxn_msg_holder ts-message .message_content {
    margin-left: 0px !important;
}

.message_star_holder {
    display: none;
}

.p-channel_sidebar__jumper {
	display: none;
}

.c-scrollbar__track {
    display: none;
}

#main_emo_menu, .msg_mentions_button {
    bottom: 10px !important;
}
}
