css:
@-moz-document domain("hackforums.net") {
#panel .upper,
#panel .lower {
    background: #073642;
    color: #586E75;
    border-top: dashed 2px #586E75;
}
#panel .lower {
    border-bottom: dashed 2px #586E75;
}
#panel .upper a:link,
#panel .upper a:visited,
#panel .upper a:hover,
#panel .upper a:active {
    color: #839496;
}
#panel .lower a:link,
#panel .lower a:visited,
#panel .lower a:hover,
#panel .lower a:active {
    font-weight: 700;
    color: #839496;
}
body {
    background: #002B36;
    font-family: monospace;
    color: #839496;
}
#content {
    background: #073642;
}
#container {
    border-left: dashed 2px #586E75;
    border-right: dashed 2px #586E75;
}
.pm_alert {
    background: #002B36;
    border: 2px solid #586E75;
}
a,
a:visited,
a:link {
    font-weight: bold;
    color: #93a1a1;
}
.smalltext {
    color: #839496;
}
.thead {
    background: #073642;
    border: 1px dashed #586E75;
}
.tcat {
    background: #073642;
    border: 1px dashed #586E75;
    border-top: none !important;
}
.trow2 {
    background: #073642;
    border: 1px dashed #586E75;
    border-top: none !important;
}
.trow1 {
    background: #073642;
    border: 1px dashed #586E75;
    border-top: none !important;
}
.tfoot {
    background: #002B36;
}
.post {
    background: #002B36;
}
.post_controls {
    background: #002B36;
    border-bottom: 2px dashed #586E75;
}
.post_content .signature {
    border-top: 1px dotted #586E75;
}
.post .post_head {
    border-bottom: 1px dashed #586E75;
}
.post .post_author {
    border-bottom: 1px dashed #586E75;
}
