html::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 1);
    background-color: #EEE8D5;
    border: 2px solid black;
}
html::-webkit-scrollbar {
    width: 12px;
    background-color: #002B36;
}
html::-webkit-scrollbar-thumb {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 1);
    background-color: #002B36;
}
.codeblock {
    background: #073642;
    border-style: solid;
}
table {
    color: #839496;
}
blockquote {
    background: #073642;
    border-style: solid;
    color: #839496;
}
.shadetabs li a {
    background-color: #002B36;
    font-family: monospace;
    font-weight: 900;
    border: 1px solid #EEE8D5;
    border-radius: 0px !important;
}
.postbit_buttons > a:link,
.postbit_buttons > a:hover,
.postbit_buttons > a:visited,
.postbit_buttons > a:active {
    background: #073642;
    color: #EEE8D5;
    border: 2px solid #586E75;
}
a.button:link,
a.button:hover,
a.button:visited,
a.button:active {
    background: #002B36;
    border: 2px solid #586E75;
}
.forum_legend {
    display: none;
}
p {
    color: #839496;
}
fieldset {
    background: #002B36;
}
iframe html body {
    color: #839496 !important;
}
.sceditor-container {
    background: #93a1a1 !important;
    border: 1px solid #EEE8D5 !important;
    border-radius: 0px !important;
}
.sceditor-container iframe {
    background: #93a1a1 !important;
}
div.sceditor-toolbar {
    background: #93a1a1 !important;
    border-bottom: none !important;
}
div.sceditor-group {
    background: #EEE8D5 !important;
}
button,
input.button {
    background: #002B36;
    color: #839496;
}
img[src="https://hackforums.net/images/logo/logo.png"] {
    background: url(data:image/png;base64,) no-repeat !important;
}
}
