css:
@-moz-document domain("standingtrials.com") {
h1 > span,
h2 > span,
h3 > span,
h4 > span,
h5 > span,
h6 > span,
h1 > a,
h2 > a,
h3 > a,
h4 > a,
h5 > a,
h6 > a,
p > strong,
p.author,
.pagination,
.gensmall,
.postprofile,
dl.icon,
dt > strong,
dt > span,
#smiley-box,
#smiley-box > a,
#postingbox label,
.signature,
#page-body > p > a,
label,
.panel.bg1 > .inner,
.panel.bg2 > .inner,
a.postlink,
dl.left-box > dd,
dl.left-box > dt,
dl.details > dd,
dl.details > dt,
dd.mark,
ul.topiclist > li.header > dl > dt
{
    color: white !important;
}

.content 
{   
	color: #888 !important;
}

li.header,
#navi > a,
#navi > ul.linklist > li > div > a {
    color: white !important;
}

#phpbb > ul {
    background-color: #1A1A1F !important;
    height: 44px !important;
    border-bottom: 1px solid #2A2A2F !important;
}
#phpbb > ul > li > a,
b {
    color: #E8E8E8 !important;
}
#phpbb > ul > li {
    font-size: 10px !important;
}
#phpbb,
html {
    background-image: url('http://standingtrials.com/gallery/image.php?album_id=291&image_id=11991&view=no_count') !important;
}
#search-box input {
    width: 100% !important;
}
#wrap {
    background-color: #1A1A1F !important;
    border: none !important;
    box-shadow: 0px 3px 15px rgba(0, 0, 0, 0.8) !important;
}
.barr,
.upouttherest,
.forabg,
.upoutannouncement {
    border: none !important;
}
.mainnavi,
ul.topiclist.forums {
    background-color: #2A2A2F !important;
}
ul.topiclist.forums > li.row:hover {
    background-color: #2a303a !important;
}
.upoutannouncement > img {
    content: url('http://standingtrials.com/gallery/image.php?album_id=291&image_id=11987&view=no_count') !important;
    width: 500px !important;
    height: 300px !important;
}
.upouttherest > div > div,
.therest,
.therest > div,
.mainnavi {
    border-color: #2A2A2F !important;
    color: white !important;
}
/*
uncomment the below statement if you want get rid of all the news and stuff on the homepage
/*
.upouttherest,
.therest,
.upoutannouncement,
.mainnavi,
#page-footer,
#search-box > form > fieldset > a {
    display: none !important;
}*/

.upouttherest > div > div > div.inner > ul.topiclist.topics > dd.posts {
    border: none !important;
}
.upouttherest > div > div > div.inner > ul.topiclist > li.header > dl.icon > dd.posts {
    margin-left: -45px !important;
}
.upouttherest > div > div > div.inner > ul.topiclist > li.header > dl.icon > dd.views {
    margin-left: -5px !important;
}
span.corners-top,
span.corners-bottom {
    display: none !important;
}
.upouttherest,
.upouttherest > div,
.upouttherest > div > div,
.upoutannouncement,
.therest,
.forabg,
.forumbg,
.panel
{
    background-color: #1A1A1F !important;
}
ul.topiclist > li.header > dl.icon > dt,
ul.topiclist > li.header > dl.icon > dt > a,
ul.topiclist > li.header > dl.icon > dd.topics,
ul.topiclist > li.header > dl.icon > dd.posts,
ul.topiclist > li.header > dl.icon > dd.lastpost,
ul.topiclist > li.header > dl.icon > dd.views,
ul.topiclist.forums > li.row > dl.icon > dd.lastpost > span > a:not(.username-coloured),
#page-body > h2 > a,
h1,
h2,
h3,
h4,
h5,
h6,
.pagination > a,
a.forumtitle,
dt > a:not(.username-coloured),
a.postlink:hover,
form#viewfolder > div.panel > div.inner > fieldset.display-actions > div > a
{
    color: orange !important;
}
ul.topiclist.forums > li.row > dl.icon > dd.posts,
ul.topiclist.forums > li.row > dl.icon > dd.topics,
ul.topiclist.forums > li.row > dl.icon > dd.lastpost {
    border: none !important;
    color: white !important;
}
ul.topiclist.forums > li.header > dl.icon > dd.topics,
li.row,
.post.bg1,
.post.bg2 {
    border-bottom: 1px solid black !important;
    border-top: 1px solid black !important;
}
ul.topiclist.forums > li.row > dl.icon {
    background-image: none !important;
}
ul.topiclist.forums > li.row > dl.icon > dt {
    padding-left: 0px !important;
}
ul.topiclist > li.header > dl.icon > dd.topics,
ul.topiclist > li.header > dl.icon > dd.posts,
ul.topiclist > li.header > dl.icon > dd.lastpost {
    cursor: pointer !important;
    text-align: left !important;
}
.dropdown-content,
.dropdown-contentone,
.dropdown-contenttwo,
.dropdown-contentthree,
.dropdown-contentfour,
.dropdown-contentfive {
    background-color: #2A2A2F !important;
    border: 1px solid black !important;
}
.dropdown-content > lii,
.dropdown-contentone > lii,
.dropdown-contenttwo > lii,
.dropdown-contentthree > lii,
.dropdown-contentfour > lii,
.dropdown-contentfive > lii {
    width: 100% !important;
}
.dropdown-content > lii > a,
.dropdown-contentone > lii > a,
.dropdown-contenttwo > lii > a,
.dropdown-contentthree > lii > a,
.dropdown-contentfour > lii > a,
.dropdown-contentfive > lii > a,
.dropdown-content > a,
.dropdown-contentone > a,
.dropdown-contenttwo > a,
.dropdown-contentthree > a,
.dropdown-contentfour > a,
.dropdown-contentfive > a {
    color: white !important;
}
.dropdown-content > lii > a:hover,
.dropdown-contentone > lii > a:hover,
.dropdown-contenttwo > lii > a:hover,
.dropdown-contentthree > lii > a:hover,
.dropdown-contentfour > lii > a:hover,
.dropdown-contentfive > lii > a:hover,
.dropdown-content > a:hover,
.dropdown-contentone > a:hover,
.dropdown-contenttwo > a:hover,
.dropdown-contentthree > a:hover,
.dropdown-contentfour > a:hover,
.dropdown-contentfive > a:hover,
dl.details > dd > a,
dl.details > dd > strong > a
{
    color: orange !important;
}
.postbody,
.post.bg1,
.post.bg2,
.row.bg1,
.row.bg2 {
    background-color: #2A2A2F !important;
    margin: 0 !important;
    border-left: none !important;
    border-right: none !important;
    padding-top: 8px;
    padding-bottom: 8px;
}
.bg1,
.bg2 {
    border: 2px solid #2A2A2F !important;
}

.pmlist li.bg1, .pmlist li.bg2 {
     padding: 4px;   
}

ul.topiclist > li.header > dl.icon > dd.posts,
ul.topiclist > li.header > dl.icon > dd.views,
ul.topiclist > li.header > dl.icon > dd.lastpost,
ul.topiclist.topics > li.row.bg1 > dl.icon > dd.posts,
ul.topiclist.topics > li.row.bg1 > dl.icon > dd.views,
ul.topiclist.topics > li.row.bg1 > dl.icon > dd.lastpost,
ul.topiclist.topics > li.row.bg2 > dl.icon > dd.posts,
ul.topiclist.topics > li.row.bg2 > dl.icon > dd.views,
ul.topiclist.topics > li.row.bg2 > dl.icon > dd.lastpost {
    border: none !important;
    text-align: left !important;
}
ul.topiclist > li.header > dl.icon > dd.posts {
    margin: 0 !important;
    padding: 0 !important;
}
ul.topiclist > li.header > dl.icon > dd.topics {
    margin: 0 !important;
    padding: 0 !important;
}
ul.topiclist > li.header > dl.icon > dd.views,
ul.topiclist > li.header > dl.icon > dd.lastpost {
    margin: 0 !important;
    padding: 0 !important;
}
.forumbg {
    border: none !important;
    background-image: none !important;
    border-image: none !important;
}
#postingbox,
#postingbox2 {
    padding: 8px !important;
    background-color: #2A2A2F !important;
}
/*
ul.topiclist.topics > li.row.bg1 > dl.icon,
ul.topiclist.topics > li.row.bg2 > dl.icon {
    background-image: url('http://standingtrials.com/gallery/image.php?album_id=291&image_id=11988&view=no_count') !important;
}*/

.barr > a {
    display: block !important;
}
.content > blockquote {
    color: black;
}

#post-body {
    background-color: #1A1A1F !important;
}

.panel.bg1,
.panel.bg2,
#cp-main .pm
{
    background-color: #2A2A2F !important;
    padding: 8px !important;
}

#toggle_medal > .panel.bg2 > .inner {
	height: 100% !important;
}

a.postlink:visited 
{
	color: #999 !important;    
}

a.postlink:hover 
{
    background-color: transparent !important;
}

::-webkit-scrollbar 
{
    width: 0px;
}
}

@-moz-document url-prefix("http://standingtrials.com/wiki") {
#maincontent, #sidebar, .row, .container, .mediawiki {
    background-color: rgba(26,26,31, 0.6) !important;
}

body {
    background-image: url('http://standingtrials.com/gallery/image.php?album_id=291&image_id=11991&view=no_count') !important;
}

p, a, li, ul, h1, h2, h3, h4, h5 ,h6, div {
 color:white;   
}

#maincontent {
    border-right: 1px white solid !important;
    border-left: 1px white solid !important;
}

.container {
  border-right: 1px black solid !important;
  border-left: 1px black solid !important;   
}
}
