css:
@-moz-document domain("gameme.panda-community.com") {
body{
    background-color: rgb(18, 18, 18);
}

div.header{
    border: 0px solid #495425;
    background-image: url(https://s1.gameme.net);
    background-color: #202020;
}

div.nav_bar{
    color: #dadada;
    background-color: #545454;
}

div.nav_bar a{
    color: #dadada;
}

a:visited{
	color: #afafaf;   
}

tr, th, td{
    border: 0px solid #000000;
}

th{
    color: #a9a9a9;
    background-color: rgb(51, 51, 51);
}

tr.ranking_line td{
    background-color: rgb(33, 33, 33);
    color: #cacaca;
}

a.rnk:visited{
	color: #afafaf;   
}

a.rnk:link{
	color: #afafaf;   
}

tr.ranking_line a{
	color: #afafaf;   
}

.srv_empty{
    color: #585858;
}

p.summary{
	color: #afafaf;   
}

div h3{
	color: #afafaf;   
}

tr.server_line td{
    background-color: #212121;
    color: #afafaf;
}

a.srv:visited{
	color: #afafaf;   
}

a.srv:link{
	color: #afafaf;   
}

tr.server_line a{
	color: #afafaf;   
}

div.container table{
    border-left: 0px solid #000000;
    border-right: 0px solid #000000;
    border-bottom: 0px solid #000000;
}

td{
    background-color: rgb(51, 51, 51);
    color: #afafaf; 
}

ul.sections li a:visited{
	color: #afafaf;   
}

ul.sections li a:link{
	color: #afafaf;   
}

a:link{
	color: #afafaf;   
}

a.ext:visited{
	color: #afafaf;   
}

a.ext:link{
	color: #afafaf;   
}

.footer p{
	color: #afafaf;   
}


input{
    background-color: rgb(33, 33, 33);
    color: #afafaf; 
    border-width: 0px;
    font-size: 12px;    
} 

.t_sc{
    background-color: rgb(51, 51, 51);
    color: #afafaf;   
} 

.wpn_bg{
    background-color: rgb(51, 51, 51);  
} 

.weapon{
    background-color: rgb(51, 51, 51);  
} 

textarea{
    background-color: #464646 !important;
    color: #c1c1c1 !important;
    border-width: 0px;
}

th a:visited{
	color: #afafaf;   
}

th a:link{
	color: #afafaf;   
}

i{
	color: #afafaf;   
}

p.right{
	color: #afafaf;   
}

tr.team_a_fc td{
    color: #00aaff;
    background-color: #074f7f;
}

tr.team_a_summary_fc td{
    border-top: 0px solid #0080C0;
    color: #00aaff;
    background-color: #002d4c;
}

tr.team_b_fc td {
    color: #ff0000;
    background-color: #4c0000;
}

tr.team_b_summary_fc td {
    border-top: 0px solid #FF2D2D;
    color: #ff0000;
    background-color: #2f0000;
}

table.calendar td.selected{
    background-color: #636363;
}
}
