css:
@-moz-document domain("bans.panda-community.com") {
.front-module-intro{
    display: none;
}
div[style="background-color: #bab5b2;"] { background-color: rgb(18, 18, 18) !important; }

body {
    background-color: rgb(18, 18, 18) !important;
}

#header{
    height: 125px;
}

#search{
    top: -156px;
}

#head-logo{
    margin-top: 30px;
}

#tabsWrapper {
    background-color: rgb(37, 37, 37);
}

td.listtable_top{
    background-color: rgb(37, 37, 37);
}

#front-servers{
    margin-bottom: 10px;
}

#footer{
    background-color: rgb(37, 37, 37);
}

#tabs ul li.active a{
    background-color: #1b1b1b;
}

#tabs ul li a:hover{
    background-color: #4a4a4a;
}

h3{
    background-color: #272727;
}

td.listtable_top{
    background-color: rgb(51, 51, 51);
    border-right: 0px solid #CFCAC6 !important;
    border-left: 0px solid #CFCAC6 !important;
}

td.listtable_1{
    background-color: #212121;
    color: #9e9e9e;
    border-right: 0px solid #CFCAC6 !important;
    border-left: 0px solid #CFCAC6 !important;
}

td.listtable_1_banned{
    color: #000000;
}

td.listtable_2{
    border-width: 0px 0px 0px 0px;
    background-color: #2b2b2b;
}

.ban-edit li a{
    border-bottom: 0px solid #DDD;
}

.textbox{
    background: #3c3c3c;
    border: 0px solid #ccc;
    color: #9e9e9e;
}

.select{
    background: #3c3c3c;
    border: 0px solid #ccc;
    color: #9e9e9e;
}

.ok{
    background-color: #5885A2;
    border-color: #5885A2;
}

hr{
    border: 0px solid #ddd;
}

#nav a{
    color: #a5a5a5;
}

#content_title{
    color: #a5a5a5;
}

a:link{
    color: #a5a5a5;
}

a:visited{
    color: #a5a5a5;
}

#breadcrumb{
    color: #a5a5a5;
}

#banlist-nav{
    color: #a5a5a5;
}

#cpanel{
    background-color: #212121;
}

#admin-page-content{
    background-color: #2b2b2b;
}

.rowdesc{
    color: #e8e8e8;
}

#admin-page-menu ul li a{
    background-color: rgb(37, 37, 37);
}

#admin-page-menu ul li.active a:hover{
    background-color: #4a4a4a;
}

#admin-page-menu ul li a:hover{
    background-color: #4a4a4a;
}
p{
    color: #c3c3c3;
}

div.contextmenu{
	background-color: #383838;
    border: 0px solid #dddddd;
    border-left: 0px solid #dddddd;
}

.contextmenu .head{
	background-color: #6d6d6d;
}

.contextmenu .separator{
    border-bottom: 1px solid #616161;
    background: #0c0c0c00;
}

.contextmenu a:hover{
	background-color: #6d6d6d;
}

.contextmenu a{
    margin: 0 0px;
}
 
div.dialog-control{
    background: #292929  none repeat scroll 0%;
}

div.dialog-body{
    border-bottom: 0px solid #CCCCCC;
    color: #a5a5a5;
}

.dialog-content{
    border-width: 0px 0px 0px 0px;
    background: #292929  none repeat scroll 0%;
}
}
