css:
@-moz-document domain("seasonvar.ru") {
/* html body */

html body {
    top: 20px !important;
    background-color: #5D4843;
}
/* body */

body {
    padding-top: 1px !important;
}
/*Скрыть*/

.header_icon,
.header_txt,
.pgs-back,
.pgs-stags-wrap,
.one.tabs-title,
.tabs-close:nth-of-type(6) > .tabs-title,
.tabs-close:nth-of-type(7) > .tabs-close-btn,
.tabs-close:nth-of-type(7) > .one.tabs-title,
.pgs-afterplay.tabs-result,
.pgs-review.tabs-result,
.footer,
.act.tabs-close-btn,
.pgs-head,
.news-head:before,
.infoBox.svnotify {
    display: none !important;
    visibility: hidden !important;
}
/*Конец Скрыть*/
/* noindex */

noindex,
.lside-player div div {
    display: none !important;
}
noindex,
.td-for-left-block,
#___plusone_1td,
#player_msoc,
div [align="center"] {
    display: none !important;
}
.middle {
    background-color: #edeef0;
    box-shadow: 0 0 0 0;
}
/* конец noindex */
}

@-moz-document url-prefix("http://seasonvar.ru/serial") {
/*Скрыть*/

.tabs-title,
.header_icon a,
.header_icon span,
.pgs-back,
.tabs-result-comments,
.pgs-afterplay,
.pgs-review,
.tabs-close-btn,
.btn-r,
.rside-t_en,
.pgs-msg {
    display: none !important;
    visibility: hidden !important;
}
/*Конец Скрыть*/

.middle.left {
    background-color: #392E29 !important;
}
.pgs-nearcarusel {
    padding-top: 20px !important;
}
.pgs-nearcarusel-wrap {
    height: 220px;
}
.header_line {
    background-color: #392E29;
}
.pgs-mark_line-switch .html {
    background-image: linear-gradient(#ff0000, #9e0505);
    box-shadow: 2px 3px 5px 4px black;
    padding-right: 10px;
    padding-left: 5px;
    border-radius: 3px;
    margin-right: 5px;
    height: 55px;
}
.pgs-mark_line-switch .flash {
    background-image: linear-gradient(#9e85e5, #1e016e);
    box-shadow: 2px 3px 5px 4px black;
    padding-right: 10px;
    padding-left: 5px;
    border-radius: 3px;
    margin-right: 5px;
    height: 55px;
}
.pgs-mark_line-cur {
    background-image: linear-gradient(#9e85e5, #1e016e);
}
.pgs-mark_line-set span {
    text-transform: none;
    text-decoration: underline;
    background-color: #a80076;
    height: 55px;
}
/* border */

.pgs-mark_line-set span,
.pgs-mark_line-switch.act .html,
.pgs-mark_line-switch .flash,
.rside-title li.act,
.rside-title li:hover,
.tabs-title li.act,
.tabs-title li:hover {
    background-image: linear-gradient(#9e85e5, #1e016e);
    border-right: 1;
}
.head-btn .btn-abs.today {
    background-color: #f6bc23;
}
/* конец border */

.pgs-sinfo,
.tabs-result,
.tabs-title {
    background-color: #5d4843 !important;
    ;
    color: white !important;
    ;
}
.pgs-sinfo-actor a:hover,
.pgs-seaslist span {
    color: #02ffe8 !important;
}
.pgs-sinfo-actor a,
.pgs-seaslist a {
    color: #fff700 !important;
}
.content-wrap {
    background-color: #5d4843;
}
.middle.left {
    border-left-width: 20px !important;
    padding-left: 20px !important;
}
span.svico-help.btn.btn-y.tooltipstered,
.btn-abs,
.btn-y {
    display: none !important;
}
.pgs-sinfo-title {
    font-size: 3em;
    background: -webkit-linear-gradient(top, #4C2924 0%, #000 100%);
}
.pgs-sinfo-info>p {
    font-size: 2em;
    color: #fff700 !important;
}
.pgs-sinfo-actor a,
.pgs-seaslist a {
    font-size: 2em;
}
.pgs-mark_line {
    position: relative;
    z-index: 1;
    opacity: 0.7;
    top: 55px !important;
}
.pgs-mark_line-set span {
    border-radius: 0px 0px 0 0 !important;
}
.pgs-sinfo-actor {
    font-size: 5pt;
}
.pgs-sinfo-actor [data-tabt=actorSpoiler] {
    color: #3f0;
}
.pgs-sinfo-actor-title {
    color: #02e8ff !important;
}
.pgs-sinfo_list {
    font: 16pt Tahoma;
    color: #07ddff;
}
.pgs-sinfo_list.rating span {
    color: #62ff00;
}
.b-taglist-form-lnk {
    color: #ff7600;
}
.pgs-sinfo-actor-title:after,
.pgs-sinfo-actor-title:before {
    border-top: 2px solid #02e8ff;
}
}

@-moz-document url("http://seasonvar.ru/?mod=pause"), url-prefix("http://seasonvar.ru/actor/"), url-prefix("http://seasonvar.ru/search") {
.tabs-title li {
    text-shadow: 0 1px 0 #000;
    color: #fbff5d;
    background-image: linear-gradient(#A7A1A2, #383F4A);
}
.pgs-marks-target {
    text-align: left;
}
.pgs-marks-target label input[type=checkbox]+span {
    position: relative;
    background-image: linear-gradient(#5d4843, #251e1d);
    box-shadow: 2px 3px 2px 2px #332b28;
    padding-right: 10px;
    padding-left: 5px;
    border-radius: 3px;
    color: #fdbd00;
}
div.pgs-marks-target label span {
    font-size: 24px;
}
.pgs-marks-bn .pgs-marks-el a {
    background-color: #56322a;
}
.rside-title li {
    background-image: -webkit-linear-gradient(bottom, #eee, #cfcfcf);
    background-image: linear-gradient(0deg, #277111 0, #3cf741);
    text-shadow: 0 0px 0 #000;
    box-shadow: 0 0 1px 2px #ca757b;
    color: #000;
}
.pgs-marks-name {
    font-size: 36px;
}
.pgs-marks-current {
    font-size: 26px;
}
.profile-menu-head,
.profile-menu a,
.profile-menu li {
    display: none !important;
    visibility: hidden !important;
}
.middle {
    background-color: #3b2f2c !important;
}
.doptxt {
    margin-left: -220px !important;
    box-shadow: 0 0 2px 2px #980c3d;
    background-color: #796059;
}
.rside-result.is-bottomhide,
.rside-title.is-bottomhide {
    background-color: #5d4843;
    box-shadow: 0 0 1px 1px black;
}
.pgs-marks-el {
    width: 100%;
}
.pgs-marks-last {
    font-size: 0px;
    color: #fff;
}
.pgs-marks-current {
    font-size: 32px;
    color: #00ff21;
}
.pgs-marks-name {
    color: #ffa803;
    padding-top: .1em;
    padding-left: 100px;
    font-size: 48px;
}
.pgs-marks-el a {
    box-shadow: 0 0 1px 2px rgb(8, 8, 8);
}
.pgs-marks-target label input[type=checkbox]:checked+span:after {
    color: #00ff39;
}
.rside-result.is-bottomhide,
.rside-title.is-bottomhide {
    box-shadow: 0 0 2px 2px #910c3a;
}
.pgs-search {
    background-color: #5d4843;
    color: white;
}
.href,
a {
    color: #00f3f5;
}
.lside-serial a {
    color: #fff900;
}
.content-wrap {
    background-color: #3b2f2c;
}
}

@-moz-document url("http://seasonvar.ru/") {
.news {
    background-color: #9a9393;
    color: black;
}
.news-w {
    background-color: #989494;
}
.news_s {
    color: #9ff4fd;
}
.news a:nth-child(odd) {
    background-color: #827c7c;
}
.news-w {
    background-color: #989494;
    box-shadow: inset 0 0 6px 1px black;
}
.news_n {
    padding-left: .8em;
}
.rside-title li {
    background-image: -webkit-linear-gradient(bottom, #eee, #cfcfcf);
    background-image: linear-gradient(0deg, #277111 0, #3cf741);
    text-shadow: 0 0px 0 #000;
    color: #000;
}
.middle {
    background-color: #b7b0b0;
}
.rside-result.is-bottomhide,
.rside-title.is-bottomhide {
    background-color: #9b9aa0;
}
}
