@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('retkipaikka.fi')
{

  font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(https://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
  }

  @font-face {
    font-family: 'PT Serif';
    font-style: normal;
    font-weight: 400;
    src: local('PT Serif'), local('PTSerif-Regular'), url(https://themes.googleusercontent.com/static/fonts/ptserif/v4/sDRi4fY9bOiJUbgq53yZCfesZW2xOQ-xsNqO47m55DA.woff) format('woff');
  }
    
 	.logo img {
  	transform: scale(0.5)
  }
  
  .template-blog .entry-content p {
  	width: 540px;
		margin: 2em auto;
		display: block;
    line-height: 1.6em;
    font-family: Georgia, "Times New Roman", serif;
    color: #333!important;
  }
  
  .lightbox-added {
		transform: scale(1.2);
    margin: 4em 0!important;
	}
  
  .rounded-container {
  	transform: scale(0.5);
  }
  
  #footer {
  	font-size: 0.6em;
    background: #000;
  }
  
  #top .fullsize .template-blog .blog-meta {
  	top: -20px;
    float: right;
  }
  
  .template-blog article h1, .template-blog article h2, .template-blog article h3 {
  	max-width: 840px!important;
		margin: 2em auto!important;
		text-align: center!important;
		font-family: Georgia, "Times New Roman", serif!important;
		text-transform: none!important;
		color: #333!important;
  }
  
  .blog-tags, .comment-entry, .related_posts {
  	max-width: 940px;
		margin: 2em auto;
    float: none;
  }
  
  .related_posts:after {
  	content: '';
    display: table;
    clear: both;
  }
  
  .blog-tags a {
  	font-size: 0.6em;
		background: #f0f0f0;
		border-radius: 5px;
		padding: 5px 10px;
		font-weight: bold;
		color: #000;
		letter-spacing: 1px;
  }
  
  .blog-tags strong {
  	display: none;
  }
  
  .sharedaddy {
  	text-align: center;
  }
  
  #header {
  	box-shadow: 0 2px 2px -2px rgba(0,0,0,.15);
  }
  
  .main_menu a span.avia-menu-text {
  	font-size: 0.9em!important;
		letter-spacing: .03em !important;
		text-transform: uppercase;
		font-weight: normal;
		color: #000;
  }

}
