css:
@-moz-document domain("shinden.pl") {
nav.nav-buttons a:hover,
nav.nav-buttons a.active {
    border: none !important;
    color: #D4D4D4;
    text-decoration: none;
    box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.34);
    background-color: #303030;
}
nav.nav-buttons a {
    border-radius: 500px !important;
    background-color: #262626;
}
.limit-height {
    max-height: 500px;
}
.media-item .media-title-id > a {
    color: #BDBDBD;
}
.nav-buttons.h2 {
    margin: 50px !important;
}
.logo {
    height: 150px;
    background-position: 0px -10px
}
.logo-title {
    position: absolute;
}
.logo .logo-title img {
    right: -75em;
    margin: 0;
}
.logo-char {
    height: 100%;
    left: 35%;
    top: 25px;
}
body > script:nth-child(3) {
    display: none;
}
.info-aside-img {
    margin: 20px auto 13px auto;
}
body .form-search fieldset select {
    left: -5.5em;
    width: 6em;
}
h1.h2.box-title {
    margin-top: 1em;
}
section.push0-col4-special {
    margin-top: 4em;
}
/* elements remover */

.comments.fb {
    display: none;
}
.pasek {
    display: none;
}
section.push0-col4-special div {
    display: none;
}
section.push0-col4-special div.slider tab-inner {
    display: none;
}
.slider.tab-inner {
    display: block;
}
div#przycisk {
    display: none;
}
.ads {
    display: none;
}
script#ad_tmpl2 {
    display: none;
}
script#ad_tmpl {
    display: none;
}
div#player-block iframe {
    display: block;
}
div#ad-player {
    display: none;
}
.l-footer {
    padding-bottom: 0px;
}
div.more-link {
    margin-bottom: 0.5em;
    font-size: 19px;
    margin-left: 44%;
    font-weight: bold;
    margin-top: -10px;
}
article.page-content.page-episode-view {
    background-color: #d0e0fb;
    padding-right: 1em;
    padding-left: 1em;
}
aside.info-aside.aside-title {
    padding-left: 1em;
}
.message.info {
    border-radius: 0.25em;
    padding: 0.4em;
}
.top-bar {
    background: #566471;
}
.top-bar--user #menu-search button {
    background-color: #38434c;
}
.top-bar--user #menu-search {
    margin-top: 0;
}
.top-bar .top-button {
    background: #38434c;
    margin: 0px 10px 0;
}
.title-other,
.episode-head {
    padding: 1px 30px;
}
h1.page-title.small {
    font-size: x-large;
    padding-top: 0.2em;
    padding-left: 1.2em;
}
.fb_iframe_widget {
    display: none;
}
.info-aside-img {
    margin: 20px auto 1px auto;
}
.player-container {
    padding: 0;
    text-align: center;
}
menu,
ol,
ul {
    list-style-type: none;
    padding: 0 0 0 0px;
}
.posts-last .add-post .buttons {
    margin-top: 0;
    text-align: center;
    padding: 7px;
    background-color: inherit;
}
.top-bar--nav-user {
    background: #38434c;
}
.info-aside-list li {
    background: inherit;
    padding-left: 0;
}
a {
    color: #000000;
    text-decoration: none;
}
div.rateit {
    margin-right: 0.4em;
}
i.fa.fa-home {
    font-size: 19px;
}
.l-container-primary,
.l-main-contantainer {
    padding-bottom: inherit;
    background-color: #d0e0fb;
}
.newTitle span {
    background: #d0e0fb;
}
.newTitle.newTitle-blue span {
    background: #d0e0fb;
}
.l-container-secondary {
    background-color: #d0e0fb;
}
.carousel-text .carousel-pagination {
    top: 296px;
}
.carousel-text {
    height: 333px;
    border-bottom: none;
}
.search-section {
    background: #d0e0fb;
}
.media-item .media-title-id > a {
    color: #000000;
}
.box-bottom {
    background-color: #d0e0fb;
    background-image: none;
}
img[src="/f//skins/1/img/shinden-title.png"] {
    background-image: url(https://cdn.discordapp.com/attachments/255420313372852235/415909819552104448/shinden-titlev2.png);
    background-position: -13px 0;
}
}
