css:
@-moz-document domain("web.any.do") {
#preloader {
    background-color: #1f2326 !important;
}

.theme_bg_8 {
    background-color: #1f2326 !important;
}

.theme_set_input .columns .columns-container section {
    color: #1f2326;
}

.theme_bg_7 {
    background-color: #1f2326 !important;
}

.theme_color_2 {
    color: #DDD !important;
}

.theme_border_6 {
    border-color: #666 !important;
}

.theme_after_border_6:after {
    border-color: #1f2326 !important;
}

.theme_set_input .lightbox {
    background-color: #1f2326;
}

.theme_set_input #sidebar li a {
    color: #a8a8a8;
}

.theme_bg_6 {
    background-color: #1c1f22 !important;
}

#task #task-details #title-box #title {
    color: #fff;
}

.theme_set_input input, .theme_set_input textarea {
    color: #a8a8a8;
}

.theme_set_input #sidebar li {
    border-color: #1f2326;
}

#sidebar .navbar-settings li .theme {
    border-width: 0px;
}

.columns .columns-container section header .add-inline {
    border-style: dashed;
}

.theme_menu {
    background-color: #1f2326 !important;
    border-color: #666 !important;
}

.theme_menu:hover {
    background-color: #101010 !important;
    color: #a8a8a8 !important;
}

.app #notification-bar {
    width: 377px;
    right: 1px;
}

/* calendar */

.theme_calender .dw-cal-header, .theme_calender .dwwc, .theme_calender .dwc, .theme_calender .dw-cal-table, .theme_calender .dw-cal-day {
    background-color: #101010 !important;
}

.theme_calender .dwbg .dwwbp, .theme_calender .dwwbm {
    background-color: #101010 !important;
}

.theme_calender .ios7 .dw-cal-day-fg {
    color: #a8a8a8 !important;
}

.theme_calender .dw-i {
    color: #a8a8a8 !important;
}

.theme_calender .dw-cal-btnw-m, .theme_calender .dw-cal-month, .theme_calender .dw-cal-year {
    color: #a8a8a8 !important;
}

.dialog .ctrlBar>div {
    background: #101010 !important;
}

.dialog .ctrlBar>div:hover {
    background: rgba(102,102,102,0.4)!important;
}

.theme_calender .ios7 .dwwo {
    background: rgba(22,22,22,0)!important;
}

.theme_calender .ios7 .dw-cal-today {
    background-color: #000 !important;
    border: 2px solid;
}

.theme_calender .ios7 .dw-sel .dw-cal-day-fg {
    background-color: #000 !important;
    color: #09c !important;
    border-color: #09c !important;
}

#dateAndTimeDialog {
    background-color: #101010 !important;
}

a#gopremium {
    display: none!important;
}
}
