css:
@-moz-document domain("qiita.com"){
.btn-default {
    background: linear-gradient(#555,#222);
    border-color: #666;
    color: #ccc;
}

.code-frame {
    background: linear-gradient(#222,#111);
    border-bottom: 1px solid #334;
    box-shadow: 0 1px 5px #222;
}

.code-frame pre {
    font-size: 1.4em;
    line-height: 2;
    text-shadow: 0 0 5px #234, 0 0 15px #123, 0 0 25px #012;
}

.code-lang {
    border-radius: 3px;
    box-shadow: 0 0 3px #000;
    color: #55c500;
    text-shadow: 0 0 5px #333;
}

.commentForm form, .commentForm_contentHeader, .commentForm_tab, .commentForm_tab.active, .commentForm_body, .commentForm_imageUploader {
    background: #333;
    border: none;
    color: #ccc;
}

.commentForm_body textarea {
    background: #222;
    border: none;
    box-shadow: 0 0 10px #000 inset;
    color: #ccc;
}

.footer,.st-Footer {
    background-color: #333;
}

.footer_container {
    border-top: 2px dotted #666;
}

.footer_container a {
    color: #060;
}

.form-control {
    background-color: #333;
    border-color: #555;
    box-shadow: 0 0 5px #000 inset;
    color: #999;
}

.highlight {
    border-top: 1px solid #566;
    padding: 30px;
    margin-bottom: -14px!important;
    box-shadow: 0 -8px 8px #233 inset;
}

.highlight .p {
    color: #567;
}

.highlight .k {
    color: #aa6;
}

.highlight .kd {
    color: #678;
}

.highlight .c1 {
    color: #234;
    text-shadow: none;
}

.highlight .nx {
    color: #6f6;
}

.highlight .o {
    color: #7a7;
}

.highlight .mi {
    color: #6cc;
}

.highlight .s1 {
    color: #c66;
}

.it-Actions_item-like button {
    background-color: #333;
}

.it-AdcalRibbon {
    background: linear-gradient(90deg,#ef5350,#900);
    border-color: #900;
}

.it-AdcalRibbon a.it-AdcalRibbon_title {
    color: #222;
}

.it-AdcalRibbon:before, .it-AdcalRibbon:after {
    background: #900;
    border-color: #900;
}

.it-AuthorInfo {
    border-bottom: 2px dotted #666;
}

.it-MdContent table tr:nth-child(even) td {
    background-color: #222;
    border: 1px solid #444;
    color: #ccc;
}

.it-MdContent table tr:nth-child(odd) td {
    background-color: #282828;
    border: 1px solid #444;
    color: #ccc;
}

.it-MdContent table, .it-MdContent table th {
    background-color: #222;
    border-color: #444;
    color: #555;
}

.it-Tags_item {
    background-color: #333;
    font-size: 0.6em;
    font-weight: bold;
}

.it-Tags_item span {
    color: #ac0;
}

.p-home .ra-UserList, .p-home .ra-TagList {
    background-color: #333;
    color: #ccc;
}

.p-home_main {
    background-color: #333;
    color: #ccc;
}

.p-items {
    background-color: #333;
}

.p-items_article a {
    color: #ac0;
}

.p-items_article code {
    background: #000;
    color: #85e530;
}

.p-items_article h1 {
    border-bottom: 2px dotted #060;
    border-left: 5px solid #060;
    color: #35a500;
    font-size: 1.8em;
    font-weight: normal;
    padding-left: 15px;
    text-shadow: 0 0 5px #000,0 0 10px #000,0 0 15px #000;
}

.p-items_article h1.it-Header_title {
    border: none;
    color: #85e530;
    display: block;
    font-size: 3em;
    font-weight: bold;
    text-shadow: 0 0 5px #000,0 0 10px #000,0 0 15px #000;
}

.p-items_article h2 {
    border-left: 5px solid #060;
    font-size: 1.6em;
    font-weight: bold;
    padding-bottom: 8px
;
    padding-left: 15px;
}

.p-items_article h2, .p-items_article h3, .p-items_article h4 {
    border-bottom: 2px dotted #060;
    color: #35a500;
    font-weight: normal;
    text-shadow: 0 0 5px #000,0 0 10px #000,0 0 15px #000;
}

.p-items_article h3 {
    font-size: 1.3em;
    padding-bottom: 4px
;
}

.p-items_article h4 {
    font-size: 1.1em;
}

.p-items_article h5 {
    color: #ccc;
    font-size: 1.1em;
    font-weight: normal;
}

.p-items_article li {
    color: #fff;
    font-size: 1.1em;
    line-height: 3;
    list-style-type: square;
    text-shadow: 0 0 5px #000,0 0 10px #000,0 0 15px #000;
}

.p-items_article p {
    color: #ccc;
}

.p-items_article ul {
    margin: 50px 0 50px 0;
}

.p-items_article,.p-home {
    background: #222;
    box-shadow: 0 0 6px #000 inset;
}

.p-items_wrapper {
    background: linear-gradient(#333,#444);
    box-shadow: 0 2px 5px #000 inset;
}

.p-items_wrapper .p-items_article {
    background-color: #222;
    box-shadow: 0 1px 5px #000 inset;
}

.partiallyMatchedTag hr, .radiusWidget {
    border-color: #666;
}

.ra-UserList_content:not(:last-child), .ra-TagList_content:not(:last-child) {
    border-bottom: 2px dotted #666;
}

.radiusWidget_title {
    background-color: #333;
    border-color: #666;
    color: #ccc;
    font-size: 0.6em;
    font-weight: bold;
}

.s-mgb-05em, .partiallyMatchedTag_stats, .searchResultContainer_queryDescription {
    color: #ccc;
}

.searchResult,.nav-tabs,.searchResultContainer_sortButtonContainer {
    border-bottom-color: #666;
}

.searchResultContainer_main a em {
    color: #85e530;
}

.searchResultContainer_main a, .searchResultContainer_main a:hover, .searchResultContainer_main a:focus {
    color: #55c500;
}

.searchResultContainer_navigation .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    background-color: #222;
    border-left-color: #666;
    border-right-color: #666;
    border-top-color: #666;
    color: #aaa;
}

.searchResultContainer_queryExample, .searchResultContainer_queryExample:focus, .searchResultContainer_queryExample:hover {
    background-color: #555;
    border-color: #666;
    font-weight: bold;
}

.st-Header {
    background: linear-gradient(#55c500,#35a500);
    box-shadow: 0 1px 1px #a5f560 inset;
}

.st-Header svg {
    filter: drop-shadow(0 0 5px #040);
}

.st-Header_search input {
    background-color: #080;
    box-shadow: 0 0 5px #050 inset;
    color: #85e530;
}

.tocTree li.active a {
    background-color: #2a2a2a;
    border-radius: 3px;
    color: #999;
}

.tr-Item,.tr-ItemList_header {
    border-bottom: 2px dotted #000;
}

.with-js {
    background-color: #222;
}

span[id^=MathJax] {
    color: #6c3;
    text-shadow: 0 0 5px #060;
}




}
