css:
@-moz-document domain("workflowy.com") {
 .edge,
 .corner {
     display: none;
 }

	body, page-container {
		font-family: 'Consolas' !important;
		font-size: 17px !important;
		font-weight: normal !important;
	}

	#pageContainer {
        margin-top: 0px;
    }

	#backgroundImage {
		background: #4D4D4D !important;
	}

	.getMoreSpaceButton, .siteSlogan{
		display: none;
	}

	.proPitch, .proPitch:hover {
		display: none !important;
	}


	BODY {
		color: #FFFFFF !important;
		background: #4D4D4D !important;
	}

	div.content {
		color: #FFFFFF !important;
	}

	.highlight {
		height: 100%;
		left: -5px;
		padding: 2px 5px 1px;
		position: absolute;
		top: 2px;
		width: 100%;
	}

	.moving > .highlight, .highlighted > .highlight {
		background: #0F0F0F !important;
	}

	.moving > .highlight, .highlighted.moveHovered > .highlight {
		background: #0F0F0F !important;
	}

        .project.highlighted::before, .project.moving::before, .project.tapped::before, .draggableNameClone::before
        {
                background:  #484848 !important;
        }


	.notes > .content {
		color: #666666 !important;
	}

    .page {
        background: #262626 !important;
        max-width: 100% !important;
        border: none !important;
        margin-bottom: 0 !important;
        /* fix for dynamic positioning of the .page div */
        transform: none !important;
        width: auto !important;
    }


	a.bullet {
		color: #569CD6 !important;
	}



	span.plus {
		color: #cfcfcf !important;
	}

	span.text {
		color: #cfcfcf !important;
	}


	div.top.edge {
		display: none;
	}

	div.bottom.edge {
		display: none;
	}

	div.left.edge {
		display: none;
	}

	div.right.edge {
		display: none;
	}

	DIV.top.right.corner {
		display: none;
	}

	DIV.top.left.corner {
		display: none;
	}

	DIV.bottom.right.corner {
		display: none;
	}

	DIV.bottom.left.corner {
		display: none;
	}

	.contentTag {
		font-weight: bold !important;
		padding: 2px 3px 2px !important;
		color: limegreen !important; /* grey by default(#888888)*/
        background-color: rgba(34, 139, 34, 0.4);
    	border: 1px solid forestgreen;
		font-size: 14px !important;
	}
	.contentTag:hover {
        background-color: limegreen !important;
        color: black !important;
        border: 1px solid lime;
	}
	/*hide the tags when editing */
	.content.editing .contentTag {
		visibility: hidden;
	}
	/* remove the underline from tags */
	.content .contentTagText {
		text-decoration: none !important;
	}
	
	#move{
		color: #ffffff !important; 
		/*background-color: #cccccc !important;*/
		background: url(http://www.workflowy.com/media/i/move_icon_white.png) no-repeat !important;
	}
	
	.expandPlus, .expandMinus{
		color: #cfcfcf !important; 
	}
	
	.content .contentMatch {
		background-color: #484848 !important;
	}

	.project.metaMatches:not(.addedToSelection) > .name > .content > .innerContentContainer, .project.metaMatches:not(.addedToSelection) > .notes > .content > .innerContentContainer
	{
		background-color: transparent !important;
	}
}
