css:
@-moz-document domain("azure-gallery.net") {
  #Illusts {
      margin: 0px auto;
  }
  #Selector {
      color: #424242;
      background-color: #fff;
      padding: 10px;
      min-width: 250px;
      box-shadow: 0 0 20px #CFD8DC;
  }
  /*
#Selector::before {
  content: "";
}
*/
  
  fieldset {
      border: 1px solid #4DD0E1;
      border-radius: 4px;
      width: 200px;
      margin: 0 auto;
      display: block;
  }
  legend.illust.rate,
  label.illust.rate {
      color: #424242;
  }
  div.ship-selector_layout {
      width: 200px;
      margin: 20px auto 0;
      border: 1px solid #4DD0E1;
      border-radius: 4px;
      padding: 10px;
  }
  #Illusts {
      padding: 10px 50px;
  }
  body {
      background-color: #ECEFF1;
      padding: 0;
      margin: 0;
  }
  div.illust.change-page {
      color: #607D8B;
      background-color: #fff;
      border: 1px solid #CFD8DC;
      border-radius: 4px;
      font-size: 12px;
      width: 25px;
      height: 25px;
      text-align: center;
      line-height: 25px;
      cursor: pointer;
  }
  div.illust.change-page:hover {
      background-color: #E0F7FA;
      border: solid 1px #4DD0E1;
  }
  div.illust.change-page.current {
      color: #fff;
      background-color: #4DD0E1;
      border: solid 1px #00BCD4;
  }
  div.illust-container {
      background-color: #fff;
      border: 1px solid #CFD8DC;
      border-radius: 4px;
      position: relative;
  }
  img.twitter.icon {
      border: 1px solid #CFD8DC;
      border-radius: 16px;
      float: left;
  }
  div.userInfo {
      position: relative;
      cursor: pointer;
  }
  div.twitter.name {
      color: #263238;
      display: block;
      font-size: 12px;
      font-weight: normal;
  }
  div.twitter.screenname {
      color: #00BCD4;
      display: block;
      position: absolute;
      bottom: -14px;
      left: 60px;
  }
  div.created_at {
      clear: both;
      color: #9E9E9E;
  }
  div.illust-box {
      border: 1px solid #CFD8DC;
      border-radius: 4px;
      background: #37474F;
  }
  img.illust {
      margin: 0;
  }
  div.text {
      margin-top: 10px;
      font-size: 12px;
      color: #424242;
  }
  div.illust-option {
      position: absolute;
      bottom: 8px;
  }
  div.illust.tag,
  div.ship-selector {
      font-size: 10px;
      font-weight: bold;
      background-color: none;
      border: 1px solid #CFD8DC;
      border-radius: 4px;
      padding: 3px 6px;
      cursor: pointer;
  }
  div.illust.tag.category,
  div.ship-selector.country {
      color: #FFA000;
      border: 1px solid #FFA000;
      background-color: #fff;
  }
  div.illust.tag.ship,
  div.ship-selector.shipType {
      color: #3F51B5;
      border: 1px solid #3F51B5;
      background-color: #fff;
  }
  div.ship-selector {
      padding: 6px 12px;
      font-size: 12px
  }
  div.illust.time,
  div.illust.page,
  div.illust.count {
      color: #424242;
      font-size: 14px;
  }
  div.illust.time::before {
      content: "読み込み速度：";
  }
  div.illust.page::before {
      content: "閲覧中ページ：";
  }
  div.illust.count::before {
      content: "合計：";
  }
  @media screen and ( max-width:950px) {
      #MainContainer {
          display: block;
      }
      #Selector {
          width: 100%;
          height: 100%;
          overflow: hidden;
          margin-bottom: 10px;
      }
      div.ship-selector_layout {
          width: 80%;
      }
      fieldset.illust.rate {
          width: 80%;
          height: 60px;
      }
      #Illusts {
          text-align: center;
      }
  }
}
