css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url-prefix("https://www.google."),
  url-prefix("https://encrypted.google.")
{
  /* Chrome promo */
  div[aria-label="promo"] {
    display: none !important;
  }
  
  /* Tool menu: always displayed vertical */
  #hdtbMenus {
    top: 68px !important;
    width: 148px !important;
    display: block !important;
  }
  #hdtb-mn-gp, .cdr_sep, .mn-dwn-arw, #hdtb-tls {
    display: none !important;
  }
  .hdtb-mn-hd {
    display: block !important;
    font-weight: bold;
  }
  .hdtb-mn-hd:hover {
    color: #777 !important;
  }
  .hdtbU {
    display: block !important;
    position: static !important;
    min-width: 0 !important;
    border: none !important;
    box-shadow: none !important;
    white-space: normal !important;
  }
  #hdtb .hdtbItm a:not([id^=sl]) {
    padding: 4px 2px 4px 30px !important;
  }
  .hdtbSel {
    background-position: 5px center;
  }
  .tnv-lt-sm {
    height: auto !important;
    white-space: nowrap !important;
  }
  .tnv-lt-arw {
    margin-top: 6px !important;
  }
  /* Color swatches fix */
  #sc-block {
    display: flex;
    flex-wrap: wrap;
    width: 120px;
  }
  .sc {
    float: none !important;
  }
  /* Image list fix */
  #rg_s {
    margin: 0 0 0 160px;
    width: calc(100% - 160px) !important;
  }
  #irc_bg {
    z-index: 1000;
  }

  /* Snippets: highlights */
  .s .st em, .st.s.std em {
    background: #ffa;
  }

  /* Snippets: inline menu */
  .ab_ctl.action-menu,
  .ab_ctl.action-menu * {
    visibility: visible !important;
    position: static !important;
    display: inline !important;
    margin: 0 !important;
    padding: 0 !important;
    box-shadow: none !important;
    border: none !important;
    font: inherit !important;
    vertical-align: inherit !important;
  }
  .ab_ctl.action-menu {
    margin: 0 0 0 20px !important;
  }
  .ab_ctl.action-menu a {
    font-size: 11px !important;
    padding: 0 2px !important;
    color: #093 !important;
  }
}
