css:
@-moz-document domain("pplware.sapo.pt") {
.learn-more { background: none repeat scroll 0 0 #303030 !important; }
.heading-more { background: #303030 !important; }
.custom-list-check { background: #303030 !important; }
a, .themeform label .required, #flexslider-featured .flex-direction-nav .flex-next:hover, #flexslider-featured .flex-direction-nav .flex-prev:hover, .post-hover:hover .post-title a, .post-title a:hover, .s1 .post-nav li a:hover i, .content .post-nav li a:hover i, .post-related a:hover, .s1 .widget_rss ul li a, #footer .widget_rss ul li a, .s1 .widget_calendar a, #footer .widget_calendar a, .s1 .alx-tab .tab-item-category a, .s1 .alx-posts .post-item-category a, .s1 .alx-tab li:hover .tab-item-title a, .s1 .alx-tab li:hover .tab-item-comment a, .s1 .alx-posts li:hover .post-item-title a, #footer .alx-tab .tab-item-category a, #footer .alx-posts .post-item-category a, #footer .alx-tab li:hover .tab-item-title a, #footer .alx-tab li:hover .tab-item-comment a, #footer .alx-posts li:hover .post-item-title a, .comment-tabs li.active a, .comment-awaiting-moderation, .child-menu a:hover, .child-menu .current_page_item > a, .wp-pagenavi a { color: hsl(196, 100%, 47%) !important; }
.wpcmsdev-toggle { background-color: #303030 !important; }
.wpcmsdev-toggle .toggle-title a { background-color: #303030 !important; background-image: unset !important; }
.cycle-slideshow { background: #303030 !important; }
.wp-embed { background: #303030 !important; }
.wpcmsdev-box .box-title { background-color: #333333 !important; }
.box-content { background-color: #333333 !important; }
.page-title span { color: #b2b1b1 !important; }
#review-statistics .review-wrap-up .review-wu-right ul li, #review-statistics .review-wu-bars h3, .review-wu-bars span, #review-statistics .review-wrap-up .cwpr-review-top .cwp-item-category a { color: #d0d0d0 !important; }
.sidebar { display: none; }
#review-statistics .review-wrap-up div.cwpr-review-top { border-top: 0px solid #fff !important; }
.col-2cl .main-inner { background: white; padding-right: 0; }
img { display: block; margin: 0 auto; }
.home .post-inner img[class*="wp-image-"], .archive .post-inner img[class*="wp-image-"] { width: 100% !important; }
#bsu-v2-ctn { display: none !important; }
body { all: initial; /** {
    all: unset;
   }*/ }
body { text-rendering: optimizelegibility; background: #212121; font-size: 16px; /*line-height: 1.5em;*/
    color: #303030 !important; font-weight: 400; font-family: "Source Sans Pro", Arial, sans-serif; overflow-x: hidden; }
#header { background-color: unset; box-shadow: unset; }
#nav-header.nav-container { background-color: #171717 !important; }
.large-pesquisa input.search, .small-pesquisa input.search { background: #171717; color: #fff !important; }
.content { background-color: #303030 !important; color: white; border-right: unset !important; }
blockquote > p { color: #aaa; }
.post-title, time, h1, h2, h3, h4 { color: #52aee5 !important; text-align: center !important; }
.post-title > a { color: #fff; }
hr { color: #303030 !important; border-bottom: 2px solid #444; }
.page-title { background-color: #121212 !important; border-bottom: unset; }
.post-tags a { background-color: #444 !important; }
#a_pais { display: none; }
.post-nav { border-left: unset !important; border-top: unset !important; border-bottom: unset !important; border: unset !important; }
.commentlist .fn, .commentlist .comment-body p { color: unset; }
#commentform { background: unset; }
#reply-title { margin-bottom: 0px; margin-left: 20px; }
#respond { background-color: #212121 !important; padding-top: 20px; }
.themeform label { color: #aaa; }
#commentform textarea, #commentform input { background-color: #303030 !important; color: #fff; }
#commentform textarea { height: 100px; }
#disclaimer { background-color: unset !important; }
.entry.excerpt { font-size: 16px; color: #A4A4A4 !important; }
.widgettitle { margin-bottom: 0px !important; }
.footer-imgs { background-color: #303030; }
.author-box { background: #303030 !important; }
.box-download { background: #303030 !important; }
.box-content { background-color: #303030 !important; }
#page { background: #303030 !important; }
#author-info { background-color: #303030 !important; }
#wpcomm * { box-sizing: border-box; background-color: #303030 !important; }
#wpcomm .wc-comment .wc-comment-right { background: #303030 !important; }
#nav-header-fixed.nav-container { background-color: #151515 !important; }
.header-search input.search { border-radius: 15px !important; background: #303030 !important; z-index: 0 !important; }
#wpcomm .wc-comment .wc-comment-left .wpd-xborder { height: 0px !important; }
div { color: white !important; }
#wpcomm .wc-comment-label { box-shadow: 0px 10px 9px -7px rgba(255, 255, 255, 0) !important; }
.social-links li a { background: #303030 !important; }
.search-div .fa-search { color: #00b0f0 !important; }
.back-to-top { background-color: #151515 !important; }
}
