css:
@-moz-document domain("qiita.com"), domain("jser.info"), domain("pygments.org"), domain("rouge.jneen.net"), domain("github.com"), domain("developer.mozilla.org"), domain("coliss.com") {
:root {
    --gray:       hsl( 50, 10%, 70%);
    --red:        hsl(340, 90%, 70%);
    --purple:     hsl(280, 80%, 80%);
    --lightblue:  hsl(200, 40%, 70%);
    --green:      hsl(150, 90%, 35%);
    --lightgreen: hsl(100, 50%, 66%);
    --orange:     hsl( 30, 90%, 60%);
    --yellow:     hsl( 65, 99%, 45%);
}


.it-MdContent .code-frame,
.MathJax,
.syntax pre,
pre.highlight
/* dark reader解除 */
{   filter: invert(100%) hue-rotate(180deg) !important; }

.highlight pre,
.syntax pre,
pre.highlight
{
    font: 13px "Fira Code" !important;
    background-color: #333;
}

.it-MdContent code,
:not(pre)>*>code, :not(pre)>code /* for moz */
{
    font: "Fira Code" !important;
    color: hsl( 30, 99%, 15%);
    background-color: #ccc;
}

/* for ptgments */

/* comment */
.c1, .cm, .cp, .c, .ch, .sd
{
    font-style: normal !important;
    color: var(--gray) !important;
}

/* html tag */
.nt,
/* diff deleted */
.gd
{   color: var(--red)  !important; }

/* html attr */
.na
{   color: var(--purple)  !important; }


.o, .mo,/* operator */
.p,/* puncture ()[]{} */
.si/* `${}` */
{   color: var(--lightblue) !important; }

/* keyword */
.kd, .k, .kr, .kn, .ow, .bp, .kc, .kp,
/* number */
.m, .mi, .mn, .mh, .mf,
/* yaml anchor */
.nl
{   color: var(--purple) !important; }

/* ふつうの語 */
.nb, .nc/* py class */, .nf/* py function */, .no
{   color: var(--yellow) !important; }

/* variable */
.n, .nx, .nn, .py,
.vi, .vc, .vg,/*Ruby @x, @@x, $x */
/* 不明 */
.nv,
/* yaml */
.l-Scalar
{   color: var(--orange) !important; }

/* string */
.s1, .s2, .s, .sb, .sc, .ss, .sx, .no
{   color: var(--lightgreen) !important; }

/* RegExp */
.sr,
/* escape string */
.se
{   color: var(--green) !important; }

/* () */
/* .pun */
/* {   color: #000 !important; } */

/* type */
.kt
{   color: #29f !important; }

/* .lit */
/* {   color: #380 !important; } */

/* error */
.err {
    border-bottom: 4px solid #d00 !important;
}

